static bool
add_response_entry_n (struct MHD_Response *response,
                      enum MHD_ValueKind kind,
                      const char *header,
                      size_t header_len,
                      const char *content,
                      size_t content_len)
{
  /* Logging probe: record entry and parameters to help reproduce potential
     out-of-bounds access via memchr. Logs are appended to /tmp/logs/923.txt. */
  {
    FILE *logf = fopen("/tmp/logs/923.txt", "a");
    if (logf)
      {
        fprintf(logf, "ENTER add_response_entry_n: response=%p kind=%d header=%p header_len=%zu content=%p content_len=%zu\n",
                (void *) response, (int) kind, (const void *) header, header_len,
                (const void *) content, content_len);
        fflush(logf);
        fclose(logf);
      }
  }

  if (NULL == response)
    {
      FILE *logf = fopen("/tmp/logs/923.txt", "a");
      if (logf)
        {
          fprintf(logf, "EARLY RETURN: response == NULL -> return false\n");
          fflush(logf);
          fclose(logf);
        }
      return false;
    }
  if (0 == header_len)
    {
      FILE *logf = fopen("/tmp/logs/923.txt", "a");
      if (logf)
        {
          fprintf(logf, "EARLY RETURN: header_len == 0 -> return false\n");
          fflush(logf);
          fclose(logf);
        }
      return false;
    }
  if (0 == content_len)
    {
      FILE *logf = fopen("/tmp/logs/923.txt", "a");
      if (logf)
        {
          fprintf(logf, "EARLY RETURN: content_len == 0 -> return false\n");
          fflush(logf);
          fclose(logf);
        }
      return false;
    }

  /* Before performing memchr-based checks, log the pointer/null status and lengths.
     Note: we intentionally do not change the original control flow (we do not
     add extra NULL checks that would alter behavior). This allows reproduction
     of the reported violation when header == NULL and header_len > 0. */
  {
    FILE *logf = fopen("/tmp/logs/923.txt", "a");
    if (logf)
      {
        fprintf(logf, "CHECKS: header is %s, header_len=%zu; content is %s, content_len=%zu\n",
                (header == NULL) ? "NULL" : "non-NULL", header_len,
                (content == NULL) ? "NULL" : "non-NULL", content_len);
        fflush(logf);
        fclose(logf);
      }
  }

  /* Each memchr invocation is logged immediately before and after the call so
     that the exact point of potential out-of-bounds access can be observed */
  {
    const void *res;

    /* Check for '\t' in header */
    {
      FILE *logf = fopen("/tmp/logs/923.txt", "a");
      if (logf)
        {
          fprintf(logf, "About to memchr(header, '\\t', header_len) with header=%p header_len=%zu\n",
                  (const void *) header, header_len);
          fflush(logf);
          fclose(logf);
        }
    }
    res = memchr (header, '\t', header_len);
    {
      FILE *logf = fopen("/tmp/logs/923.txt", "a");
      if (logf)
        {
          fprintf(logf, "memchr(... '\\t' ) returned %p\n", res);
          fflush(logf);
          fclose(logf);
        }
    }
    if (NULL != res)
      return false;

    /* Check for ' ' in header */
    {
      FILE *logf = fopen("/tmp/logs/923.txt", "a");
      if (logf)
        {
          fprintf(logf, "About to memchr(header, ' ', header_len) with header=%p header_len=%zu\n",
                  (const void *) header, header_len);
          fflush(logf);
          fclose(logf);
        }
    }
    res = memchr (header, ' ', header_len);
    {
      FILE *logf = fopen("/tmp/logs/923.txt", "a");
      if (logf)
        {
          fprintf(logf, "memchr(... ' ' ) returned %p\n", res);
          fflush(logf);
          fclose(logf);
        }
    }
    if (NULL != res)
      return false;

    /* Check for '\r' in header */
    {
      FILE *logf = fopen("/tmp/logs/923.txt", "a");
      if (logf)
        {
          fprintf(logf, "About to memchr(header, '\\r', header_len) with header=%p header_len=%zu\n",
                  (const void *) header, header_len);
          fflush(logf);
          fclose(logf);
        }
    }
    res = memchr (header, '\r', header_len);
    {
      FILE *logf = fopen("/tmp/logs/923.txt", "a");
      if (logf)
        {
          fprintf(logf, "memchr(... '\\r' ) returned %p\n", res);
          fflush(logf);
          fclose(logf);
        }
    }
    if (NULL != res)
      return false;

    /* Check for '\n' in header */
    {
      FILE *logf = fopen("/tmp/logs/923.txt", "a");
      if (logf)
        {
          fprintf(logf, "About to memchr(header, '\\n', header_len) with header=%p header_len=%zu\n",
                  (const void *) header, header_len);
          fflush(logf);
          fclose(logf);
        }
    }
    res = memchr (header, '\n', header_len);
    {
      FILE *logf = fopen("/tmp/logs/923.txt", "a");
      if (logf)
        {
          fprintf(logf, "memchr(... '\\n' ) returned %p\n", res);
          fflush(logf);
          fclose(logf);
        }
    }
    if (NULL != res)
      return false;

    /* Check for '\r' in content */
    {
      FILE *logf = fopen("/tmp/logs/923.txt", "a");
      if (logf)
        {
          fprintf(logf, "About to memchr(content, '\\r', content_len) with content=%p content_len=%zu\n",
                  (const void *) content, content_len);
          fflush(logf);
          fclose(logf);
        }
    }
    res = memchr (content, '\r', content_len);
    {
      FILE *logf = fopen("/tmp/logs/923.txt", "a");
      if (logf)
        {
          fprintf(logf, "memchr(content, '\\r') returned %p\n", res);
          fflush(logf);
          fclose(logf);
        }
    }
    if (NULL != res)
      return false;

    /* Check for '\n' in content */
    {
      FILE *logf = fopen("/tmp/logs/923.txt", "a");
      if (logf)
        {
          fprintf(logf, "About to memchr(content, '\\n', content_len) with content=%p content_len=%zu\n",
                  (const void *) content, content_len);
          fflush(logf);
          fclose(logf);
        }
    }
    res = memchr (content, '\n', content_len);
    {
      FILE *logf = fopen("/tmp/logs/923.txt", "a");
      if (logf)
        {
          fprintf(logf, "memchr(content, '\\n') returned %p\n", res);
          fflush(logf);
          fclose(logf);
        }
    }
    if (NULL != res)
      return false;
  }

  /* All checks passed: call the original function as before. */
  return MHD_add_response_entry_no_check_ (response, kind, header, header_len,
                                           content, content_len);
}
