static bool
add_response_entry_n (struct MHD_Response *response,
                      enum MHD_ValueKind kind,
                      const char *header,
                      size_t header_len,
                      const char *content,
                      size_t content_len)
{
  /* Open log file (append) to record control flow and state for debugging. */
  FILE *log = fopen("/tmp/logs/925.txt", "a");
  if (log)
    {
      fprintf(log, "ENTER add_response_entry_n: response=%p kind=%d header=%p header_len=%zu content=%p content_len=%zu\n",
              (void *) response, (int) kind, (const void *) header, header_len, (const void *) content, content_len);
      fflush(log);
    }

  if (NULL == response)
    {
      if (log)
        {
          fprintf(log, "EXIT add_response_entry_n: response == NULL -> false\n");
          fclose(log);
        }
      return false;
    }

  if (0 == header_len)
    {
      if (log)
        {
          fprintf(log, "EXIT add_response_entry_n: header_len == 0 -> false\n");
          fclose(log);
        }
      return false;
    }

  if (0 == content_len)
    {
      if (log)
        {
          fprintf(log, "EXIT add_response_entry_n: content_len == 0 -> false\n");
          fclose(log);
        }
      return false;
    }

  /* Check for forbidden characters in header */
  {
    const void *p = memchr (header, '\t', header_len);
    if (NULL != p)
      {
        if (log)
          {
            fprintf(log, "EXIT add_response_entry_n: header contains '\\t' at offset %td -> false\n", (const char *)p - header);
            fclose(log);
          }
        return false;
      }
  }

  {
    const void *p = memchr (header, ' ', header_len);
    if (NULL != p)
      {
        if (log)
          {
            fprintf(log, "EXIT add_response_entry_n: header contains ' ' at offset %td -> false\n", (const char *)p - header);
            fclose(log);
          }
        return false;
      }
  }

  {
    const void *p = memchr (header, '\r', header_len);
    if (NULL != p)
      {
        if (log)
          {
            fprintf(log, "EXIT add_response_entry_n: header contains '\\r' at offset %td -> false\n", (const char *)p - header);
            fclose(log);
          }
        return false;
      }
  }

  {
    const void *p = memchr (header, '\n', header_len);
    if (NULL != p)
      {
        if (log)
          {
            fprintf(log, "EXIT add_response_entry_n: header contains '\\n' at offset %td -> false\n", (const char *)p - header);
            fclose(log);
          }
        return false;
      }
  }

  /* Check for forbidden characters in content */
  {
    const void *p = memchr (content, '\r', content_len);
    if (NULL != p)
      {
        if (log)
          {
            fprintf(log, "EXIT add_response_entry_n: content contains '\\r' at offset %td -> false\n", (const char *)p - content);
            fclose(log);
          }
        return false;
      }
  }

  {
    const void *p = memchr (content, '\n', content_len);
    if (NULL != p)
      {
        if (log)
          {
            fprintf(log, "EXIT add_response_entry_n: content contains '\\n' at offset %td -> false\n", (const char *)p - content);
            fclose(log);
          }
        return false;
      }
  }

  /* Before calling the unchecked add, log the call arguments to provide evidence of pointer values and lengths. */
  if (log)
    {
      fprintf(log, "CALL MHD_add_response_entry_no_check_: response=%p kind=%d header=%p header_len=%zu content=%p content_len=%zu\n",
              (void *) response, (int) kind, (const void *) header, header_len, (const void *) content, content_len);
      fflush(log);
    }

  bool result = MHD_add_response_entry_no_check_ (response, kind, header, header_len,
                                                   content, content_len);

  if (log)
    {
      fprintf(log, "RETURN from MHD_add_response_entry_no_check_: result=%d\n", result ? 1 : 0);
      fclose(log);
    }

  return result;
}
