static enum MHD_Result
add_response_header_connection (struct MHD_Response *response,
                                const char *value)
{
  static const char *key = MHD_HTTP_HEADER_CONNECTION;
  /** the length of the "Connection" key */
  static const size_t key_len =
    MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONNECTION);
  size_t value_len; /**< the length of the @a value */
  size_t old_value_len; /**< the length of the existing "Connection" value */
  size_t buf_size;  /**< the size of the buffer */
  size_t norm_len;  /**< the length of the normalised value */
  char *buf;        /**< the temporal buffer */
  struct MHD_HTTP_Res_Header *hdr; /**< existing "Connection" header */
  bool value_has_close; /**< the @a value has "close" token */
  bool already_has_close; /**< existing "Connection" header has "close" token */
  size_t pos = 0;   /**< position of addition in the @a buf */

  /* Open a log file for debugging/control-flow tracing. We intentionally
     append so repeated calls accumulate evidence. We will flush after
     important events so other agents can inspect the file even if the
     process does not terminate cleanly. */
  FILE *log = fopen("/tmp/logs/1128.txt", "a");
  if (log)
  {
    fprintf (log, "ENTER add_response_header_connection: response=%p, value=%s\n",
             (void *) response, (value != NULL) ? value : "(null)");
    fflush (log);
  }

  if ( (NULL != strchr (value, '\r')) ||
       (NULL != strchr (value, '\n')) )
  {
    if (log)
    {
      fprintf (log, "REJECT: value contains CR or LF\n");
      fflush (log);
    }
    return MHD_NO;
  }

  if (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                       key, key_len);
    already_has_close =
      (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));

    /* Log the values used in the assertion so that the invariant can be
       reproduced even if the mhd_assert macro is compiled out in production. */
    if (log)
    {
      fprintf (log, "BRANCH: has_connection_hdr=1, flags_auto=0x%lx, hdr=%p\n",
               (unsigned long) response->flags_auto, (void *) hdr);
      if (hdr && hdr->value)
        fprintf (log, "HDR_VALUE: '%.*s' (size=%zu)\n",
                 (int) hdr->value_size, hdr->value, hdr->value_size);
      fprintf (log, "ASSERT_CHECK: already_has_close=%d\n",
               (int) already_has_close);
      fflush (log);
    }

    mhd_assert (already_has_close == (0 == memcmp (hdr->value, "close", 5)));
    mhd_assert (NULL != hdr);
  }
  else
  {
    hdr = NULL;
    already_has_close = false;

    if (log)
    {
      fprintf (log, "BRANCH: has_connection_hdr=0, verifying no existing header\n");
      fflush (log);
    }

    mhd_assert (NULL == MHD_get_response_element_n_ (response,
                                                     MHD_HEADER_KIND,
                                                     key, key_len));
    mhd_assert (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
  }
  if (NULL != hdr)
    old_value_len = hdr->value_size + 2; /* additional size for ", " */
  else
    old_value_len = 0;

  value_len = strlen (value);
  if (value_len >= SSIZE_MAX)
  {
    if (log)
    {
      fprintf (log, "REJECT: value_len >= SSIZE_MAX (%zu)\n", value_len);
      fflush (log);
    }
    return MHD_NO;
  }
  /* Additional space for normalisation and zero-termination */
  norm_len = value_len + value_len / 2 + 1;
  if (norm_len >= SSIZE_MAX)
  {
    if (log)
    {
      fprintf (log, "REJECT: norm_len >= SSIZE_MAX (%zu)\n", norm_len);
      fflush (log);
    }
    return MHD_NO;
  }
  buf_size = old_value_len + (size_t) norm_len;

  if (log)
  {
    fprintf (log, "ALLOC: old_value_len=%zu, value_len=%zu, norm_len=%zu, buf_size=%zu\n",
             old_value_len, value_len, norm_len, buf_size);
    fflush (log);
  }

  buf = malloc (buf_size);
  if (NULL == buf)
  {
    if (log)
    {
      fprintf (log, "ALLOC_FAIL: malloc returned NULL for size %zu\n", buf_size);
      fflush (log);
    }
    return MHD_NO;
  }
  if (1)
  { /* local scope */
    ssize_t norm_len_s = (ssize_t) norm_len;
    /* Remove "close" token (if any), it will be moved to the front */
    value_has_close = MHD_str_remove_token_caseless_ (value, value_len, "close",
                                                      MHD_STATICSTR_LEN_ ( \
                                                        "close"),
                                                      buf + old_value_len,
                                                      &norm_len_s);
    if (log)
    {
      fprintf (log, "TOKEN_REMOVE: value_has_close=%d, norm_len_s=%zd\n",
               (int) value_has_close, norm_len_s);
      fflush (log);
    }

    mhd_assert (0 <= norm_len_s);
    if (0 > norm_len_s)
    {
      /* Must never happen with realistic sizes */
      if (log)
      {
        fprintf (log, "ERROR: norm_len_s < 0 (%zd), freeing buf and returning\n",
                 norm_len_s);
        fflush (log);
      }
      free (buf);
      return MHD_NO;
    }
    else
      norm_len = (size_t) norm_len_s;
  }
#ifdef UPGRADE_SUPPORT
  if ( (NULL != response->upgrade_handler) && value_has_close)
  { /* The "close" token cannot be used with connection "upgrade" */
    if (log)
    {
      fprintf (log, "REJECT: upgrade_handler present and value_has_close=1\n");
      fflush (log);
    }
    free (buf);
    return MHD_NO;
  }
#endif /* UPGRADE_SUPPORT */
  if (0 != norm_len)
    MHD_str_remove_tokens_caseless_ (buf + old_value_len, &norm_len,
                                     "keep-alive",
                                     MHD_STATICSTR_LEN_ ("keep-alive"));
  if (0 == norm_len)
  { /* New value is empty after normalisation */
    if (! value_has_close)
    { /* The new value had no tokens */
      if (log)
      {
        fprintf (log, "REJECT: norm_len==0 && !value_has_close -> nothing to add\n");
        fflush (log);
      }
      free (buf);
      return MHD_NO;
    }
    if (already_has_close)
    { /* The "close" token is already present, nothing to modify */
      if (log)
      {
        fprintf (log, "NOOP: already_has_close=1 and new value empty -> returning YES\n");
        fflush (log);
      }
      free (buf);
      return MHD_YES;
    }
  }
  /* Add "close" token if required */
  if (value_has_close && ! already_has_close)
  {
    /* Need to insert "close" token at the first position */
    if (log)
    {
      fprintf (log, "INSERT_CLOSE: need to insert 'close' token. buf_size=%zu, old_value_len=%zu, norm_len=%zu\n",
               buf_size, old_value_len, norm_len);
      fflush (log);
    }

    mhd_assert (buf_size >= old_value_len + norm_len   \
                + MHD_STATICSTR_LEN_ ("close, ") + 1);
    if (0 != norm_len)
      memmove (buf + MHD_STATICSTR_LEN_ ("close, ") + old_value_len,
               buf + old_value_len, norm_len + 1);
    memcpy (buf, "close", MHD_STATICSTR_LEN_ ("close"));
    pos += MHD_STATICSTR_LEN_ ("close");
  }
  /* Add old value tokens (if any) */
  if (0 != old_value_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    memcpy (buf + pos, hdr->value,
            hdr->value_size);
    pos += hdr->value_size;
  }
  /* Add new value token (if any) */
  if (0 != norm_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    /* The new value tokens must be already at the correct position */
    if (log)
    {
      fprintf (log, "ASSERT_POS_CHECK: value_has_close=%d, already_has_close=%d, MHD_STATICSTR_LEN(close, )=%zu, old_value_len=%zu, pos=%zu\n",
               (int) value_has_close, (int) already_has_close,
               (size_t) MHD_STATICSTR_LEN_ ("close, "), old_value_len, pos);
      fflush (log);
    }

    mhd_assert ((value_has_close && ! already_has_close) ? \
                (MHD_STATICSTR_LEN_ ("close, ") + old_value_len == pos) : \
                (old_value_len == pos));
    pos += norm_len;
  }
  mhd_assert (buf_size > pos);
  if (log)
  {
    fprintf (log, "FINALISE: buf_size=%zu, pos=%zu -> terminating buffer\n",
             buf_size, pos);
    fflush (log);
  }
  buf[pos] = 0; /* Null terminate the result */

  if (NULL == hdr)
  {
    struct MHD_HTTP_Res_Header *new_hdr; /**< new "Connection" header */
    /* Create new response header entry */
    new_hdr = MHD_calloc_ (1, sizeof (struct MHD_HTTP_Res_Header));
    if (NULL != new_hdr)
    {
      new_hdr->header = malloc (key_len + 1);
      if (NULL != new_hdr->header)
      {
        memcpy (new_hdr->header, key, key_len + 1);
        new_hdr->header_size = key_len;
        new_hdr->value = buf;
        new_hdr->value_size = pos;
        new_hdr->kind = MHD_HEADER_KIND;
        if (value_has_close)
          response->flags_auto = (MHD_RAF_HAS_CONNECTION_HDR
                                  | MHD_RAF_HAS_CONNECTION_CLOSE);
        else
          response->flags_auto = MHD_RAF_HAS_CONNECTION_HDR;
        _MHD_insert_header_first (response, new_hdr);
        if (log)
        {
          fprintf (log, "RETURN: created new header, flags_auto=0x%lx, returning MHD_YES\n",
                   (unsigned long) response->flags_auto);
          fflush (log);
        }
        return MHD_YES;
      }
      free (new_hdr);
    }
    if (log)
    {
      fprintf (log, "RETURN_FAIL: unable to create new header or header->header allocation failed\n");
      fflush (log);
    }
    free (buf);
    return MHD_NO;
  }

  /* Update existing header entry */
  free (hdr->value);
  hdr->value = buf;
  hdr->value_size = pos;
  if (value_has_close && ! already_has_close)
    response->flags_auto |= MHD_RAF_HAS_CONNECTION_CLOSE;

  if (log)
  {
    fprintf (log, "UPDATE: updated existing header at %p, new value_size=%zu, flags_auto=0x%lx, returning MHD_YES\n",
             (void *) hdr, hdr->value_size, (unsigned long) response->flags_auto);
    fflush (log);
    /* keep the file open or closed is not strictly necessary here; we flushed */
    fclose (log);
  }
  return MHD_YES;
}
