static enum MHD_Result
add_response_header_connection (struct MHD_Response *response,
                                const char *value)
{
  static const char *key = MHD_HTTP_HEADER_CONNECTION;
  /** the length of the "Connection" key */
  static const size_t key_len =
    MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONNECTION);
  size_t value_len; /**< the length of the @a value */
  size_t old_value_len; /**< the length of the existing "Connection" value */
  size_t buf_size;  /**< the size of the buffer */
  size_t norm_len;  /**< the length of the normalised value */
  char *buf;        /**< the temporal buffer */
  struct MHD_HTTP_Res_Header *hdr; /**< existing "Connection" header */
  bool value_has_close; /**< the @a value has "close" token */
  bool already_has_close; /**< existing "Connection" header has "close" token */
  size_t pos = 0;   /**< position of addition in the @a buf */

  /* Logging helper: append logs to /tmp/logs/1128.txt. */
  FILE *logf = fopen("/tmp/logs/1128.txt", "a");
  if (logf)
  {
    fprintf(logf, "ENTER add_response_header_connection: response=%p, value=\"%s\"\n", (void *)response, value ? value : "(null)");
    fflush(logf);
  }

  if ( (NULL != strchr (value, '\r')) ||
       (NULL != strchr (value, '\n')) )
  {
    if (logf)
    {
      fprintf(logf, "REJECTED: value contains CR or LF\n");
      fclose(logf);
    }
    return MHD_NO;
  }

  if (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    if (logf)
      fprintf(logf, "BRANCH: response->flags_auto indicates existing Connection header (flags_auto=0x%zx)\n", (size_t)response->flags_auto);

    hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                       key, key_len);
    already_has_close =
      (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));

    /* Evaluate memcmp as original code does (this may dereference hdr->value as in original). */
    {
      int cmp_res = 0;
      if (hdr && hdr->value)
        cmp_res = memcmp (hdr->value, "close", 5);
      else
      {
        /* Preserve original behavior: the original code performed memcmp before asserting hdr non-null.
           If hdr is NULL, this block attempts to document the situation without changing control flow.
           Do not avoid dereference here because that would change original semantics; however, we
           first record the hdr pointer situation and then perform the same memcmp if safe. */
      }
      if (logf)
      {
        fprintf(logf, "hdr=%p, hdr->value=%p, hdr->value_size=%zu, already_has_close=%d, memcmp_close_5=%d\n",
                (void *)hdr,
                hdr ? (void *)hdr->value : NULL,
                hdr ? hdr->value_size : 0,
                already_has_close,
                cmp_res);
        fflush(logf);
      }
    }

    mhd_assert (already_has_close == (0 == memcmp (hdr->value, "close", 5)));
    mhd_assert (NULL != hdr);
  }
  else
  {
    if (logf)
      fprintf(logf, "BRANCH: no existing Connection header (flags_auto=0x%zx)\n", (size_t)response->flags_auto);

    hdr = NULL;
    already_has_close = false;
    mhd_assert (NULL == MHD_get_response_element_n_ (response,
                                                     MHD_HEADER_KIND,
                                                     key, key_len));
  }
  if (NULL != hdr)
    old_value_len = hdr->value_size + 2; /* additional size for ", " */
  else
    old_value_len = 0;

  value_len = strlen (value);
  if (value_len >= SSIZE_MAX)
  {
    if (logf)
    {
      fprintf(logf, "REJECTED: value_len >= SSIZE_MAX (%zu)\n", value_len);
      fclose(logf);
    }
    return MHD_NO;
  }
  /* Additional space for normalisation and zero-termination */
  norm_len = value_len + value_len / 2 + 1;
  if (norm_len >= SSIZE_MAX)
  {
    if (logf)
    {
      fprintf(logf, "REJECTED: norm_len >= SSIZE_MAX (%zu)\n", norm_len);
      fclose(logf);
    }
    return MHD_NO;
  }
  buf_size = old_value_len + (size_t) norm_len;

  buf = malloc (buf_size);
  if (NULL == buf)
  {
    if (logf)
    {
      fprintf(logf, "REJECTED: malloc failed for buf_size=%zu\n", buf_size);
      fclose(logf);
    }
    return MHD_NO;
  }
  if (1)
  { /* local scope */
    ssize_t norm_len_s = (ssize_t) norm_len;
    /* Remove "close" token (if any), it will be moved to the front */
    value_has_close = MHD_str_remove_token_caseless_ (value, value_len, "close",
                                                      MHD_STATICSTR_LEN_ ( \
                                                        "close"),
                                                      buf + old_value_len,
                                                      &norm_len_s);
    if (logf)
      fprintf(logf, "After MHD_str_remove_token_caseless_: value_has_close=%d, norm_len_s=%zd\n",
              value_has_close, norm_len_s);

    mhd_assert (0 <= norm_len_s);
    if (0 > norm_len_s)
    {
      /* Must never happen with realistic sizes */
      if (logf)
      {
        fprintf(logf, "ERROR: norm_len_s < 0 (%zd) - freeing buf and returning MHD_NO\n", norm_len_s);
        fclose(logf);
      }
      free (buf);
      return MHD_NO;
    }
    else
      norm_len = (size_t) norm_len_s;
  }
#ifdef UPGRADE_SUPPORT
  if ( (NULL != response->upgrade_handler) && value_has_close)
  { /* The "close" token cannot be used with connection "upgrade" */
    if (logf)
    {
      fprintf(logf, "REJECTED: upgrade_handler present and value_has_close==true\n");
      fclose(logf);
    }
    free (buf);
    return MHD_NO;
  }
#endif /* UPGRADE_SUPPORT */
  if (0 != norm_len)
    MHD_str_remove_tokens_caseless_ (buf + old_value_len, &norm_len,
                                     "keep-alive",
                                     MHD_STATICSTR_LEN_ ("keep-alive"));
  if (0 == norm_len)
  { /* New value is empty after normalisation */
    if (! value_has_close)
    { /* The new value had no tokens */
      if (logf)
      {
        fprintf(logf, "REJECTED: normalized value empty and value_has_close==false\n");
        fclose(logf);
      }
      free (buf);
      return MHD_NO;
    }
    if (already_has_close)
    { /* The "close" token is already present, nothing to modify */
      if (logf)
      {
        fprintf(logf, "NO-OP: normalized empty but already_has_close==true -> returning MHD_YES\n");
        fclose(logf);
      }
      free (buf);
      return MHD_YES;
    }
  }
  /* Add "close" token if required */
  if (value_has_close && ! already_has_close)
  {
    /* Need to insert "close" token at the first position */
    mhd_assert (buf_size >= old_value_len + norm_len   \
                + MHD_STATICSTR_LEN_ ("close, ") + 1);
    if (0 != norm_len)
      memmove (buf + MHD_STATICSTR_LEN_ ("close, ") + old_value_len,
               buf + old_value_len, norm_len + 1);
    memcpy (buf, "close", MHD_STATICSTR_LEN_ ("close"));
    pos += MHD_STATICSTR_LEN_ ("close");
    if (logf)
      fprintf(logf, "Inserted 'close' token: pos=%zu, buf_size=%zu\n", pos, buf_size);
  }
  /* Add old value tokens (if any) */
  if (0 != old_value_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ','.
;      buf[pos++] = ' ';
    }
    memcpy (buf + pos, hdr->value,
            hdr->value_size);
    pos += hdr->value_size;
    if (logf)
      fprintf(logf, "Copied old header value: hdr->value_size=%zu, new pos=%zu\n", hdr->value_size, pos);
  }
  /* Add new value token (if any) */
  if (0 != norm_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    /* The new value tokens must be already at the correct position */
    mhd_assert ((value_has_close && ! already_has_close) ? \
                (MHD_STATICSTR_LEN_ ("close, ") + old_value_len == pos) : \
                (old_value_len == pos));
    pos += norm_len;
    if (logf)
      fprintf(logf, "Appended normalized new value: norm_len=%zu, pos=%zu\n", norm_len, pos);
  }
  mhd_assert (buf_size > pos);
  buf[pos] = 0; /* Null terminate the result */

  if (NULL == hdr)
  {
    struct MHD_HTTP_Res_Header *new_hdr; /**< new "Connection" header */
    /* Create new response header entry */
    new_hdr = MHD_calloc_ (1, sizeof (struct MHD_HTTP_Res_Header));
    if (NULL != new_hdr)
    {
      new_hdr->header = malloc (key_len + 1);
      if (NULL != new_hdr->header)
      {
        memcpy (new_hdr->header, key, key_len + 1);
        new_hdr->header_size = key_len;
        new_hdr->value = buf;
        new_hdr->value_size = pos;
        new_hdr->kind = MHD_HEADER_KIND;
        if (value_has_close)
          response->flags_auto = (MHD_RAF_HAS_CONNECTION_HDR
                                  | MHD_RAF_HAS_CONNECTION_CLOSE);
        else
          response->flags_auto = MHD_RAF_HAS_CONNECTION_HDR;
        _MHD_insert_header_first (response, new_hdr);
        if (logf)
        {
          fprintf(logf, "Inserted new header: header=%p, value=%p, value_size=%zu, flags_auto=0x%zx -> returning MHD_YES\n",
                  (void *)new_hdr, (void *)new_hdr->value, new_hdr->value_size, (size_t)response->flags_auto);
          fclose(logf);
        }
        return MHD_YES;
      }
      free (new_hdr);
    }
    if (logf)
    {
      fprintf(logf, "FAILED to create new_hdr or allocate new_hdr->header -> freeing buf and returning MHD_NO\n");
      fclose(logf);
    }
    free (buf);
    return MHD_NO;
  }

  /* Update existing header entry */
  if (logf)
    fprintf(logf, "Updating existing header hdr=%p: freeing old value=%p and replacing with buf=%p, new pos=%zu\n",
            (void *)hdr, (void *)hdr->value, (void *)buf, pos);
  free (hdr->value);
  hdr->value = buf;
  hdr->value_size = pos;
  if (value_has_close && ! already_has_close)
    response->flags_auto |= MHD_RAF_HAS_CONNECTION_CLOSE;
  if (logf)
  {
    fprintf(logf, "DONE: updated header hdr=%p, hdr->value=%p, hdr->value_size=%zu, flags_auto=0x%zx -> returning MHD_YES\n",
            (void *)hdr, (void *)hdr->value, hdr->value_size, (size_t)response->flags_auto);
    fclose(logf);
  }
  return MHD_YES;
}
