static enum MHD_Result
add_response_header_connection (struct MHD_Response *response,
                                const char *value)
{
  static const char *key = MHD_HTTP_HEADER_CONNECTION;
  /** the length of the "Connection" key */
  static const size_t key_len =
    MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONNECTION);
  size_t value_len; /**< the length of the @a value */
  size_t old_value_len; /**< the length of the existing "Connection" value */
  size_t buf_size;  /**< the size of the buffer */
  size_t norm_len;  /**< the length of the normalised value */
  char *buf;        /**< the temporal buffer */
  struct MHD_HTTP_Res_Header *hdr; /**< existing "Connection" header */
  bool value_has_close; /**< the @a value has "close" token */
  bool already_has_close; /**< existing "Connection" header has "close" token */
  size_t pos = 0;   /**< position of addition in the @a buf */

  /* Logging: entry */
  {
    FILE *log = fopen("/tmp/logs/1123.txt", "a");
    if (NULL != log)
    {
      fprintf (log, "ENTER add_response_header_connection: response=%p, value=%p\n",
               (void *) response, (const void *) value);
      fclose (log);
    }
  }

  /* Check for CR or LF in value. Keep the same calls as original but
     capture the results so we can log them before returning. Note:
     calling strchr on a NULL value is the same behavior as original code. */
  {
    char *cr = NULL;
    char *lf = NULL;

    cr = strchr ((const char *) value, '\r');
    lf = strchr ((const char *) value, '\n');

    /* Logging: result of CR/LF scan */
    {
      FILE *log = fopen("/tmp/logs/1123.txt", "a");
      if (NULL != log)
      {
        fprintf (log, "SCAN CR/LF: value=%p, cr=%p, lf=%p\n",
                 (const void *) value, (void *) cr, (void *) lf);
        fclose (log);
      }
    }

    if ( (NULL != cr) || (NULL != lf) )
    {
      FILE *log = fopen("/tmp/logs/1123.txt", "a");
      if (NULL != log)
      {
        fprintf (log, "RETURN: contains CR or LF -> MHD_NO\n");
        fclose (log);
      }
      return MHD_NO;
    }
  }

  if (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                       key, key_len);
    already_has_close =
      (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
    mhd_assert (already_has_close == (0 == memcmp (hdr->value, "close", 5)));
    mhd_assert (NULL != hdr);

    /* Logging: existing header present */
    {
      FILE *log = fopen("/tmp/logs/1123.txt", "a");
      if (NULL != log)
      {
        fprintf (log, "EXISTING HDR: hdr=%p, hdr->value=%p, hdr->value_size=%zu, already_has_close=%d\n",
                 (void *) hdr, (void *) (hdr ? hdr->value : NULL),
                 (hdr ? hdr->value_size : 0), (int) already_has_close);
        fclose (log);
      }
    }
  }
  else
  {
    hdr = NULL;
    already_has_close = false;
    mhd_assert (NULL == MHD_get_response_element_n_ (response,
                                                     MHD_HEADER_KIND,
                                                     key, key_len));
    mhd_assert (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));

    /* Logging: no existing header */
    {
      FILE *log = fopen("/tmp/logs/1123.txt", "a");
      if (NULL != log)
      {
        fprintf (log, "NO EXISTING HDR: hdr=NULL, already_has_close=0\n");
        fclose (log);
      }
    }
  }
  if (NULL != hdr)
    old_value_len = hdr->value_size + 2; /* additional size for ", " */
  else
    old_value_len = 0;

  /* Compute value_len. This will crash if value is NULL (same as original). */
  value_len = strlen (value);

  /* Logging: value length */
  {
    FILE *log = fopen("/tmp/logs/1123.txt", "a");
    if (NULL != log)
    {
      fprintf (log, "value_len=%zu, old_value_len=%zu\n", value_len, old_value_len);
      fclose (log);
    }
  }

  if (value_len >= SSIZE_MAX)
  {
    FILE *log = fopen("/tmp/logs/1123.txt", "a");
    if (NULL != log)
    {
      fprintf (log, "RETURN: value_len >= SSIZE_MAX -> MHD_NO\n");
      fclose (log);
    }
    return MHD_NO;
  }
  /* Additional space for normalisation and zero-termination */
  norm_len = value_len + value_len / 2 + 1;
  if (norm_len >= SSIZE_MAX)
  {
    FILE *log = fopen("/tmp/logs/1123.txt", "a");
    if (NULL != log)
    {
      fprintf (log, "RETURN: norm_len >= SSIZE_MAX -> MHD_NO\n");
      fclose (log);
    }
    return MHD_NO;
  }
  buf_size = old_value_len + (size_t) norm_len;

  /* Logging: computed sizes */
  {
    FILE *log = fopen("/tmp/logs/1123.txt", "a");
    if (NULL != log)
    {
      fprintf (log, "buf_size=%zu, norm_len=%zu\n", buf_size, norm_len);
      fclose (log);
    }
  }

  buf = malloc (buf_size);
  if (NULL == buf)
  {
    FILE *log = fopen("/tmp/logs/1123.txt", "a");
    if (NULL != log)
    {
      fprintf (log, "RETURN: malloc failed (buf==NULL) -> MHD_NO\n");
      fclose (log);
    }
    return MHD_NO;
  }
  if (1)
  { /* local scope */
    ssize_t norm_len_s = (ssize_t) norm_len;
    /* Remove "close" token (if any), it will be moved to the front */
    value_has_close = MHD_str_remove_token_caseless_ (value, value_len, "close",
                                                      MHD_STATICSTR_LEN_ ( \
                                                        "close"),
                                                      buf + old_value_len,
                                                      &norm_len_s);
    mhd_assert (0 <= norm_len_s);
    if (0 > norm_len_s)
    {
      /* Must never happen with realistic sizes */
      free (buf);

      FILE *log = fopen("/tmp/logs/1123.txt", "a");
      if (NULL != log)
      {
        fprintf (log, "RETURN: norm_len_s < 0 after remove token -> MHD_NO\n");
        fclose (log);
      }
      return MHD_NO;
    }
    else
      norm_len = (size_t) norm_len_s;

    /* Logging: result of remove_token */
    {
      FILE *log = fopen("/tmp/logs/1123.txt", "a");
      if (NULL != log)
      {
        fprintf (log, "AFTER remove_token: value_has_close=%d, norm_len=%zu\n",
                 (int) value_has_close, norm_len);
        fclose (log);
      }
    }
  }
#ifdef UPGRADE_SUPPORT
  if ( (NULL != response->upgrade_handler) && value_has_close)
  { /* The "close" token cannot be used with connection "upgrade" */
    free (buf);

    FILE *log = fopen("/tmp/logs/1123.txt", "a");
    if (NULL != log)
    {
      fprintf (log, "RETURN: upgrade_handler present and value_has_close -> MHD_NO\n");
      fclose (log);
    }
    return MHD_NO;
  }
#endif /* UPGRADE_SUPPORT */
  if (0 != norm_len)
    MHD_str_remove_tokens_caseless_ (buf + old_value_len, &norm_len,
                                     "keep-alive",
                                     MHD_STATICSTR_LEN_ ("keep-alive"));

  /* Logging: norm_len after removal of keep-alive */
  {
    FILE *log = fopen("/tmp/logs/1123.txt", "a");
    if (NULL != log)
    {
      fprintf (log, "AFTER remove_tokens: norm_len=%zu\n", norm_len);
      fclose (log);
    }
  }

  if (0 == norm_len)
  { /* New value is empty after normalisation */
    if (! value_has_close)
    { /* The new value had no tokens */
      free (buf);

      FILE *log = fopen("/tmp/logs/1123.txt", "a");
      if (NULL != log)
      {
        fprintf (log, "RETURN: normalized value empty and no close -> MHD_NO\n");
        fclose (log);
      }
      return MHD_NO;
    }
    if (already_has_close)
    { /* The "close" token is already present, nothing to modify */
      free (buf);

      FILE *log = fopen("/tmp/logs/1123.txt", "a");
      if (NULL != log)
      {
        fprintf (log, "RETURN: normalized value empty but already_has_close -> MHD_YES\n");
        fclose (log);
      }
      return MHD_YES;
    }
  }
  /* Add "close" token if required */
  if (value_has_close && ! already_has_close)
  {
    /* Need to insert "close" token at the first position */
    mhd_assert (buf_size >= old_value_len + norm_len   \
                + MHD_STATICSTR_LEN_ ("close, ") + 1);
    if (0 != norm_len)
      memmove (buf + MHD_STATICSTR_LEN_ ("close, ") + old_value_len,
               buf + old_value_len, norm_len + 1);
    memcpy (buf, "close", MHD_STATICSTR_LEN_ ("close"));
    pos += MHD_STATICSTR_LEN_ ("close");

    /* Logging: inserted close token */
    {
      FILE *log = fopen("/tmp/logs/1123.txt", "a");
      if (NULL != log)
      {
        fprintf (log, "INSERT close: pos=%zu, buf_size=%zu\n", pos, buf_size);
        fclose (log);
      }
    }
  }
  /* Add old value tokens (if any) */
  if (0 != old_value_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    memcpy (buf + pos, hdr->value,
            hdr->value_size);
    pos += hdr->value_size;

    /* Logging: appended old value */
    {
      FILE *log = fopen("/tmp/logs/1123.txt", "a");
      if (NULL != log)
      {
        fprintf (log, "APPEND old value: new pos=%zu\n", pos);
        fclose (log);
      }
    }
  }
  /* Add new value token (if any) */
  if (0 != norm_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    /* The new value tokens must be already at the correct position */
    mhd_assert ((value_has_close && ! already_has_close) ? \
                (MHD_STATICSTR_LEN_ ("close, ") + old_value_len == pos) : \
                (old_value_len == pos));
    pos += norm_len;

    /* Logging: added new tokens */
    {
      FILE *log = fopen("/tmp/logs/1123.txt", "a");
      if (NULL != log)
      {
        fprintf (log, "ADD new tokens: norm_len=%zu, pos=%zu\n", norm_len, pos);
        fclose (log);
      }
    }
  }
  mhd_assert (buf_size > pos);
  buf[pos] = 0; /* Null terminate the result */

  if (NULL == hdr)
  {
    struct MHD_HTTP_Res_Header *new_hdr; /**< new "Connection" header */
    /* Create new response header entry */
    new_hdr = MHD_calloc_ (1, sizeof (struct MHD_HTTP_Res_Header));
    if (NULL != new_hdr)
    {
      new_hdr->header = malloc (key_len + 1);
      if (NULL != new_hdr->header)
      {
        memcpy (new_hdr->header, key, key_len + 1);
        new_hdr->header_size = key_len;
        new_hdr->value = buf;
        new_hdr->value_size = pos;
        new_hdr->kind = MHD_HEADER_KIND;
        if (value_has_close)
          response->flags_auto = (MHD_RAF_HAS_CONNECTION_HDR
                                  | MHD_RAF_HAS_CONNECTION_CLOSE);
        else
          response->flags_auto = MHD_RAF_HAS_CONNECTION_HDR;
        _MHD_insert_header_first (response, new_hdr);

        /* Logging: created new header */
        {
          FILE *log = fopen("/tmp/logs/1123.txt", "a");
          if (NULL != log)
          {
            fprintf (log, "RETURN: created new header new_hdr=%p, new_hdr->value=%p, new_hdr->value_size=%zu -> MHD_YES\n",
                     (void *) new_hdr, (void *) new_hdr->value, new_hdr->value_size);
            fclose (log);
          }
        }

        return MHD_YES;
      }
      free (new_hdr);
    }
    free (buf);

    FILE *log = fopen("/tmp/logs/1123.txt", "a");
    if (NULL != log)
    {
      fprintf (log, "RETURN: failed to allocate new_hdr->header -> MHD_NO\n");
      fclose (log);
    }
    return MHD_NO;
  }

  /* Update existing header entry */
  free (hdr->value);
  hdr->value = buf;
  hdr->value_size = pos;
  if (value_has_close && ! already_has_close)
    response->flags_auto |= MHD_RAF_HAS_CONNECTION_CLOSE;

  /* Logging: updated existing header */
  {
    FILE *log = fopen("/tmp/logs/1123.txt", "a");
    if (NULL != log)
    {
      fprintf (log, "RETURN: updated existing header hdr=%p, hdr->value=%p, hdr->value_size=%zu -> MHD_YES\n",
               (void *) hdr, (void *) hdr->value, hdr->value_size);
      fclose (log);
    }
  }
  return MHD_YES;
}
