static enum MHD_Result
add_response_header_connection (struct MHD_Response *response,
                                const char *value)
{
  static const char *key = MHD_HTTP_HEADER_CONNECTION;
  /** the length of the "Connection" key */
  static const size_t key_len =
    MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONNECTION);
  size_t value_len; /**< the length of the @a value */
  size_t old_value_len; /**< the length of the existing "Connection" value */
  size_t buf_size;  /**< the size of the buffer */
  size_t norm_len;  /**< the length of the normalised value */
  char *buf;        /**< the temporal buffer */
  struct MHD_HTTP_Res_Header *hdr; /**< existing "Connection" header */
  bool value_has_close; /**< the @a value has "close" token */
  bool already_has_close; /**< existing "Connection" header has "close" token */
  size_t pos = 0;   /**< position of addition in the @a buf */

  /* Open log file as early as possible to capture the control flow */
  {
    FILE *log = fopen("/tmp/logs/1123.txt", "a");
    if (log)
    {
      fprintf(log, "ENTER add_response_header_connection: response=%p value=%p\n",
              (void *) response, (void *) value);
      if (value)
        fprintf(log, "  initial value str=\"%s\"\n", value);
      else
        fprintf(log, "  initial value is NULL\n");
      fclose(log);
    }
  }

  if ( (NULL != strchr (value, '\r')) ||
       (NULL != strchr (value, '\n')) )
  {
    FILE *log = fopen("/tmp/logs/1123.txt", "a");
    if (log)
    {
      fprintf(log, "REJECT: value contains CR/LF\n");
      fclose(log);
    }
    return MHD_NO;
  }

  if (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                       key, key_len);
    already_has_close =
      (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
    mhd_assert (already_has_close == (0 == memcmp (hdr->value, "close", 5)));
    mhd_assert (NULL != hdr);
  }
  else
  {
    hdr = NULL;
    already_has_close = false;
    mhd_assert (NULL == MHD_get_response_element_n_ (response,
                                                     MHD_HEADER_KIND,
                                                     key, key_len));
    mhd_assert (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
  }

  if (NULL != hdr)
    old_value_len = hdr->value_size + 2; /* additional size for ", " */
  else
    old_value_len = 0;

  value_len = strlen (value);
  {
    FILE *log = fopen("/tmp/logs/1123.txt", "a");
    if (log)
    {
      fprintf(log, "After initial checks: value_len=%zu old_value_len=%zu already_has_close=%d hdr=%p hdr_value_size=%zu\n",
              value_len, old_value_len, (int) already_has_close, (void *) hdr,
              (hdr ? hdr->value_size : 0));
      fclose(log);
    }
  }

  if (value_len >= SSIZE_MAX)
  {
    FILE *log = fopen("/tmp/logs/1123.txt", "a");
    if (log)
    {
      fprintf(log, "REJECT: value_len >= SSIZE_MAX (%zu)\n", value_len);
      fclose(log);
    }
    return MHD_NO;
  }
  /* Additional space for normalisation and zero-termination */
  norm_len = value_len + value_len / 2 + 1;
  if (norm_len >= SSIZE_MAX)
  {
    FILE *log = fopen("/tmp/logs/1123.txt", "a");
    if (log)
    {
      fprintf(log, "REJECT: norm_len >= SSIZE_MAX (%zu)\n", norm_len);
      fclose(log);
    }
    return MHD_NO;
  }
  buf_size = old_value_len + (size_t) norm_len;

  {
    FILE *log = fopen("/tmp/logs/1123.txt", "a");
    if (log)
    {
      fprintf(log, "Buffer sizes: norm_len=%zu buf_size=%zu old_value_len=%zu\n",
              norm_len, buf_size, old_value_len);
      fclose(log);
    }
  }

  buf = malloc (buf_size);
  if (NULL == buf)
  {
    FILE *log = fopen("/tmp/logs/1123.txt", "a");
    if (log)
    {
      fprintf(log, "malloc failed for buf_size=%zu\n", buf_size);
      fclose(log);
    }
    return MHD_NO;
  }
  if (1)
  { /* local scope */
    ssize_t norm_len_s = (ssize_t) norm_len;
    /* Remove "close" token (if any), it will be moved to the front */
    value_has_close = MHD_str_remove_token_caseless_ (value, value_len, "close",
                                                      MHD_STATICSTR_LEN_ ( \
                                                        "close"),
                                                      buf + old_value_len,
                                                      &norm_len_s);
    mhd_assert (0 <= norm_len_s);
    if (0 > norm_len_s)
    {
      /* Must never happen with realistic sizes */
      free (buf);
      FILE *log = fopen("/tmp/logs/1123.txt", "a");
      if (log)
      {
        fprintf(log, "ERROR: norm_len_s < 0 (%zd)\n", norm_len_s);
        fclose(log);
      }
      return MHD_NO;
    }
    else
      norm_len = (size_t) norm_len_s;

    {
      FILE *log = fopen("/tmp/logs/1123.txt", "a");
      if (log)
      {
        fprintf(log, "After remove_token: value_has_close=%d norm_len=%zu buf=%p buf+old_value_len=%p\n",
                (int) value_has_close, norm_len, (void *) buf, (void *) (buf + old_value_len));
        fprintf(log, "  content_at_buf+old_value_len=\"");
        /* Try to print upto 200 chars of the buffer portion (safe-ish); don't crash if not null-terminated */
        if (norm_len > 0)
        {
          size_t to_print = (norm_len > 200 ? 200 : norm_len);
          fwrite(buf + old_value_len, 1, to_print, log);
          if (to_print < norm_len)
            fprintf(log, "...\n");
          else
            fprintf(log, "\n");
        }
        else
          fprintf(log, "<empty>\n");
        fclose(log);
      }
    }
  }
#ifdef UPGRADE_SUPPORT
  if ( (NULL != response->upgrade_handler) && value_has_close)
  { /* The "close" token cannot be used with connection "upgrade" */
    free (buf);
    FILE *log = fopen("/tmp/logs/1123.txt", "a");
    if (log)
    {
      fprintf(log, "REJECT: upgrade handler present but value_has_close\n");
      fclose(log);
    }
    return MHD_NO;
  }
#endif /* UPGRADE_SUPPORT */
  if (0 != norm_len)
    MHD_str_remove_tokens_caseless_ (buf + old_value_len, &norm_len,
                                     "keep-alive",
                                     MHD_STATICSTR_LEN_ ("keep-alive"));
  if (0 == norm_len)
  { /* New value is empty after normalisation */
    if (! value_has_close)
    { /* The new value had no tokens */
      free (buf);
      FILE *log = fopen("/tmp/logs/1123.txt", "a");
      if (log)
      {
        fprintf(log, "REJECT: normalized value empty and value_has_close==false\n");
        fclose(log);
      }
      return MHD_NO;
    }
    if (already_has_close)
    { /* The "close" token is already present, nothing to modify */
      free (buf);
      FILE *log = fopen("/tmp/logs/1123.txt", "a");
      if (log)
      {
        fprintf(log, "NO-OP: normalized value empty and already_has_close==true\n");
        fclose(log);
      }
      return MHD_YES;
    }
  }
  /* Add "close" token if required */
  if (value_has_close && ! already_has_close)
  {
    /* Need to insert "close" token at the first position */
    mhd_assert (buf_size >= old_value_len + norm_len   \
                + MHD_STATICSTR_LEN_ ("close, ") + 1);

    /* Log the parameters that will be used for the memmove to capture the
       potential out-of-bounds scenario reported by the other agent. */
    {
      size_t shift = MHD_STATICSTR_LEN_ ("close, ") + old_value_len;
      void *dest = (void *) (buf + shift);
      void *src = (void *) (buf + old_value_len);
      size_t n = norm_len + 1; /* include terminating zero copied by memmove */
      FILE *log = fopen("/tmp/logs/1123.txt", "a");
      if (log)
      {
        fprintf(log, "BEFORE memmove: buf=%p buf_size=%zu old_value_len=%zu norm_len=%zu shift=%zu dest=%p src=%p n=%zu\n",
                (void *) buf, buf_size, old_value_len, norm_len, shift, dest, src, n);
        /* Also log the computed last write address for clarity */
        fprintf(log, "  computed_last_write_address = %p\n",
                (void *) ((char *) dest + (n ? n - 1 : 0)));
        fclose(log);
      }

    }

    if (0 != norm_len)
      memmove (buf + MHD_STATICSTR_LEN_ ("close, ") + old_value_len,
               buf + old_value_len, norm_len + 1);
    memcpy (buf, "close", MHD_STATICSTR_LEN_ ("close"));
    pos += MHD_STATICSTR_LEN_ ("close");

    /* Log a small snapshot after memmove/memcpy */
    {
      FILE *log = fopen("/tmp/logs/1123.txt", "a");
      if (log)
      {
        fprintf(log, "AFTER memmove/memcpy: pos=%zu buf=%p buf_size=%zu\n",
                pos, (void *) buf, buf_size);
        /* try to print a short preview around the insertion point */
        size_t preview_off = 0;
        if (buf_size > 0)
        {
          preview_off = (old_value_len < 50) ? 0 : (old_value_len - 50);
          size_t preview_len = (buf_size - preview_off > 200) ? 200 : buf_size - preview_off;
          fprintf(log, "  preview (offset %zu, len %zu): ", preview_off, preview_len);
          fwrite(buf + preview_off, 1, preview_len, log);
          fprintf(log, "\n");
        }
        fclose(log);
      }
    }
  }
  /* Add old value tokens (if any) */
  if (0 != old_value_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    memcpy (buf + pos, hdr->value,
            hdr->value_size);
    pos += hdr->value_size;
  }
  /* Add new value token (if any) */
  if (0 != norm_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    /* The new value tokens must be already at the correct position */
    mhd_assert ((value_has_close && ! already_has_close) ? \
                (MHD_STATICSTR_LEN_ ("close, ") + old_value_len == pos) : \
                (old_value_len == pos));
    pos += norm_len;
  }
  mhd_assert (buf_size > pos);
  buf[pos] = 0; /* Null terminate the result */

  if (NULL == hdr)
  {
    struct MHD_HTTP_Res_Header *new_hdr; /**< new "Connection" header */
    /* Create new response header entry */
    new_hdr = MHD_calloc_ (1, sizeof (struct MHD_HTTP_Res_Header));
    if (NULL != new_hdr)
    {
      new_hdr->header = malloc (key_len + 1);
      if (NULL != new_hdr->header)
      {
        memcpy (new_hdr->header, key, key_len + 1);
        new_hdr->header_size = key_len;
        new_hdr->value = buf;
        new_hdr->value_size = pos;
        new_hdr->kind = MHD_HEADER_KIND;
        if (value_has_close)
          response->flags_auto = (MHD_RAF_HAS_CONNECTION_HDR
                                  | MHD_RAF_HAS_CONNECTION_CLOSE);
        else
          response->flags_auto = MHD_RAF_HAS_CONNECTION_HDR;
        _MHD_insert_header_first (response, new_hdr);

        FILE *log = fopen("/tmp/logs/1123.txt", "a");
        if (log)
        {
          fprintf(log, "INSERT new header: new_hdr=%p header=%p value=%p value_size=%zu\n",
                  (void *) new_hdr, (void *) new_hdr->header, (void *) new_hdr->value, new_hdr->value_size);
          fclose(log);
        }
        return MHD_YES;
      }
      free (new_hdr);
    }
    free (buf);
    FILE *log = fopen("/tmp/logs/1123.txt", "a");
    if (log)
    {
      fprintf(log, "FAIL: could not allocate new_hdr->header or new_hdr==NULL\n");
      fclose(log);
    }
    return MHD_NO;
  }

  /* Update existing header entry */
  free (hdr->value);
  hdr->value = buf;
  hdr->value_size = pos;
  if (value_has_close && ! already_has_close)
    response->flags_auto |= MHD_RAF_HAS_CONNECTION_CLOSE;

  {
    FILE *log = fopen("/tmp/logs/1123.txt", "a");
    if (log)
    {
      fprintf(log, "UPDATE existing header: hdr=%p new_value=%p new_value_size=%zu response_flags_auto=0x%08x\n",
              (void *) hdr, (void *) hdr->value, hdr->value_size, (unsigned) response->flags_auto);
      /* show final constructed header value up to a reasonable limit */
      size_t show_len = (hdr->value_size > 200) ? 200 : hdr->value_size;
      if (show_len > 0)
        fwrite(hdr->value, 1, show_len, log);
      fprintf(log, "\n");
      fclose(log);
    }
  }
  return MHD_YES;
}
