static enum MHD_Result
add_response_header_connection (struct MHD_Response *response,
                                const char *value)
{
  static const char *key = MHD_HTTP_HEADER_CONNECTION;
  /** the length of the "Connection" key */
  static const size_t key_len =
    MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONNECTION);
  size_t value_len; /**< the length of the @a value */
  size_t old_value_len; /**< the length of the existing "Connection" value */
  size_t buf_size;  /**< the size of the buffer */
  size_t norm_len;  /**< the length of the normalised value */
  char *buf;        /**< the temporal buffer */
  struct MHD_HTTP_Res_Header *hdr; /**< existing "Connection" header */
  bool value_has_close; /**< the @a value has "close" token */
  bool already_has_close; /**< existing "Connection" header has "close" token */
  size_t pos = 0;   /**< position of addition in the @a buf */

  /* Open a log file for diagnostic traces about control flow and state.
     We intentionally do not close the log here on every return to avoid
     altering the original control flow (and potential early crashes) of
     the function. We flush after each write so the test harness can read
     evidence even if the process crashes later. */
  FILE *mhd_log = fopen("/tmp/logs/1123.txt", "a");
  if (mhd_log)
  {
    fprintf (mhd_log, "ENTER add_response_header_connection: response=%p value=%p\n",
             (void *) response, (const void *) value);
    fflush (mhd_log);
  }

  if (mhd_log)
  {
    fprintf (mhd_log, "About to check for CR/LF in value pointer %p\n", (const void *) value);
    fflush (mhd_log);
  }

  if ( (NULL != strchr (value, '\r')) ||
       (NULL != strchr (value, '\n')) )
  {
    if (mhd_log)
    {
      fprintf (mhd_log, "Rejected: value contains CR or LF or strchr crashed (value=%p)\n",
               (const void *) value);
      fflush (mhd_log);
    }
    return MHD_NO;
  }

  if (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                       key, key_len);
    already_has_close =
      (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
    mhd_assert (already_has_close == (0 == memcmp (hdr->value, "close", 5)));
    mhd_assert (NULL != hdr);

    if (mhd_log)
    {
      fprintf (mhd_log, "Existing Connection header found: hdr=%p value_size=%zu already_has_close=%d\n",
               (void *) hdr, (size_t) hdr->value_size, (int) already_has_close);
      fflush (mhd_log);
    }
  }
  else
  {
    hdr = NULL;
    already_has_close = false;
    mhd_assert (NULL == MHD_get_response_element_n_ (response,
                                                     MHD_HEADER_KIND,
                                                     key, key_len));
    mhd_assert (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));

    if (mhd_log)
    {
      fprintf (mhd_log, "No existing Connection header.\n");
      fflush (mhd_log);
    }
  }
  if (NULL != hdr)
    old_value_len = hdr->value_size + 2; /* additional size for ", " */
  else
    old_value_len = 0;

  if (mhd_log)
  {
    fprintf (mhd_log, "About to call strlen on value pointer %p\n", (const void *) value);
    fflush (mhd_log);
  }

  value_len = strlen (value);

  if (mhd_log)
  {
    fprintf (mhd_log, "strlen(value) returned %zu (value=%p)\n", value_len, (const void *) value);
    fflush (mhd_log);
  }

  if (value_len >= SSIZE_MAX)
  {
    if (mhd_log)
    {
      fprintf (mhd_log, "Rejected: value_len >= SSIZE_MAX (%zu)\n", value_len);
      fflush (mhd_log);
    }
    return MHD_NO;
  }
  /* Additional space for normalisation and zero-termination */
  norm_len = value_len + value_len / 2 + 1;
  if (norm_len >= SSIZE_MAX)
  {
    if (mhd_log)
    {
      fprintf (mhd_log, "Rejected: norm_len >= SSIZE_MAX (%zu)\n", norm_len);
      fflush (mhd_log);
    }
    return MHD_NO;
  }
  buf_size = old_value_len + (size_t) norm_len;

  if (mhd_log)
  {
    fprintf (mhd_log, "Allocating buffer: buf_size=%zu old_value_len=%zu norm_len=%zu\n",
             buf_size, old_value_len, norm_len);
    fflush (mhd_log);
  }

  buf = malloc (buf_size);
  if (NULL == buf)
  {
    if (mhd_log)
    {
      fprintf (mhd_log, "malloc returned NULL for buf_size=%zu\n", buf_size);
      fflush (mhd_log);
    }
    return MHD_NO;
  }
  if (1)
  { /* local scope */
    ssize_t norm_len_s = (ssize_t) norm_len;
    /* Remove "close" token (if any), it will be moved to the front */
    if (mhd_log)
    {
      fprintf (mhd_log, "Calling MHD_str_remove_token_caseless_ with value=%p value_len=%zu\n",
               (const void *) value, value_len);
      fflush (mhd_log);
    }
    value_has_close = MHD_str_remove_token_caseless_ (value, value_len, "close",
                                                      MHD_STATICSTR_LEN_ ( \
                                                        "close"),
                                                      buf + old_value_len,
                                                      &norm_len_s);
    mhd_assert (0 <= norm_len_s);
    if (mhd_log)
    {
      fprintf (mhd_log, "MHD_str_remove_token_caseless_ returned value_has_close=%d norm_len_s=%zd\n",
               (int) value_has_close, norm_len_s);
      fflush (mhd_log);
    }
    if (0 > norm_len_s)
    {
      /* Must never happen with realistic sizes */
      free (buf);
      if (mhd_log)
      {
        fprintf (mhd_log, "Error: norm_len_s < 0, freeing buf and returning MHD_NO\n");
        fflush (mhd_log);
      }
      return MHD_NO;
    }
    else
      norm_len = (size_t) norm_len_s;
  }
#ifdef UPGRADE_SUPPORT
  if ( (NULL != response->upgrade_handler) && value_has_close)
  { /* The "close" token cannot be used with connection "upgrade" */
    free (buf);
    if (mhd_log)
    {
      fprintf (mhd_log, "Rejected: upgrade_handler present and value_has_close==true\n");
      fflush (mhd_log);
    }
    return MHD_NO;
  }
#endif /* UPGRADE_SUPPORT */
  if (0 != norm_len)
  {
    if (mhd_log)
    {
      fprintf (mhd_log, "Removing keep-alive tokens from normalized buffer at %p norm_len=%zu\n",
               (void *) (buf + old_value_len), norm_len);
      fflush (mhd_log);
    }
    MHD_str_remove_tokens_caseless_ (buf + old_value_len, &norm_len,
                                     "keep-alive",
                                     MHD_STATICSTR_LEN_ ("keep-alive"));
  }
  if (0 == norm_len)
  { /* New value is empty after normalisation */
    if (! value_has_close)
    { /* The new value had no tokens */
      free (buf);
      if (mhd_log)
      {
        fprintf (mhd_log, "New value empty after normalisation and no close token -> returning MHD_NO\n");
        fflush (mhd_log);
      }
      return MHD_NO;
    }
    if (already_has_close)
    { /* The "close" token is already present, nothing to modify */
      free (buf);
      if (mhd_log)
      {
        fprintf (mhd_log, "Already has close token -> nothing to modify, returning MHD_YES\n");
        fflush (mhd_log);
      }
      return MHD_YES;
    }
  }
  /* Add "close" token if required */
  if (value_has_close && ! already_has_close)
  {
    /* Need to insert "close" token at the first position */
    mhd_assert (buf_size >= old_value_len + norm_len   \
                + MHD_STATICSTR_LEN_ ("close, ") + 1);
    if (0 != norm_len)
      memmove (buf + MHD_STATICSTR_LEN_ ("close, ") + old_value_len,
               buf + old_value_len, norm_len + 1);
    memcpy (buf, "close", MHD_STATICSTR_LEN_ ("close"));
    pos += MHD_STATICSTR_LEN_ ("close");
    if (mhd_log)
    {
      fprintf (mhd_log, "Inserted 'close' token at buf (pos=%zu)\n", pos);
      fflush (mhd_log);
    }
  }
  /* Add old value tokens (if any) */
  if (0 != old_value_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    memcpy (buf + pos, hdr->value,
            hdr->value_size);
    pos += hdr->value_size;
    if (mhd_log)
    {
      fprintf (mhd_log, "Appended old header tokens: hdr=%p hdr->value_size=%zu new pos=%zu\n",
               (void *) hdr, (size_t) hdr->value_size, pos);
      fflush (mhd_log);
    }
  }
  /* Add new value token (if any) */
  if (0 != norm_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    /* The new value tokens must be already at the correct position */
    mhd_assert ((value_has_close && ! already_has_close) ? \
                (MHD_STATICSTR_LEN_ ("close, ") + old_value_len == pos) : \
                (old_value_len == pos));
    pos += norm_len;
    if (mhd_log)
    {
      fprintf (mhd_log, "Added new normalized tokens: norm_len=%zu resulting pos=%zu\n",
               norm_len, pos);
      fflush (mhd_log);
    }
  }
  mhd_assert (buf_size > pos);
  buf[pos] = 0; /* Null terminate the result */

  if (mhd_log)
  {
    fprintf (mhd_log, "Finalized buffer (pos=%zu buf_size=%zu) buf=%p\n", pos, buf_size, (void *) buf);
    fflush (mhd_log);
  }

  if (NULL == hdr)
  {
    struct MHD_HTTP_Res_Header *new_hdr; /**< new "Connection" header */
    /* Create new response header entry */
    new_hdr = MHD_calloc_ (1, sizeof (struct MHD_HTTP_Res_Header));
    if (NULL != new_hdr)
    {
      new_hdr->header = malloc (key_len + 1);
      if (NULL != new_hdr->header)
      {
        memcpy (new_hdr->header, key, key_len + 1);
        new_hdr->header_size = key_len;
        new_hdr->value = buf;
        new_hdr->value_size = pos;
        new_hdr->kind = MHD_HEADER_KIND;
        if (value_has_close)
          response->flags_auto = (MHD_RAF_HAS_CONNECTION_HDR
                                  | MHD_RAF_HAS_CONNECTION_CLOSE);
        else
          response->flags_auto = MHD_RAF_HAS_CONNECTION_HDR;
        _MHD_insert_header_first (response, new_hdr);
        if (mhd_log)
        {
          fprintf (mhd_log, "Inserted new header: new_hdr=%p header=%p value=%p value_size=%zu\n",
                   (void *) new_hdr, (void *) new_hdr->header, (void *) new_hdr->value, new_hdr->value_size);
          fflush (mhd_log);
        }
        return MHD_YES;
      }
      free (new_hdr);
    }
    free (buf);
    if (mhd_log)
    {
      fprintf (mhd_log, "Failed to create new header (malloc/calloc failure) -> returning MHD_NO\n");
      fflush (mhd_log);
    }
    return MHD_NO;
  }

  /* Update existing header entry */
  if (mhd_log)
  {
    fprintf (mhd_log, "Updating existing header: hdr=%p replacing old value at %p\n",
             (void *) hdr, (void *) hdr->value);
    fflush (mhd_log);
  }
  free (hdr->value);
  hdr->value = buf;
  hdr->value_size = pos;
  if (value_has_close && ! already_has_close)
    response->flags_auto |= MHD_RAF_HAS_CONNECTION_CLOSE;

  if (mhd_log)
  {
    fprintf (mhd_log, "Update complete: hdr=%p new value=%p value_size=%zu flags_auto=%#x -> returning MHD_YES\n",
             (void *) hdr, (void *) hdr->value, hdr->value_size, (unsigned) response->flags_auto);
    fflush (mhd_log);
  }

  return MHD_YES;
}
