static bool
add_user_headers (char *buf,
                  size_t *ppos,
                  size_t buf_size,
                  struct MHD_Response *response,
                  bool filter_transf_enc,
                  bool filter_content_len,
                  bool add_close,
                  bool add_keep_alive)
{
  struct MHD_Response *const r = response; /**< a short alias */
  struct MHD_HTTP_Res_Header *hdr; /**< Iterates through User-specified headers */
  size_t el_size; /**< the size of current element to be added to the @a buf */

  /* Open log for tracing control flow and state changes. */
  FILE *mhd_log = fopen("/tmp/logs/4382.txt", "a");
  if (mhd_log)
  {
    fprintf(mhd_log, "ENTER add_user_headers: buf=%p ppos=%p (*ppos)=%zu buf_size=%zu response=%p\n",
            (void *) buf, (void *) ppos, (ppos ? *ppos : (size_t)0), buf_size, (void *) response);
    fprintf(mhd_log, "  in params: filter_transf_enc=%d filter_content_len=%d add_close=%d add_keep_alive=%d\n",
            (int) filter_transf_enc, (int) filter_content_len, (int) add_close, (int) add_keep_alive);
  }

  mhd_assert (! add_close || ! add_keep_alive);

  if (0 == (r->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED))
  {
    if (mhd_log) fprintf(mhd_log, "  no TRANS_ENC_CHUNKED: clearing filter_transf_enc (was=%d)\n", (int) filter_transf_enc);
    filter_transf_enc = false;   /* No such header */
  }
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH))
  {
    if (mhd_log) fprintf(mhd_log, "  no CONTENT_LENGTH: clearing filter_content_len (was=%d)\n", (int) filter_content_len);
    filter_content_len = false;  /* No such header */
  }
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    if (mhd_log) fprintf(mhd_log, "  no CONNECTION_HDR: clearing add_close (was=%d) and add_keep_alive (was=%d)\n",
                         (int) add_close, (int) add_keep_alive);
    add_close = false;          /* No such header */
    add_keep_alive = false;     /* No such header */
    if (mhd_log) fprintf(mhd_log, "    add_close now=%d add_keep_alive now=%d\n", (int) add_close, (int) add_keep_alive);
  }
  else if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE))
  {
    if (mhd_log) fprintf(mhd_log, "  CONNECTION_CLOSE present: clearing add_close (was=%d)\n", (int) add_close);
    add_close = false;          /* "close" token was already set */
    if (mhd_log) fprintf(mhd_log, "    add_close now=%d\n", (int) add_close);
  }

  for (hdr = r->first_header; NULL != hdr; hdr = hdr->next)
  {
    size_t initial_pos = *ppos;
    if (mhd_log) fprintf(mhd_log, "  loop hdr=%p kind=%d header_size=%zu value_size=%zu initial_pos=%zu\n",
                         (void *) hdr, (int) hdr->kind, hdr->header_size, hdr->value_size, initial_pos);

    if (MHD_HEADER_KIND != hdr->kind)
    {
      if (mhd_log) fprintf(mhd_log, "    skipping: not MHD_HEADER_KIND\n");
      continue;
    }
    if (filter_transf_enc)
    { /* Need to filter-out "Transfer-Encoding" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_TRANSFER_ENCODING) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_TRANSFER_ENCODING,
                                          hdr->header, hdr->header_size)) )
      {
        if (mhd_log) fprintf(mhd_log, "    found Transfer-Encoding header, clearing filter_transf_enc and skipping header\n");
        filter_transf_enc = false; /* There is the only one such header */
        continue; /* Skip "Transfer-Encoding" header */
      }
    }
    if (filter_content_len)
    { /* Need to filter-out "Content-Length" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONTENT_LENGTH) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_CONTENT_LENGTH,
                                          hdr->header, hdr->header_size)) )
      {
        if (mhd_log) fprintf(mhd_log, "    found Content-Length header; resetting filter_transf_enc per flags (r->flags=0x%zx) and skipping header\n",
                             (size_t) r->flags);
        /* Reset filter flag if only one header is allowed */
        filter_transf_enc =
          (0 == (r->flags & MHD_RF_INSANITY_HEADER_CONTENT_LENGTH));
        if (mhd_log) fprintf(mhd_log, "    filter_transf_enc now=%d\n", (int) filter_transf_enc);
        continue; /* Skip "Content-Length" header */
      }
    }

    /* Add user header */
    el_size = hdr->header_size + 2 + hdr->value_size + 2;
    if (buf_size < *ppos + el_size)
    {
      if (mhd_log) fprintf(mhd_log, "    insufficient buffer: need %zu have %zu -> RETURN false\n", *ppos + el_size, buf_size);
      if (mhd_log) fclose(mhd_log);
      return false;
    }
    memcpy (buf + *ppos, hdr->header, hdr->header_size);
    (*ppos) += hdr->header_size;
    buf[(*ppos)++] = ':';
    buf[(*ppos)++] = ' ';
    if (add_close || add_keep_alive)
    {
      /* "Connection:" header must be always the first one */
      mhd_assert (MHD_str_equal_caseless_n_ (hdr->header, \
                                             MHD_HTTP_HEADER_CONNECTION, \
                                             hdr->header_size));

      if (add_close)
      {
        if (mhd_log) fprintf(mhd_log, "    adding token: close (add_close was=%d)\n", (int) add_close);
        el_size += MHD_STATICSTR_LEN_ ("close, ");
        if (buf_size < initial_pos + el_size)
        {
          if (mhd_log) fprintf(mhd_log, "    insufficient buffer for close token: need %zu have %zu -> RETURN false\n", initial_pos + el_size, buf_size);
          if (mhd_log) fclose(mhd_log);
          return false;
        }
        memcpy (buf + *ppos, "close, ",
                MHD_STATICSTR_LEN_ ("close, "));
        *ppos += MHD_STATICSTR_LEN_ ("close, ");
      }
      else
      {
        if (mhd_log) fprintf(mhd_log, "    adding token: Keep-Alive (add_keep_alive was=%d)\n", (int) add_keep_alive);
        el_size += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
        if (buf_size < initial_pos + el_size)
        {
          if (mhd_log) fprintf(mhd_log, "    insufficient buffer for Keep-Alive token: need %zu have %zu -> RETURN false\n", initial_pos + el_size, buf_size);
          if (mhd_log) fclose(mhd_log);
          return false;
        }
        memcpy (buf + *ppos, "Keep-Alive, ",
                MHD_STATICSTR_LEN_ ("Keep-Alive, "));
        *ppos += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
      }

      /* NOTE: This is the MISRA-reported modification of a non-pointer parameter.
         We log the change (old value already logged above) and then perform the
         same assignment as in the original implementation to preserve semantics. */
      if (mhd_log) fprintf(mhd_log, "    clearing add_close and add_keep_alive (before: add_close=%d add_keep_alive=%d)\n",
                           (int) add_close, (int) add_keep_alive);
      add_close = false;
      add_keep_alive = false;
      if (mhd_log) fprintf(mhd_log, "    after clear: add_close=%d add_keep_alive=%d\n", (int) add_close, (int) add_keep_alive);
    }
    if (0 != hdr->value_size)
      memcpy (buf + *ppos, hdr->value, hdr->value_size);
    *ppos += hdr->value_size;
    buf[(*ppos)++] = '\r';
    buf[(*ppos)++] = '\n';
    mhd_assert (initial_pos + el_size == (*ppos));
    if (mhd_log) fprintf(mhd_log, "    wrote header: advanced ppos from %zu to %zu (expected %zu)\n",
                         initial_pos, *ppos, initial_pos + el_size);
  }

  if (mhd_log) fprintf(mhd_log, "EXIT add_user_headers: returning true, final ppos=%zu\n", *ppos);
  if (mhd_log) fclose(mhd_log);
  return true;
}
