static bool
add_user_headers (char *buf,
                  size_t *ppos,
                  size_t buf_size,
                  struct MHD_Response *response,
                  bool filter_transf_enc,
                  bool filter_content_len,
                  bool add_close,
                  bool add_keep_alive)
{
  struct MHD_Response *const r = response; /**< a short alias */
  struct MHD_HTTP_Res_Header *hdr; /**< Iterates through User-specified headers */
  size_t el_size; /**< the size of current element to be added to the @a buf */
  bool result = true;
  FILE *log = NULL;

  /* Open log for tracing control flow and parameter changes. Use append mode
     so multiple calls accumulate evidence. */
  log = fopen("/tmp/logs/4382.txt", "a");
  if (log)
  {
    fprintf (log, "add_user_headers: entered\n");
    fprintf (log, "  initial args: filter_transf_enc=%d, filter_content_len=%d, add_close=%d, add_keep_alive=%d, buf_size=%zu, *ppos=%zu\n",
             filter_transf_enc, filter_content_len, add_close, add_keep_alive, buf_size, *ppos);
    fprintf (log, "  response->flags_auto=0x%08x, response->flags=0x%08x\n", r->flags_auto, r->flags);
  }

  mhd_assert (! add_close || ! add_keep_alive);

  if (0 == (r->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED))
  {
    if (log)
      fprintf (log, "  condition: no TRANS_ENC_CHUNKED -> setting filter_transf_enc from %d to 0\n", filter_transf_enc);
    filter_transf_enc = false;   /* No such header */
  }
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH))
  {
    if (log)
      fprintf (log, "  condition: no CONTENT_LENGTH -> setting filter_content_len from %d to 0\n", filter_content_len);
    filter_content_len = false;  /* No such header */
  }
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    if (log)
      fprintf (log, "  condition: no CONNECTION_HDR -> clearing add_close(%d) and add_keep_alive(%d)\n", add_close, add_keep_alive);
    add_close = false;          /* No such header */
    add_keep_alive = false;     /* No such header */
  }
  else if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE))
  {
    if (log)
      fprintf (log, "  condition: CONNECTION_CLOSE present -> clearing add_close (was %d)\n", add_close);
    add_close = false;          /* "close" token was already set */
  }

  for (hdr = r->first_header; NULL != hdr; hdr = hdr->next)
  {
    size_t initial_pos = *ppos;
    if (MHD_HEADER_KIND != hdr->kind)
      continue;
    if (filter_transf_enc)
    { /* Need to filter-out "Transfer-Encoding" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_TRANSFER_ENCODING) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_TRANSFER_ENCODING,
                                          hdr->header, hdr->header_size)) )
      {
        if (log)
          fprintf (log, "  matched Transfer-Encoding header at pos %zu: setting filter_transf_enc from %d to 0 and skipping header\n",
                   *ppos, filter_transf_enc);
        filter_transf_enc = false; /* There is the only one such header */
        continue; /* Skip "Transfer-Encoding" header */
      }
    }
    if (filter_content_len)
    { /* Need to filter-out "Content-Length" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONTENT_LENGTH) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_CONTENT_LENGTH,
                                          hdr->header, hdr->header_size)) )
      {
        /* Reset filter flag if only one header is allowed */
        if (log)
          fprintf (log, "  matched Content-Length header at pos %zu: previous filter_transf_enc=%d, response->flags=0x%08x\n",
                   *ppos, filter_transf_enc, r->flags);
        filter_transf_enc =
          (0 == (r->flags & MHD_RF_INSANITY_HEADER_CONTENT_LENGTH));
        if (log)
          fprintf (log, "    new filter_transf_enc=%d (based on MHD_RF_INSANITY_HEADER_CONTENT_LENGTH)\n", filter_transf_enc);
        continue; /* Skip "Content-Length" header */
      }
    }

    /* Add user header */
    el_size = hdr->header_size + 2 + hdr->value_size + 2;
    if (buf_size < *ppos + el_size)
    {
      if (log)
        fprintf (log, "  buffer too small: need %zu, have %zu -> returning false\n", *ppos + el_size, buf_size);
      result = false;
      goto cleanup;
    }
    memcpy (buf + *ppos, hdr->header, hdr->header_size);
    (*ppos) += hdr->header_size;
    buf[(*ppos)++] = ':';
    buf[(*ppos)++] = ' ';
    if (add_close || add_keep_alive)
    {
      /* "Connection:" header must be always the first one */
      mhd_assert (MHD_str_equal_caseless_n_ (hdr->header, \
                                             MHD_HTTP_HEADER_CONNECTION, \
                                             hdr->header_size));

      if (add_close)
      {
        el_size += MHD_STATICSTR_LEN_ ("close, ");
        if (buf_size < initial_pos + el_size)
        {
          if (log)
            fprintf (log, "  buffer too small for Connection close insertion: need %zu, have %zu -> returning false\n", initial_pos + el_size, buf_size);
          result = false;
          goto cleanup;
        }
        if (log)
          fprintf (log, "  inserting 'close, ' into Connection header at pos %zu\n", *ppos);
        memcpy (buf + *ppos, "close, ",
                MHD_STATICSTR_LEN_ ("close, "));
        *ppos += MHD_STATICSTR_LEN_ ("close, ");
      }
      else
      {
        el_size += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
        if (buf_size < initial_pos + el_size)
        {
          if (log)
            fprintf (log, "  buffer too small for Connection Keep-Alive insertion: need %zu, have %zu -> returning false\n", initial_pos + el_size, buf_size);
          result = false;
          goto cleanup;
        }
        if (log)
          fprintf (log, "  inserting 'Keep-Alive, ' into Connection header at pos %zu\n", *ppos);
        memcpy (buf + *ppos, "Keep-Alive, ",
                MHD_STATICSTR_LEN_ ("Keep-Alive, "));
        *ppos += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
      }
      if (log)
        fprintf (log, "  clearing add_close/add_keep_alive flags (was add_close=%d, add_keep_alive=%d)\n", add_close, add_keep_alive);
      add_close = false;
      add_keep_alive = false;
    }
    if (0 != hdr->value_size)
      memcpy (buf + *ppos, hdr->value, hdr->value_size);
    *ppos += hdr->value_size;
    buf[(*ppos)++] = '\r';
    buf[(*ppos)++] = '\n';
    mhd_assert (initial_pos + el_size == (*ppos));
  }

  result = true;

cleanup:
  if (log)
  {
    fprintf (log, "add_user_headers: exiting with result=%d, final *ppos=%zu\n", result, *ppos);
    fclose (log);
  }
  return result;
}
