static bool
add_user_headers (char *buf,
                  size_t *ppos,
                  size_t buf_size,
                  struct MHD_Response *response,
                  bool filter_transf_enc,
                  bool filter_content_len,
                  bool add_close,
                  bool add_keep_alive)
{
  struct MHD_Response *const r = response; /**< a short alias */
  struct MHD_HTTP_Res_Header *hdr; /**< Iterates through User-specified headers */
  size_t el_size; /**< the size of current element to be added to the @a buf */

  /* Lightweight entry probe: log parameters and some response flags */
  {
    FILE *logf = fopen("/tmp/logs/4382.txt", "a");
    if (logf)
    {
      fprintf(logf, "enter add_user_headers: buf=%p ppos=%p (*ppos)=%zu buf_size=%zu response=%p filter_transf_enc=%d filter_content_len=%d add_close=%d add_keep_alive=%d r->flags_auto=0x%08x r->flags=0x%08x\n",
              (void*)buf, (void*)ppos, (ppos ? *ppos : (size_t)0), buf_size,
              (void*)response, (int)filter_transf_enc, (int)filter_content_len,
              (int)add_close, (int)add_keep_alive,
              (unsigned) (response ? response->flags_auto : 0U),
              (unsigned) (response ? response->flags : 0U));
      fclose(logf);
    }
  }

  mhd_assert (! add_close || ! add_keep_alive);

  if (0 == (r->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED))
  {
    /* Log the fact we're about to modify the parameter filter_transf_enc */
    FILE *logf = fopen("/tmp/logs/4382.txt", "a");
    if (logf)
    {
      fprintf(logf, "branch: no TRANS_ENC_CHUNKED in flags_auto (flags_auto=0x%08x) -> setting filter_transf_enc from %d to 0\n",
              (unsigned) r->flags_auto, (int) filter_transf_enc);
      fclose(logf);
    }
    filter_transf_enc = false;   /* No such header */
  }
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH))
  {
    FILE *logf = fopen("/tmp/logs/4382.txt", "a");
    if (logf)
    {
      fprintf(logf, "branch: no CONTENT_LENGTH in flags_auto (flags_auto=0x%08x) -> setting filter_content_len from %d to 0\n",
              (unsigned) r->flags_auto, (int) filter_content_len);
      fclose(logf);
    }
    filter_content_len = false;  /* No such header */
  }
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    FILE *logf = fopen("/tmp/logs/4382.txt", "a");
    if (logf)
    {
      fprintf(logf, "branch: no CONNECTION_HDR in flags_auto (flags_auto=0x%08x) -> setting add_close from %d to 0 and add_keep_alive from %d to 0\n",
              (unsigned) r->flags_auto, (int) add_close, (int) add_keep_alive);
      fclose(logf);
    }
    add_close = false;          /* No such header */
    add_keep_alive = false;     /* No such header */
  }
  else if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE))
  {
    FILE *logf = fopen("/tmp/logs/4382.txt", "a");
    if (logf)
    {
      fprintf(logf, "branch: CONNECTION_CLOSE already set in flags_auto (flags_auto=0x%08x) -> setting add_close from %d to 0\n",
              (unsigned) r->flags_auto, (int) add_close);
      fclose(logf);
    }
    add_close = false;          /* "close" token was already set */
  }

  for (hdr = r->first_header; NULL != hdr; hdr = hdr->next)
  {
    size_t initial_pos = *ppos;
    if (MHD_HEADER_KIND != hdr->kind)
      continue;
    if (filter_transf_enc)
    { /* Need to filter-out "Transfer-Encoding" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_TRANSFER_ENCODING) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_TRANSFER_ENCODING,
                                          hdr->header, hdr->header_size)) )
      {
        /* Log the header match and the parameter modification */
        FILE *logf = fopen("/tmp/logs/4382.txt", "a");
        if (logf)
        {
          fprintf(logf, "filter_transf_enc: found Transfer-Encoding header (hdr=%p header_size=%zu) -> setting filter_transf_enc from %d to 0 and skipping header\n",
                  (void*)hdr, hdr->header_size, (int) filter_transf_enc);
          fclose(logf);
        }
        filter_transf_enc = false; /* There is the only one such header */
        continue; /* Skip "Transfer-Encoding" header */
      }
    }
    if (filter_content_len)
    { /* Need to filter-out "Content-Length" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONTENT_LENGTH) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_CONTENT_LENGTH,
                                          hdr->header, hdr->header_size)) )
      {
        /* Log the content-length handling and the (surprising) modification of filter_transf_enc */
        FILE *logf = fopen("/tmp/logs/4382.txt", "a");
        if (logf)
        {
          fprintf(logf, "filter_content_len: found Content-Length header (hdr=%p header_size=%zu) -> before change: filter_transf_enc=%d; r->flags=0x%08x; INSANITY_FLAG=0x%08x\n",
                  (void*)hdr, hdr->header_size, (int) filter_transf_enc,
                  (unsigned) r->flags, (unsigned) MHD_RF_INSANITY_HEADER_CONTENT_LENGTH);
          fclose(logf);
        }

        /* Reset filter flag if only one header is allowed */
        filter_transf_enc =
          (0 == (r->flags & MHD_RF_INSANITY_HEADER_CONTENT_LENGTH));

        {
          FILE *logf2 = fopen("/tmp/logs/4382.txt", "a");
          if (logf2)
          {
            fprintf(logf2, "filter_content_len: after change: filter_transf_enc=%d (derived from r->flags & MHD_RF_INSANITY_HEADER_CONTENT_LENGTH) -> skipping header\n",
                    (int) filter_transf_enc);
            fclose(logf2);
          }
        }

        continue; /* Skip "Content-Length" header */
      }
    }

    /* Add user header */
    el_size = hdr->header_size + 2 + hdr->value_size + 2;
    if (buf_size < *ppos + el_size)
    {
      FILE *logf = fopen("/tmp/logs/4382.txt", "a");
      if (logf)
      {
        fprintf(logf, "returning false: buffer too small (buf_size=%zu *ppos=%zu el_size=%zu)\n",
                buf_size, *ppos, el_size);
        fclose(logf);
      }
      return false;
    }
    memcpy (buf + *ppos, hdr->header, hdr->header_size);
    (*ppos) += hdr->header_size;
    buf[(*ppos)++] = ':';
    buf[(*ppos)++] = ' ';
    if (add_close || add_keep_alive)
    {
      /* "Connection:" header must be always the first one */
      mhd_assert (MHD_str_equal_caseless_n_ (hdr->header, \
                                             MHD_HTTP_HEADER_CONNECTION, \
                                             hdr->header_size));

      if (add_close)
      {
        el_size += MHD_STATICSTR_LEN_ ("close, ");
        if (buf_size < initial_pos + el_size)
        {
          FILE *logf = fopen("/tmp/logs/4382.txt", "a");
          if (logf)
          {
            fprintf(logf, "returning false: buffer too small for connection close token (buf_size=%zu initial_pos=%zu el_size=%zu)\n",
                    buf_size, initial_pos, el_size);
            fclose(logf);
          }
          return false;
        }
        memcpy (buf + *ppos, "close, ",
                MHD_STATICSTR_LEN_ ("close, "));
        *ppos += MHD_STATICSTR_LEN_ ("close, ");
      }
      else
      {
        el_size += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
        if (buf_size < initial_pos + el_size)
        {
          FILE *logf = fopen("/tmp/logs/4382.txt", "a");
          if (logf)
          {
            fprintf(logf, "returning false: buffer too small for Keep-Alive token (buf_size=%zu initial_pos=%zu el_size=%zu)\n",
                    buf_size, initial_pos, el_size);
            fclose(logf);
          }
          return false;
        }
        memcpy (buf + *ppos, "Keep-Alive, ",
                MHD_STATICSTR_LEN_ ("Keep-Alive, "));
        *ppos += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
      }
      add_close = false;
      add_keep_alive = false;
    }
    if (0 != hdr->value_size)
      memcpy (buf + *ppos, hdr->value, hdr->value_size);
    *ppos += hdr->value_size;
    buf[(*ppos)++] = '\r';
    buf[(*ppos)++] = '\n';
    mhd_assert (initial_pos + el_size == (*ppos));
  }

  {
    FILE *logf = fopen("/tmp/logs/4382.txt", "a");
    if (logf)
    {
      fprintf(logf, "exit add_user_headers: returning true final *ppos=%zu\n", *ppos);
      fclose(logf);
    }
  }
  return true;
}
