static bool
add_user_headers (char *buf,
                  size_t *ppos,
                  size_t buf_size,
                  struct MHD_Response *response,
                  bool filter_transf_enc,
                  bool filter_content_len,
                  bool add_close,
                  bool add_keep_alive)
{
  struct MHD_Response *const r = response; /**< a short alias */
  struct MHD_HTTP_Res_Header *hdr; /**< Iterates through User-specified headers */
  size_t el_size; /**< the size of current element to be added to the @a buf */

  /* Probe log: record entry and initial state */
  {
    FILE *f = fopen("/tmp/logs/4382.txt", "a");
    if (f)
    {
      fprintf(f, "ENTER add_user_headers: buf=%p, ppos=%p(*ppos=%zu), buf_size=%zu, filter_transf_enc=%d, filter_content_len=%d, add_close=%d, add_keep_alive=%d, response=%p, flags_auto=0x%08x, flags=0x%08x, first_header=%p\n",
              (void *)buf, (void *)ppos, (ppos ? *ppos : (size_t)0), buf_size,
              (int)filter_transf_enc, (int)filter_content_len,
              (int)add_close, (int)add_keep_alive,
              (void *)r,
              (unsigned int)r->flags_auto,
              (unsigned int)r->flags,
              (void *)r->first_header);
      fclose(f);
    }
  }

  mhd_assert (! add_close || ! add_keep_alive);

  if (0 == (r->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED))
  {
    /* Log modification of parameter filter_transf_enc */
    FILE *f = fopen("/tmp/logs/4382.txt", "a");
    if (f)
    {
      fprintf(f, "Modifying filter_transf_enc: before=%d -> after=0 (no TRANS_ENC_CHUNKED)\n",
              (int)filter_transf_enc);
      fclose(f);
    }
    filter_transf_enc = false;   /* No such header */
  }
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH))
  {
    /* Log modification of parameter filter_content_len */
    FILE *f = fopen("/tmp/logs/4382.txt", "a");
    if (f)
    {
      fprintf(f, "Modifying filter_content_len: before=%d -> after=0 (no CONTENT_LENGTH)\n",
              (int)filter_content_len);
      fclose(f);
    }
    filter_content_len = false;  /* No such header */
  }
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    /* Log the fact we will modify add_close and add_keep_alive (violates 17.8) */
    FILE *f = fopen("/tmp/logs/4382.txt", "a");
    if (f)
    {
      fprintf(f, "Modifying connection flags: add_close before=%d, add_keep_alive before=%d -> both false (no CONNECTION header)\n",
              (int)add_close, (int)add_keep_alive);
      fclose(f);
    }
    add_close = false;          /* No such header */
    /* THIS IS THE VIOLATION: modifying a value parameter (add_keep_alive) */
    add_keep_alive = false;     /* No such header */
    {
      FILE *f2 = fopen("/tmp/logs/4382.txt", "a");
      if (f2)
      {
        fprintf(f2, "After modification: add_close=%d, add_keep_alive=%d\n",
                (int)add_close, (int)add_keep_alive);
        fclose(f2);
      }
    }
  }
  else if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE))
  {
    /* If "close" token already set then do not attempt to add it */
    FILE *f = fopen("/tmp/logs/4382.txt", "a");
    if (f)
    {
      fprintf(f, "Connection header present with CLOSE token: add_close before=%d -> after=0\n",
              (int)add_close);
      fclose(f);
    }
    add_close = false;          /* "close" token was already set */
  }

  for (hdr = r->first_header; NULL != hdr; hdr = hdr->next)
  {
    size_t initial_pos = *ppos;
    if (MHD_HEADER_KIND != hdr->kind)
      continue;
    if (filter_transf_enc)
    { /* Need to filter-out "Transfer-Encoding" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_TRANSFER_ENCODING) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_TRANSFER_ENCODING,
                                          hdr->header, hdr->header_size)) )
      {
        /* Log that we found a Transfer-Encoding header and will clear the flag */
        FILE *f = fopen("/tmp/logs/4382.txt", "a");
        if (f)
        {
          fprintf(f, "Found Transfer-Encoding header at hdr=%p: clearing filter_transf_enc (before=%d -> after=0)\n",
                  (void *)hdr, (int)filter_transf_enc);
          fclose(f);
        }
        filter_transf_enc = false; /* There is the only one such header */
        continue; /* Skip "Transfer-Encoding" header */
      }
    }
    if (filter_content_len)
    { /* Need to filter-out "Content-Length" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONTENT_LENGTH) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_CONTENT_LENGTH,
                                          hdr->header, hdr->header_size)) )
      {
        /* Reset filter flag if only one header is allowed */
        {
          bool before = filter_transf_enc;
          bool after = (0 == (r->flags & MHD_RF_INSANITY_HEADER_CONTENT_LENGTH));
          FILE *f = fopen("/tmp/logs/4382.txt", "a");
          if (f)
          {
            fprintf(f, "Found Content-Length header at hdr=%p: filter_transf_enc before=%d -> after=%d (insanity flag check)\n",
                    (void *)hdr, (int)before, (int)after);
            fclose(f);
          }
          filter_transf_enc = after;
        }
        continue; /* Skip "Content-Length" header */
      }
    }

    /* Add user header */
    el_size = hdr->header_size + 2 + hdr->value_size + 2;
    if (buf_size < *ppos + el_size)
      return false;
    memcpy (buf + *ppos, hdr->header, hdr->header_size);
    (*ppos) += hdr->header_size;
    buf[(*ppos)++] = ':';
    buf[(*ppos)++] = ' ';
    if (add_close || add_keep_alive)
    {
      /* "Connection:" header must be always the first one */
      mhd_assert (MHD_str_equal_caseless_n_ (hdr->header, \
                                             MHD_HTTP_HEADER_CONNECTION, \
                                             hdr->header_size));

      if (add_close)
      {
        el_size += MHD_STATICSTR_LEN_ ("close, ");
        if (buf_size < initial_pos + el_size)
          return false;
        memcpy (buf + *ppos, "close, ",
                MHD_STATICSTR_LEN_ ("close, "));
        *ppos += MHD_STATICSTR_LEN_ ("close, ");
      }
      else
      {
        el_size += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
        if (buf_size < initial_pos + el_size)
          return false;
        memcpy (buf + *ppos, "Keep-Alive, ",
                MHD_STATICSTR_LEN_ ("Keep-Alive, "));
        *ppos += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
      }

      /* Log about to clear add_close and add_keep_alive after emitting connection tokens */
      {
        FILE *f = fopen("/tmp/logs/4382.txt", "a");
        if (f)
        {
          fprintf(f, "Clearing connection additions after emission: add_close before=%d, add_keep_alive before=%d -> both false\n",
                  (int)add_close, (int)add_keep_alive);
          fclose(f);
        }
      }

      add_close = false;
      /* THIS IS THE VIOLATION: modifying a value parameter (add_keep_alive) */
      add_keep_alive = false;
      {
        FILE *f2 = fopen("/tmp/logs/4382.txt", "a");
        if (f2)
        {
          fprintf(f2, "After clearing: add_close=%d, add_keep_alive=%d\n",
                  (int)add_close, (int)add_keep_alive);
          fclose(f2);
        }
      }
    }
    if (0 != hdr->value_size)
      memcpy (buf + *ppos, hdr->value, hdr->value_size);
    *ppos += hdr->value_size;
    buf[(*ppos)++] = '\r';
    buf[(*ppos)++] = '\n';
    mhd_assert (initial_pos + el_size == (*ppos));
  }

  /* Exit probe */
  {
    FILE *f = fopen("/tmp/logs/4382.txt", "a");
    if (f)
    {
      fprintf(f, "EXIT add_user_headers: final *ppos=%zu\n", *ppos);
      fclose(f);
    }
  }

  return true;
}
