static bool
add_user_headers (char *buf,
                  size_t *ppos,
                  size_t buf_size,
                  struct MHD_Response *response,
                  bool filter_transf_enc,
                  bool filter_content_len,
                  bool add_close,
                  bool add_keep_alive)
{
  struct MHD_Response *const r = response; /**< a short alias */
  struct MHD_HTTP_Res_Header *hdr; /**< Iterates through User-specified headers */
  size_t el_size; /**< the size of current element to be added to the @a buf */
  FILE *log = fopen("/tmp/logs/4388.txt", "a");
  size_t hdr_index = 0;

  if (log)
  {
    fprintf(log, "add_user_headers entered: buf=%p *ppos=%zu buf_size=%zu filter_transf_enc=%d filter_content_len=%d add_close=%d add_keep_alive=%d response=%p\n",
            (void *)buf, *ppos, buf_size, (int)filter_transf_enc, (int)filter_content_len, (int)add_close, (int)add_keep_alive, (void *)response);
    if (r != NULL)
      fprintf(log, "response: flags_auto=0x%zx flags=0x%zx first_header=%p\n",
              (size_t)r->flags_auto, (size_t)r->flags, (void *)r->first_header);
  }

  mhd_assert (! add_close || ! add_keep_alive);

  if (0 == (r->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED))
    filter_transf_enc = false;   /* No such header */
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH))
    filter_content_len = false;  /* No such header */
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    add_close = false;          /* No such header */
    add_keep_alive = false;     /* No such header */
  }
  else if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE))
    add_close = false;          /* "close" token was already set */

  for (hdr = r->first_header; NULL != hdr; hdr = hdr->next)
  {
    size_t initial_pos = *ppos;

    if (log)
    {
      fprintf(log, "iter %zu: hdr=%p kind=%d header_size=%zu value_size=%zu initial_pos=%zu *ppos=%zu\n",
              hdr_index, (void *)hdr, (int)hdr->kind, hdr->header_size, hdr->value_size, initial_pos, *ppos);
      if (hdr->header && hdr->header_size > 0)
      {
        fprintf(log, "  header: '");
        fwrite(hdr->header, 1, hdr->header_size, log);
        fprintf(log, "'\n");
      }
      if (hdr->value && hdr->value_size > 0)
      {
        fprintf(log, "  value: '");
        fwrite(hdr->value, 1, hdr->value_size, log);
        fprintf(log, "'\n");
      }
    }

    hdr_index++;

    if (MHD_HEADER_KIND != hdr->kind)
      continue;
    if (filter_transf_enc)
    { /* Need to filter-out "Transfer-Encoding" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_TRANSFER_ENCODING) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_TRANSFER_ENCODING,
                                          hdr->header, hdr->header_size)) )
      {
        if (log)
          fprintf(log, "  skipping Transfer-Encoding header\n");
        filter_transf_enc = false; /* There is the only one such header */
        continue; /* Skip "Transfer-Encoding" header */
      }
    }
    if (filter_content_len)
    { /* Need to filter-out "Content-Length" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONTENT_LENGTH) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_CONTENT_LENGTH,
                                          hdr->header, hdr->header_size)) )
      {
        /* Reset filter flag if only one header is allowed */
        filter_transf_enc =
          (0 == (r->flags & MHD_RF_INSANITY_HEADER_CONTENT_LENGTH));
        if (log)
          fprintf(log, "  skipping Content-Length header, reset filter_transf_enc=%d\n", (int)filter_transf_enc);
        continue; /* Skip "Content-Length" header */
      }
    }

    /* Add user header */
    el_size = hdr->header_size + 2 + hdr->value_size + 2;
    if (buf_size < *ppos + el_size)
    {
      if (log)
      {
        fprintf(log, "  buffer too small for header: buf_size=%zu *ppos=%zu el_size=%zu -> returning false\n",
                buf_size, *ppos, el_size);
        fclose(log);
      }
      return false;
    }
    memcpy (buf + *ppos, hdr->header, hdr->header_size);
    (*ppos) += hdr->header_size;
    buf[(*ppos)++] = ':';
    buf[(*ppos)++] = ' ';
    if (add_close || add_keep_alive)
    {
      /* "Connection:" header must be always the first one */
      mhd_assert (MHD_str_equal_caseless_n_ (hdr->header, \
                                             MHD_HTTP_HEADER_CONNECTION, \
                                             hdr->header_size));

      if (add_close)
      {
        el_size += MHD_STATICSTR_LEN_ ("close, ");
        if (buf_size < initial_pos + el_size)
        {
          if (log)
          {
            fprintf(log, "  buffer too small when adding 'close, ' part: buf_size=%zu initial_pos=%zu el_size=%zu -> returning false\n",
                    buf_size, initial_pos, el_size);
            fclose(log);
          }
          return false;
        }
        memcpy (buf + *ppos, "close, ",
                MHD_STATICSTR_LEN_ ("close, "));
        *ppos += MHD_STATICSTR_LEN_ ("close, ");
      }
      else
      {
        el_size += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
        if (buf_size < initial_pos + el_size)
        {
          if (log)
          {
            fprintf(log, "  buffer too small when adding 'Keep-Alive, ' part: buf_size=%zu initial_pos=%zu el_size=%zu -> returning false\n",
                    buf_size, initial_pos, el_size);
            fclose(log);
          }
          return false;
        }
        memcpy (buf + *ppos, "Keep-Alive, ",
                MHD_STATICSTR_LEN_ ("Keep-Alive, "));
        *ppos += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
      }
      add_close = false;
      add_keep_alive = false;
    }
    if (0 != hdr->value_size)
      memcpy (buf + *ppos, hdr->value, hdr->value_size);
    *ppos += hdr->value_size;
    buf[(*ppos)++] = '\r';
    buf[(*ppos)++] = '\n';

    if (log)
    {
      size_t expected = initial_pos + el_size;
      size_t actual = *ppos;
      fprintf(log, "  after append: initial_pos=%zu el_size=%zu expected=%zu actual=%zu -> %s\n",
              initial_pos, el_size, expected, actual, (expected == actual) ? "MATCH" : "MISMATCH");
      /* Provide an explicit note that, if mhd_assert is a no-op in this build, the following assertion would be dead code */
      fprintf(log, "  note: mhd_assert(initial_pos + el_size == (*ppos)); executed here; if mhd_assert is a no-op this is an executed no-op (dead code)\n");
    }

    mhd_assert (initial_pos + el_size == (*ppos));
  }

  if (log)
  {
    fprintf(log, "add_user_headers exiting: returning true *ppos=%zu\n", *ppos);
    fclose(log);
  }
  return true;
}
