static bool
add_user_headers (char *buf,
                  size_t *ppos,
                  size_t buf_size,
                  struct MHD_Response *response,
                  bool filter_transf_enc,
                  bool filter_content_len,
                  bool add_close,
                  bool add_keep_alive)
{
  struct MHD_Response *const r = response; /**< a short alias */
  struct MHD_HTTP_Res_Header *hdr; /**< Iterates through User-specified headers */
  size_t el_size; /**< the size of current element to be added to the @a buf */

  /* Minimal entry log to prove function was entered and show initial state. */
  {
    FILE *logf = fopen("/tmp/logs/4383.txt", "a");
    if (logf)
    {
      fprintf(logf, "ENTER add_user_headers: buf=%p, ppos=%p (*ppos=%llu), buf_size=%llu, response=%p, filter_transf_enc=%d, filter_content_len=%d, add_close=%d, add_keep_alive=%d\n",
              (void *)buf,
              (void *)ppos,
              (unsigned long long)(ppos ? *ppos : 0ULL),
              (unsigned long long)buf_size,
              (void *)response,
              (int)filter_transf_enc,
              (int)filter_content_len,
              (int)add_close,
              (int)add_keep_alive);
      fflush(logf);
      fclose(logf);
    }
  }

  mhd_assert (! add_close || ! add_keep_alive);

  if (0 == (r->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED))
    filter_transf_enc = false;   /* No such header */
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH))
    filter_content_len = false;  /* No such header */
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    add_close = false;          /* No such header */
    add_keep_alive = false;     /* No such header */
  }
  else if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE))
    add_close = false;          /* "close" token was already set */

  for (hdr = r->first_header; NULL != hdr; hdr = hdr->next)
  {
    size_t initial_pos = *ppos;

    /* Log header iteration state */
    {
      FILE *logf = fopen("/tmp/logs/4383.txt", "a");
      if (logf)
      {
        fprintf(logf, "ITER hdr=%p: kind=%d, header_size=%llu, value_size=%llu, initial_pos=%llu, buf_size=%llu\n",
                (void *)hdr,
                (int)hdr->kind,
                (unsigned long long)hdr->header_size,
                (unsigned long long)hdr->value_size,
                (unsigned long long)initial_pos,
                (unsigned long long)buf_size);
        fflush(logf);
        fclose(logf);
      }
    }

    if (MHD_HEADER_KIND != hdr->kind)
      continue;
    if (filter_transf_enc)
    { /* Need to filter-out "Transfer-Encoding" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_TRANSFER_ENCODING) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_TRANSFER_ENCODING,
                                          hdr->header, hdr->header_size)) )
      {
        filter_transf_enc = false; /* There is the only one such header */

        /* Log filtering event */
        {
          FILE *logf = fopen("/tmp/logs/4383.txt", "a");
          if (logf)
          {
            fprintf(logf, "SKIP Transfer-Encoding hdr=%p header_size=%llu\n",
                    (void *)hdr,
                    (unsigned long long)hdr->header_size);
            fflush(logf);
            fclose(logf);
          }
        }

        continue; /* Skip "Transfer-Encoding" header */
      }
    }
    if (filter_content_len)
    { /* Need to filter-out "Content-Length" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONTENT_LENGTH) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_CONTENT_LENGTH,
                                          hdr->header, hdr->header_size)) )
      {
        /* Reset filter flag if only one header is allowed */
        filter_transf_enc =
          (0 == (r->flags & MHD_RF_INSANITY_HEADER_CONTENT_LENGTH));

        /* Log filtering event */
        {
          FILE *logf = fopen("/tmp/logs/4383.txt", "a");
          if (logf)
          {
            fprintf(logf, "SKIP Content-Length hdr=%p header_size=%llu\n",
                    (void *)hdr,
                    (unsigned long long)hdr->header_size);
            fflush(logf);
            fclose(logf);
          }
        }

        continue; /* Skip "Content-Length" header */
      }
    }

    /* Add user header */
    el_size = hdr->header_size + 2 + hdr->value_size + 2;

    /* Compute both size_t sum (may wrap) and wide sum to observe overflow */
    size_t sum_st = *ppos + el_size; /* this is the same expression used in original check */
    unsigned long long sum_ull = (unsigned long long)(*ppos) + (unsigned long long)el_size;

    /* Log computed sizes and upcoming bounds check */
    {
      FILE *logf = fopen("/tmp/logs/4383.txt", "a");
      if (logf)
      {
        fprintf(logf, "CHECK1: el_size=%llu, *ppos=%llu, sum_st=%llu (wrap?), sum_ull=%llu, buf_size=%llu => condition buf_size < (*ppos + el_size) is %s\n",
                (unsigned long long)el_size,
                (unsigned long long)(*ppos),
                (unsigned long long)sum_st,
                sum_ull,
                (unsigned long long)buf_size,
                (buf_size < sum_st) ? "true" : "false");
        fflush(logf);
        fclose(logf);
      }
    }

    if (buf_size < sum_st)
    {
      FILE *logf = fopen("/tmp/logs/4383.txt", "a");
      if (logf)
      {
        fprintf(logf, "RETURN false at CHECK1: buf_size=%llu, *ppos=%llu, el_size=%llu, sum_st=%llu, sum_ull=%llu\n",
                (unsigned long long)buf_size,
                (unsigned long long)(*ppos),
                (unsigned long long)el_size,
                (unsigned long long)sum_st,
                (unsigned long long)sum_ull);
        fflush(logf);
        fclose(logf);
      }
      return false;
    }
    memcpy (buf + *ppos, hdr->header, hdr->header_size);
    {
      FILE *logf = fopen("/tmp/logs/4383.txt", "a");
      if (logf)
      {
        fprintf(logf, "MEMCPY header: dst=%p, src=%p, n=%llu\n",
                (void *)(buf + *ppos), (void *)hdr->header,
                (unsigned long long)hdr->header_size);
        fflush(logf);
        fclose(logf);
      }
    }
    (*ppos) += hdr->header_size;
    buf[(*ppos)++] = ':';
    buf[(*ppos)++] = ' ';
    if (add_close || add_keep_alive)
    {
      /* "Connection:" header must be always the first one */
      mhd_assert (MHD_str_equal_caseless_n_ (hdr->header, \
                                             MHD_HTTP_HEADER_CONNECTION, \
                                             hdr->header_size));

      if (add_close)
      {
        el_size += MHD_STATICSTR_LEN_ ("close, ");

        /* Recompute sums using initial_pos as original code expects */
        size_t sum2_st = initial_pos + el_size;
        unsigned long long sum2_ull = (unsigned long long)initial_pos + (unsigned long long)el_size;

        {
          FILE *logf = fopen("/tmp/logs/4383.txt", "a");
          if (logf)
          {
            fprintf(logf, "CHECK2 (add_close): el_size=%llu, initial_pos=%llu, sum2_st=%llu, sum2_ull=%llu, buf_size=%llu => condition buf_size < (initial_pos + el_size) is %s\n",
                    (unsigned long long)el_size,
                    (unsigned long long)initial_pos,
                    (unsigned long long)sum2_st,
                    sum2_ull,
                    (unsigned long long)buf_size,
                    (buf_size < sum2_st) ? "true" : "false");
            fflush(logf);
            fclose(logf);
          }
        }

        if (buf_size < sum2_st)
        {
          FILE *logf = fopen("/tmp/logs/4383.txt", "a");
          if (logf)
          {
            fprintf(logf, "RETURN false at CHECK2 (add_close): buf_size=%llu, initial_pos=%llu, el_size=%llu, sum2_st=%llu, sum2_ull=%llu\n",
                    (unsigned long long)buf_size,
                    (unsigned long long)initial_pos,
                    (unsigned long long)el_size,
                    (unsigned long long)sum2_st,
                    (unsigned long long)sum2_ull);
            fflush(logf);
            fclose(logf);
          }
          return false;
        }
        memcpy (buf + *ppos, "close, ",
                MHD_STATICSTR_LEN_ ("close, "));
        {
          FILE *logf = fopen("/tmp/logs/4383.txt", "a");
          if (logf)
          {
            fprintf(logf, "MEMCPY add_close: dst=%p, src=%s, n=%llu\n",
                    (void *)(buf + *ppos), "close, ",
                    (unsigned long long)MHD_STATICSTR_LEN_("close, "));
            fflush(logf);
            fclose(logf);
          }
        }
        *ppos += MHD_STATICSTR_LEN_ ("close, ");
      }
      else
      {
        el_size += MHD_STATICSTR_LEN_ ("Keep-Alive, ");

        size_t sum2_st = initial_pos + el_size;
        unsigned long long sum2_ull = (unsigned long long)initial_pos + (unsigned long long)el_size;

        {
          FILE *logf = fopen("/tmp/logs/4383.txt", "a");
          if (logf)
          {
            fprintf(logf, "CHECK2 (add_keep_alive): el_size=%llu, initial_pos=%llu, sum2_st=%llu, sum2_ull=%llu, buf_size=%llu => condition buf_size < (initial_pos + el_size) is %s\n",
                    (unsigned long long)el_size,
                    (unsigned long long)initial_pos,
                    (unsigned long long)sum2_st,
                    sum2_ull,
                    (unsigned long long)buf_size,
                    (buf_size < sum2_st) ? "true" : "false");
            fflush(logf);
            fclose(logf);
          }
        }

        if (buf_size < sum2_st)
        {
          FILE *logf = fopen("/tmp/logs/4383.txt", "a");
          if (logf)
          {
            fprintf(logf, "RETURN false at CHECK2 (add_keep_alive): buf_size=%llu, initial_pos=%llu, el_size=%llu, sum2_st=%llu, sum2_ull=%llu\n",
                    (unsigned long long)buf_size,
                    (unsigned long long)initial_pos,
                    (unsigned long long)el_size,
                    (unsigned long long)sum2_st,
                    (unsigned long long)sum2_ull);
            fflush(logf);
            fclose(logf);
          }
          return false;
        }
        memcpy (buf + *ppos, "Keep-Alive, ",
                MHD_STATICSTR_LEN_ ("Keep-Alive, "));
        {
          FILE *logf = fopen("/tmp/logs/4383.txt", "a");
          if (logf)
          {
            fprintf(logf, "MEMCPY add_keep_alive: dst=%p, src=%s, n=%llu\n",
                    (void *)(buf + *ppos), "Keep-Alive, ",
                    (unsigned long long)MHD_STATICSTR_LEN_("Keep-Alive, "));
            fflush(logf);
            fclose(logf);
          }
        }
        *ppos += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
      }
      add_close = false;
      add_keep_alive = false;
    }
    if (0 != hdr->value_size)
    {
      memcpy (buf + *ppos, hdr->value, hdr->value_size);
      {
        FILE *logf = fopen("/tmp/logs/4383.txt", "a");
        if (logf)
        {
          fprintf(logf, "MEMCPY value: dst=%p, src=%p, n=%llu\n",
                  (void *)(buf + *ppos), (void *)hdr->value,
                  (unsigned long long)hdr->value_size);
          fflush(logf);
          fclose(logf);
        }
      }
    }
    *ppos += hdr->value_size;
    buf[(*ppos)++] = '\r';
    buf[(*ppos)++] = '\n';

    /* Log final position and assert check */
    {
      FILE *logf = fopen("/tmp/logs/4383.txt", "a");
      if (logf)
      {
        fprintf(logf, "AFTER HDR: initial_pos=%llu, el_size=%llu, final_ppos=%llu, expected_final=%llu, assert_ok=%s\n",
                (unsigned long long)initial_pos,
                (unsigned long long)el_size,
                (unsigned long long)(*ppos),
                (unsigned long long)(initial_pos + el_size),
                (initial_pos + el_size == (*ppos)) ? "true" : "false");
        fflush(logf);
        fclose(logf);
      }
    }
    mhd_assert (initial_pos + el_size == (*ppos));
  }

  /* Exit log */
  {
    FILE *logf = fopen("/tmp/logs/4383.txt", "a");
    if (logf)
    {
      fprintf(logf, "EXIT add_user_headers: returning true, final *ppos=%llu\n",
              (unsigned long long)(ppos ? *ppos : 0ULL));
      fflush(logf);
      fclose(logf);
    }
  }

  return true;
}
