static bool
add_user_headers (char *buf,
                  size_t *ppos,
                  size_t buf_size,
                  struct MHD_Response *response,
                  bool filter_transf_enc,
                  bool filter_content_len,
                  bool add_close,
                  bool add_keep_alive)
{
  struct MHD_Response *const r = response; /**< a short alias */
  struct MHD_HTTP_Res_Header *hdr; /**< Iterates through User-specified headers */
  size_t el_size; /**< the size of current element to be added to the @a buf */
  FILE *log = fopen("/tmp/logs/4383.txt", "a");

  /* Minimal entry log so that the function reachability is recorded. */
  if (log)
  {
    fprintf (log, "ENTER add_user_headers: buf=%p, ppos=%p(*ppos=%llu), buf_size=%llu, response=%p, filter_transf_enc=%d, filter_content_len=%d, add_close=%d, add_keep_alive=%d\n",
             (void *)buf, (void *)ppos, (unsigned long long)(*ppos), (unsigned long long)buf_size,
             (void *)response, (int)filter_transf_enc, (int)filter_content_len,
             (int)add_close, (int)add_keep_alive);
    if (r)
      fprintf (log, " response->flags_auto=0x%llx, response->flags=0x%llx, first_header=%p\n",
               (unsigned long long)r->flags_auto, (unsigned long long)r->flags,
               (void *)r->first_header);
    fflush (log);
  }

  mhd_assert (! add_close || ! add_keep_alive);

  if (0 == (r->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED))
    filter_transf_enc = false;   /* No such header */
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH))
    filter_content_len = false;  /* No such header */
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    add_close = false;          /* No such header */
    add_keep_alive = false;     /* No such header */
  }
  else if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE))
    add_close = false;          /* "close" token was already set */

  for (hdr = r->first_header; NULL != hdr; hdr = hdr->next)
  {
    size_t initial_pos = *ppos;
    if (log)
    {
      fprintf (log, "LOOP hdr=%p: kind=%d, header_size=%llu, value_size=%llu, initial_pos=%llu\n",
               (void *)hdr, (int)hdr->kind, (unsigned long long)hdr->header_size,
               (unsigned long long)hdr->value_size, (unsigned long long)initial_pos);
      if (hdr->header && hdr->header_size > 0)
        fprintf (log, " header(leading)='%.*s'\n", (int)hdr->header_size, hdr->header);
      if (hdr->value && hdr->value_size > 0)
        fprintf (log, " value(leading)='%.*s'\n", (int)hdr->value_size, hdr->value);
      fflush (log);
    }

    if (MHD_HEADER_KIND != hdr->kind)
      continue;
    if (filter_transf_enc)
    { /* Need to filter-out "Transfer-Encoding" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_TRANSFER_ENCODING) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_TRANSFER_ENCODING,
                                          hdr->header, hdr->header_size)) )
      {
        filter_transf_enc = false; /* There is the only one such header */
        if (log)
        {
          fprintf (log, " SKIP Transfer-Encoding header at hdr=%p\n", (void *)hdr);
          fflush (log);
        }
        continue; /* Skip "Transfer-Encoding" header */
      }
    }
    if (filter_content_len)
    { /* Need to filter-out "Content-Length" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONTENT_LENGTH) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_CONTENT_LENGTH,
                                          hdr->header, hdr->header_size)) )
      {
        /* Reset filter flag if only one header is allowed */
        filter_transf_enc =
          (0 == (r->flags & MHD_RF_INSANITY_HEADER_CONTENT_LENGTH));
        if (log)
        {
          fprintf (log, " SKIP Content-Length header at hdr=%p, new filter_transf_enc=%d\n",
                   (void *)hdr, (int)filter_transf_enc);
          fflush (log);
        }
        continue; /* Skip "Content-Length" header */
      }
    }

    /* Add user header */
    el_size = hdr->header_size + 2 + hdr->value_size + 2;
    if (log)
    {
      unsigned long long el_sz_ll = (unsigned long long) el_size;
      unsigned long long ppos_ll = (unsigned long long) *ppos;
      unsigned long long sum = (unsigned long long) (*ppos + el_size);
      fprintf (log, " computed el_size=%llu, *ppos=%llu, *ppos+el_size=%llu\n",
               el_sz_ll, ppos_ll, sum);
      if ((size_t)sum < *ppos)
        fprintf (log, " WARNING: detected wraparound in *ppos + el_size: sum(%llu) < *ppos(%llu)\n",
                 sum, ppos_ll);
      fflush (log);
    }

    if (buf_size < *ppos + el_size)
    {
      if (log)
      {
        fprintf (log, " RETURN false: insufficient space before adding header: buf_size=%llu, *ppos=%llu, el_size=%llu\n",
                 (unsigned long long)buf_size, (unsigned long long)(*ppos), (unsigned long long)el_size);
        fflush (log);
        fclose (log);
      }
      return false;
    }
    if (log)
      fprintf (log, " memcpy header -> dest=%p, src=%p, n=%llu\n",
               (void *)(buf + *ppos), (void *)hdr->header, (unsigned long long)hdr->header_size);
    memcpy (buf + *ppos, hdr->header, hdr->header_size);
    (*ppos) += hdr->header_size;
    buf[(*ppos)++] = ':';
    buf[(*ppos)++] = ' ';
    if (add_close || add_keep_alive)
    {
      /* "Connection:" header must be always the first one */
      mhd_assert (MHD_str_equal_caseless_n_ (hdr->header, \
                                             MHD_HTTP_HEADER_CONNECTION, \
                                             hdr->header_size));

      if (add_close)
      {
        el_size += MHD_STATICSTR_LEN_ ("close, ");
        if (log)
        {
          unsigned long long sum2 = (unsigned long long) (initial_pos + el_size);
          fprintf (log, " add_close path: initial_pos=%llu, new el_size=%llu, initial_pos+el_size=%llu\n",
                   (unsigned long long)initial_pos, (unsigned long long)el_size, sum2);
          if ((size_t)sum2 < initial_pos)
            fprintf (log, " WARNING: detected wraparound in initial_pos + el_size: sum2(%llu) < initial_pos(%llu)\n",
                     sum2, (unsigned long long)initial_pos);
          fflush (log);
        }
        if (buf_size < initial_pos + el_size)
        {
          if (log)
          {
            fprintf (log, " RETURN false: insufficient space for add_close: buf_size=%llu, initial_pos=%llu, el_size=%llu\n",
                     (unsigned long long)buf_size, (unsigned long long)initial_pos, (unsigned long long)el_size);
            fflush (log);
            fclose (log);
          }
          return false;
        }
        if (log)
          fprintf (log, " memcpy close token -> dest=%p, src=%s, n=%llu\n",
                   (void *)(buf + *ppos), "close, ", (unsigned long long)MHD_STATICSTR_LEN_("close, "));
        memcpy (buf + *ppos, "close, ",
                MHD_STATICSTR_LEN_ ("close, "));
        *ppos += MHD_STATICSTR_LEN_ ("close, ");
      }
      else
      {
        el_size += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
        if (log)
        {
          unsigned long long sum2 = (unsigned long long) (initial_pos + el_size);
          fprintf (log, " add_keep_alive path: initial_pos=%llu, new el_size=%llu, initial_pos+el_size=%llu\n",
                   (unsigned long long)initial_pos, (unsigned long long)el_size, sum2);
          if ((size_t)sum2 < initial_pos)
            fprintf (log, " WARNING: detected wraparound in initial_pos + el_size: sum2(%llu) < initial_pos(%llu)\n",
                     sum2, (unsigned long long)initial_pos);
          fflush (log);
        }
        if (buf_size < initial_pos + el_size)
        {
          if (log)
          {
            fprintf (log, " RETURN false: insufficient space for add_keep_alive: buf_size=%llu, initial_pos=%llu, el_size=%llu\n",
                     (unsigned long long)buf_size, (unsigned long long)initial_pos, (unsigned long long)el_size);
            fflush (log);
            fclose (log);
          }
          return false;
        }
        if (log)
          fprintf (log, " memcpy Keep-Alive token -> dest=%p, src=%s, n=%llu\n",
                   (void *)(buf + *ppos), "Keep-Alive, ", (unsigned long long)MHD_STATICSTR_LEN_("Keep-Alive, "));
        memcpy (buf + *ppos, "Keep-Alive, ",
                MHD_STATICSTR_LEN_ ("Keep-Alive, "));
        *ppos += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
      }
      add_close = false;
      add_keep_alive = false;
    }
    if (0 != hdr->value_size)
    {
      if (log)
        fprintf (log, " memcpy value -> dest=%p, src=%p, n=%llu\n",
                 (void *)(buf + *ppos), (void *)hdr->value, (unsigned long long)hdr->value_size);
      memcpy (buf + *ppos, hdr->value, hdr->value_size);
    }
    *ppos += hdr->value_size;
    buf[(*ppos)++] = '\r';
    buf[(*ppos)++] = '\n';
    if (log)
    {
      fprintf (log, " AFTER header: new *ppos=%llu, expected=%llu (initial_pos+el_size)\n",
               (unsigned long long)(*ppos), (unsigned long long)(initial_pos + el_size));
      fflush (log);
    }
    mhd_assert (initial_pos + el_size == (*ppos));
  }
  if (log)
  {
    fprintf (log, "EXIT add_user_headers: returning true, final *ppos=%llu\n", (unsigned long long)(*ppos));
    fflush (log);
    fclose (log);
  }
  return true;
}
