static bool
add_user_headers (char *buf,
                  size_t *ppos,
                  size_t buf_size,
                  struct MHD_Response *response,
                  bool filter_transf_enc,
                  bool filter_content_len,
                  bool add_close,
                  bool add_keep_alive)
{
  /* Logging to help reproduce the Rule 8.13 scenario. Logs appended to
     /tmp/logs/4385.txt. */
  FILE *log = fopen("/tmp/logs/4385.txt", "a");
  if (log)
  {
    fprintf(log, "add_user_headers: entry response=%p filter_transf_enc=%d filter_content_len=%d add_close=%d add_keep_alive=%d buf_size=%zu ppos=%zu\n",
            (void *)response, (int)filter_transf_enc, (int)filter_content_len,
            (int)add_close, (int)add_keep_alive, buf_size, (size_t)*ppos);
    fflush(log);
  }

  struct MHD_Response *const r = response; /**< a short alias */
  struct MHD_HTTP_Res_Header *hdr; /**< Iterates through User-specified headers */
  size_t el_size; /**< the size of current element to be added to the @a buf */
  bool result = true; /* default return value */

  mhd_assert (! add_close || ! add_keep_alive);

  if (0 == (r->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED))
    filter_transf_enc = false;   /* No such header */
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH))
    filter_content_len = false;  /* No such header */
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    add_close = false;          /* No such header */
    add_keep_alive = false;     /* No such header */
  }
  else if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE))
    add_close = false;          /* "close" token was already set */

  if (log)
  {
    fprintf(log, "add_user_headers: after flags_auto checks flags_auto=0x%llx flags=0x%llx filter_transf_enc=%d filter_content_len=%d add_close=%d add_keep_alive=%d first_header=%p\n",
            (unsigned long long)r->flags_auto, (unsigned long long)r->flags,
            (int)filter_transf_enc, (int)filter_content_len,
            (int)add_close, (int)add_keep_alive, (void *)r->first_header);
    fflush(log);
  }

  for (hdr = r->first_header; NULL != hdr; hdr = hdr->next)
  {
    size_t initial_pos = *ppos;
    if (log)
    {
      fprintf(log, "add_user_headers: iter hdr=%p kind=%d header_size=%zu value_size=%zu header=%p value=%p next=%p\n",
              (void *)hdr, (int)hdr->kind, (size_t)hdr->header_size, (size_t)hdr->value_size,
              (void *)hdr->header, (void *)hdr->value, (void *)hdr->next);
      fflush(log);
    }

    if (MHD_HEADER_KIND != hdr->kind)
    {
      if (log)
      {
        fprintf(log, "add_user_headers: skipping non-header kind (%d)\n", (int)hdr->kind);
        fflush(log);
      }
      continue;
    }
    if (filter_transf_enc)
    { /* Need to filter-out "Transfer-Encoding" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_TRANSFER_ENCODING) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_TRANSFER_ENCODING,
                                          hdr->header, hdr->header_size)) )
      {
        filter_transf_enc = false; /* There is the only one such header */
        if (log)
        {
          fprintf(log, "add_user_headers: filtered Transfer-Encoding header at hdr=%p\n", (void *)hdr);
          fflush(log);
        }
        continue; /* Skip "Transfer-Encoding" header */
      }
    }
    if (filter_content_len)
    { /* Need to filter-out "Content-Length" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONTENT_LENGTH) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_CONTENT_LENGTH,
                                          hdr->header, hdr->header_size)) )
      {
        /* Reset filter flag if only one header is allowed */
        filter_transf_enc =
          (0 == (r->flags & MHD_RF_INSANITY_HEADER_CONTENT_LENGTH));
        if (log)
        {
          fprintf(log, "add_user_headers: filtered Content-Length header at hdr=%p, reset filter_transf_enc=%d\n",
                  (void *)hdr, (int)filter_transf_enc);
          fflush(log);
        }
        continue; /* Skip "Content-Length" header */
      }
    }

    /* Add user header */
    el_size = hdr->header_size + 2 + hdr->value_size + 2;
    if (buf_size < *ppos + el_size)
    {
      result = false;
      if (log)
      {
        fprintf(log, "add_user_headers: buffer overflow: buf_size=%zu *ppos=%zu needed el_size=%zu -> returning false\n",
                buf_size, (size_t)*ppos, el_size);
        fflush(log);
      }
      goto cleanup;
    }
    memcpy (buf + *ppos, hdr->header, hdr->header_size);
    (*ppos) += hdr->header_size;
    buf[(*ppos)++] = ':';
    buf[(*ppos)++] = ' ';
    if (add_close || add_keep_alive)
    {
      /* "Connection:" header must be always the first one */
      mhd_assert (MHD_str_equal_caseless_n_ (hdr->header, \
                                             MHD_HTTP_HEADER_CONNECTION, \
                                             hdr->header_size));

      if (add_close)
      {
        el_size += MHD_STATICSTR_LEN_ ("close, ");
        if (buf_size < initial_pos + el_size)
        {
          result = false;
          if (log)
          {
            fprintf(log, "add_user_headers: buffer overflow while adding 'close, ': buf_size=%zu initial_pos=%zu el_size=%zu -> returning false\n",
                    buf_size, initial_pos, el_size);
            fflush(log);
          }
          goto cleanup;
        }
        memcpy (buf + *ppos, "close, ",
                MHD_STATICSTR_LEN_ ("close, "));
        *ppos += MHD_STATICSTR_LEN_ ("close, ");
        if (log)
        {
          fprintf(log, "add_user_headers: injected 'close, ' at pos=%zu\n", (size_t)(*ppos - MHD_STATICSTR_LEN_("close, ")));
          fflush(log);
        }
      }
      else
      {
        el_size += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
        if (buf_size < initial_pos + el_size)
        {
          result = false;
          if (log)
          {
            fprintf(log, "add_user_headers: buffer overflow while adding 'Keep-Alive, ': buf_size=%zu initial_pos=%zu el_size=%zu -> returning false\n",
                    buf_size, initial_pos, el_size);
            fflush(log);
          }
          goto cleanup;
        }
        memcpy (buf + *ppos, "Keep-Alive, ",
                MHD_STATICSTR_LEN_ ("Keep-Alive, "));
        *ppos += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
        if (log)
        {
          fprintf(log, "add_user_headers: injected 'Keep-Alive, ' at pos=%zu\n", (size_t)(*ppos - MHD_STATICSTR_LEN_("Keep-Alive, ")));
          fflush(log);
        }
      }
      add_close = false;
      add_keep_alive = false;
      if (log)
      {
        fprintf(log, "add_user_headers: after connection injection add_close=%d add_keep_alive=%d\n",
                (int)add_close, (int)add_keep_alive);
        fflush(log);
      }
    }
    if (0 != hdr->value_size)
      memcpy (buf + *ppos, hdr->value, hdr->value_size);
    *ppos += hdr->value_size;
    buf[(*ppos)++] = '\r';
    buf[(*ppos)++] = '\n';
    mhd_assert (initial_pos + el_size == (*ppos));

    if (log)
    {
      fprintf(log, "add_user_headers: wrote header hdr=%p new_ppos=%zu expected_ppos=%zu\n",
              (void *)hdr, (size_t)(*ppos), (size_t)(initial_pos + el_size));
      fflush(log);
    }
  }

  result = true;

cleanup:
  if (log)
  {
    fprintf(log, "add_user_headers: exit result=%d final_ppos=%zu\n", (int)result, (size_t)*ppos);
    fclose(log);
  }
  return result;
}
