static bool
add_user_headers (char *buf,
                  size_t *ppos,
                  size_t buf_size,
                  struct MHD_Response *response,
                  bool filter_transf_enc,
                  bool filter_content_len,
                  bool add_close,
                  bool add_keep_alive)
{
  struct MHD_Response *const r = response; /**< a short alias */
  struct MHD_HTTP_Res_Header *hdr; /**< Iterates through User-specified headers */
  size_t el_size; /**< the size of current element to be added to the @a buf */
  bool result = true;
  FILE *logf = fopen("/tmp/logs/4385.txt", "a");

  if (logf)
  {
    fprintf(logf, "add_user_headers: entry r=%p first_header=%p flags_auto=0x%lx flags=0x%lx filter_transf_enc=%d filter_content_len=%d add_close=%d add_keep_alive=%d buf_size=%zu ppos=%zu\n",
            (void *)r,
            (void *)(r ? r->first_header : NULL),
            (unsigned long)(r ? r->flags_auto : 0UL),
            (unsigned long)(r ? r->flags : 0UL),
            (int)filter_transf_enc,
            (int)filter_content_len,
            (int)add_close,
            (int)add_keep_alive,
            buf_size,
            *ppos);
  }

  mhd_assert (! add_close || ! add_keep_alive);

  if (0 == (r->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED))
    filter_transf_enc = false;   /* No such header */
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH))
    filter_content_len = false;  /* No such header */
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    add_close = false;          /* No such header */
    add_keep_alive = false;     /* No such header */
  }
  else if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE))
    add_close = false;          /* "close" token was already set */

  if (logf)
  {
    fprintf(logf, "add_user_headers: after init filter_transf_enc=%d filter_content_len=%d add_close=%d add_keep_alive=%d\n",
            (int)filter_transf_enc,
            (int)filter_content_len,
            (int)add_close,
            (int)add_keep_alive);
  }

  for (hdr = r->first_header; NULL != hdr; hdr = hdr->next)
  {
    size_t initial_pos = *ppos;

    if (logf)
    {
      /* Log header pointer, sizes and a truncated view of header/value. */
      fprintf(logf, "iter hdr=%p kind=%d header_size=%zu value_size=%zu header=\"%.*s\"\n",
              (void *)hdr,
              (int)hdr->kind,
              hdr->header_size,
              hdr->value_size,
              (int)hdr->header_size,
              hdr->header ? hdr->header : "");
    }

    if (MHD_HEADER_KIND != hdr->kind)
    {
      if (logf)
        fprintf(logf, "  skipping hdr=%p: kind mismatch (wanted %d)\n", (void *)hdr, MHD_HEADER_KIND);
      continue;
    }
    if (filter_transf_enc)
    { /* Need to filter-out "Transfer-Encoding" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_TRANSFER_ENCODING) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_TRANSFER_ENCODING,
                                          hdr->header, hdr->header_size)) )
      {
        filter_transf_enc = false; /* There is the only one such header */
        if (logf)
          fprintf(logf, "  skipping hdr=%p: filtered Transfer-Encoding\n", (void *)hdr);
        continue; /* Skip "Transfer-Encoding" header */
      }
    }
    if (filter_content_len)
    { /* Need to filter-out "Content-Length" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONTENT_LENGTH) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_CONTENT_LENGTH,
                                          hdr->header, hdr->header_size)) )
      {
        /* Reset filter flag if only one header is allowed */
        filter_transf_enc =
          (0 == (r->flags & MHD_RF_INSANITY_HEADER_CONTENT_LENGTH));
        if (logf)
          fprintf(logf, "  skipping hdr=%p: filtered Content-Length; new filter_transf_enc=%d\n", (void *)hdr, (int)filter_transf_enc);
        continue; /* Skip "Content-Length" header */
      }
    }

    /* Add user header */
    el_size = hdr->header_size + 2 + hdr->value_size + 2;
    if (buf_size < *ppos + el_size)
    {
      if (logf)
        fprintf(logf, "  aborting hdr=%p: insufficient buffer (need %zu, have %zu)\n", (void *)hdr, *ppos + el_size, buf_size);
      result = false;
      goto out;
    }
    memcpy (buf + *ppos, hdr->header, hdr->header_size);
    (*ppos) += hdr->header_size;
    buf[(*ppos)++] = ':';
    buf[(*ppos)++] = ' ';
    if (add_close || add_keep_alive)
    {
      /* "Connection:" header must be always the first one */
      mhd_assert (MHD_str_equal_caseless_n_ (hdr->header, \
                                             MHD_HTTP_HEADER_CONNECTION, \
                                             hdr->header_size));

      if (add_close)
      {
        el_size += MHD_STATICSTR_LEN_ ("close, ");
        if (buf_size < initial_pos + el_size)
        {
          if (logf)
            fprintf(logf, "  aborting hdr=%p: insufficient buffer for close token (need %zu, have %zu)\n", (void *)hdr, initial_pos + el_size, buf_size);
          result = false;
          goto out;
        }
        memcpy (buf + *ppos, "close, ",
                MHD_STATICSTR_LEN_ ("close, "));
        *ppos += MHD_STATICSTR_LEN_ ("close, ");
        if (logf) fprintf(logf, "  hdr=%p: inserted 'close, ' token\n", (void *)hdr);
      }
      else
      {
        el_size += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
        if (buf_size < initial_pos + el_size)
        {
          if (logf)
            fprintf(logf, "  aborting hdr=%p: insufficient buffer for Keep-Alive token (need %zu, have %zu)\n", (void *)hdr, initial_pos + el_size, buf_size);
          result = false;
          goto out;
        }
        memcpy (buf + *ppos, "Keep-Alive, ",
                MHD_STATICSTR_LEN_ ("Keep-Alive, "));
        *ppos += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
        if (logf) fprintf(logf, "  hdr=%p: inserted 'Keep-Alive, ' token\n", (void *)hdr);
      }
      add_close = false;
      add_keep_alive = false;
    }
    if (0 != hdr->value_size)
      memcpy (buf + *ppos, hdr->value, hdr->value_size);
    *ppos += hdr->value_size;
    buf[(*ppos)++] = '\r';
    buf[(*ppos)++] = '\n';
    mhd_assert (initial_pos + el_size == (*ppos));
    if (logf)
      fprintf(logf, "  hdr=%p added: initial_pos=%zu el_size=%zu new_ppos=%zu\n",
              (void *)hdr, initial_pos, el_size, *ppos);
  }

  result = true;

out:
  if (logf)
  {
    fprintf(logf, "add_user_headers: exit result=%d final_ppos=%zu\n", (int)result, *ppos);
    fclose(logf);
  }
  return result;
}
