static bool
buffer_append (char *buf,
               size_t *ppos,
               size_t buf_size,
               const char *append,
               size_t append_size)
{
  /* Minimal probe so that execution is logged whenever this function is called. */
  FILE *log = fopen("/tmp/logs/2773.txt", "a");
  if (log)
    {
      /* Compute whether the addition would overflow in mathematical sense without
         performing an overflowing store. Use (size_t)-1 as the maximum value of size_t
         so we don't rely on SIZE_MAX being declared here. */
      size_t max_sz = (size_t)-1;
      int would_overflow = (*ppos > max_sz - append_size) ? 1 : 0;
      /* Compute the sum using size_t which mirrors the original code's computation
         (and therefore will exhibit the same wrap behavior if overflow occurs). */
      size_t wrapped_sum = *ppos + append_size;

      fprintf (log,
               "buffer_append ENTER: buf=%p, *ppos=%zu, buf_size=%zu, append=%p, append_size=%zu\n",
               (void *) buf, *ppos, buf_size, (const void *) append, append_size);
      fprintf (log, "overflow_check: would_overflow=%d, wrapped_sum=*ppos+append_size (size_t)=%zu\n",
               would_overflow, wrapped_sum);
      fclose (log);
    }

  mhd_assert (NULL != buf); /* Mute static analyzer */

  /* Preserve the original logic exactly as written (including the potential
     wrap of *ppos + append_size). */
  if (buf_size < *ppos + append_size)
    {
      FILE *log2 = fopen("/tmp/logs/2773.txt", "a");
      if (log2)
        {
          fprintf (log2, "buffer_append: bounds check failed: buf_size(%zu) < *ppos + append_size (wrapped=%zu) -> returning false\n",
                   buf_size, *ppos + append_size);
          fclose (log2);
        }
      return false;
    }

  {
    FILE *log3 = fopen("/tmp/logs/2773.txt", "a");
    if (log3)
      {
        fprintf (log3, "buffer_append: about to memcpy to buf+*ppos (%p), size=%zu\n",
                 (void *) (buf + *ppos), append_size);
        fclose (log3);
      }
  }

  memcpy (buf + *ppos, append, append_size);
  *ppos += append_size;

  {
    FILE *log4 = fopen("/tmp/logs/2773.txt", "a");
    if (log4)
      {
        fprintf (log4, "buffer_append: memcpy done, new *ppos=%zu, returning true\n", *ppos);
        fclose (log4);
      }
  }

  return true;
}
