static bool
buffer_append (char *buf,
               size_t *ppos,
               size_t buf_size,
               const char *append,
               size_t append_size)
{
  /* Preserve original assertion behavior */
  mhd_assert (NULL != buf); /* Mute static analyzer */

  /* Open log file for append. Best-effort: if this fails, do not alter behavior. */
  FILE *logf = fopen("/tmp/logs/2773.txt", "a");
  if (logf) {
    fprintf(logf, "ENTER buffer_append: buf=%p ppos=%p *ppos=%zu buf_size=%zu append=%p append_size=%zu\n",
            (void *)buf, (void *)ppos, (ppos ? *ppos : (size_t)0), buf_size, (void *)append, append_size);
  }

  /* Compute sum the same way the original code does so behavior is preserved. */
  size_t sum = *ppos + append_size;
  int overflow = (sum < *ppos); /* unsigned wrap-around detection */

  if (logf) {
    fprintf(logf, "Computed: sum = *ppos + append_size -> %zu (overflow=%d)\n", sum, overflow);
    fprintf(logf, "Condition check: (buf_size < sum) -> (%zu < %zu) == %d\n", buf_size, sum, (int)(buf_size < sum));
  }

  /* Preserve original logic exactly. */
  if (buf_size < sum)
  {
    if (logf) {
      fprintf(logf, "Result: returning false (insufficient space). buf_size=%zu sum=%zu\n", buf_size, sum);
      fclose(logf);
    }
    return false;
  }

  if (logf) {
    fprintf(logf, "About to memcpy: dest=%p src=%p n=%zu\n", (void *)(buf + *ppos), (void *)append, append_size);
    fflush(logf);
  }

  memcpy (buf + *ppos, append, append_size);

  *ppos += append_size;

  if (logf) {
    fprintf(logf, "After memcpy: updated *ppos=%zu\n", *ppos);
    fclose(logf);
  }

  return true;
}
