static enum MHD_Result
build_connection_chunked_response_footer (struct MHD_Connection *connection)
{
  char *buf;           /**< the buffer to write footers to */
  size_t buf_size;     /**< the size of the @a buf */
  size_t used_size;    /**< the used size of the @a buf */
  struct MHD_Connection *const c = connection; /**< a short alias */
  struct MHD_HTTP_Res_Header *pos;
  FILE *log = NULL;

  /* Open log file (append). If this fails, proceed without logging. */
  log = fopen("/tmp/logs/1288.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER: build_connection_chunked_response_footer connection=%p\n", (void *)connection);
  }

  if (log)
  {
    /* Log the chunked flag value if accessible. Use conditional access to avoid UB if connection is NULL. */
    if (connection != NULL)
      fprintf(log, "PRE-ASSERT: connection->rp.props.chunked=%d\n", (int) !!(connection->rp.props.chunked));
    else
      fprintf(log, "PRE-ASSERT: connection is NULL\n");
  }

  mhd_assert (connection->rp.props.chunked);

  if (log)
  {
    fprintf(log, "POST-ASSERT: executed mhd_assert(connection->rp.props.chunked);\n");
  }

  /* TODO: allow combining of the final footer with the last chunk,
   * modify the next assert. */
  if (log)
    fprintf(log, "PRE-ASSERT: expecting state MHD_CONNECTION_CHUNKED_BODY_SENT (connection=%p)\n", (void *)connection);
  mhd_assert (MHD_CONNECTION_CHUNKED_BODY_SENT == connection->state);
  if (log)
    fprintf(log, "POST-ASSERT: executed mhd_assert(state) (state=%d)\n", (int) connection->state);

  if (log)
    fprintf(log, "PRE-ASSERT: expecting c->rp.response != NULL (c=%p)\n", (void *)c);
  mhd_assert (NULL != c->rp.response);
  if (log)
    fprintf(log, "POST-ASSERT: executed mhd_assert(response) (response=%p)\n", (void *) (c->rp.response));

  buf_size = connection_maximize_write_buffer (c);
  if (log)
    fprintf(log, "connection_maximize_write_buffer returned buf_size=%zu\n", buf_size);

  /* '5' is the minimal size of chunked footer ("0\r\n\r\n") */
  if (buf_size < 5)
  {
    if (log)
    {
      fprintf(log, "EARLY RETURN: buf_size < 5 (buf_size=%zu) -> MHD_NO\n", buf_size);
      fclose(log);
    }
    return MHD_NO;
  }

  if (log)
    fprintf(log, "ASSERT: expecting write_buffer not NULL (write_buffer=%p) append_offset=%zu size=%zu\n",
            (void *) c->write_buffer, (size_t) c->write_buffer_append_offset, (size_t) c->write_buffer_size);
  mhd_assert (NULL != c->write_buffer);
  buf = c->write_buffer + c->write_buffer_append_offset;
  mhd_assert (NULL != buf);

  if (log)
    fprintf(log, "Buffer setup: buf=%p used_size starts at 0 write_buffer_append_offset=%zu\n",
            (void *) buf, (size_t) c->write_buffer_append_offset);

  used_size = 0;
  buf[used_size++] = '0';
  buf[used_size++] = '\r';
  buf[used_size++] = '\n';

  if (log)
    fprintf(log, "WROTE initial chunk terminator, used_size=%zu\n", used_size);

  for (pos = c->rp.response->first_header; NULL != pos; pos = pos->next)
  {
    if (log)
      fprintf(log, "ITER: header pos=%p kind=%d header_size=%zu value_size=%zu\n",
              (void *) pos, (int) pos->kind, (size_t) pos->header_size, (size_t) pos->value_size);

    if (MHD_FOOTER_KIND == pos->kind)
    {
      size_t new_used_size; /* resulting size with this header */
      /* '4' is colon, space, linefeeds */
      new_used_size = used_size + pos->header_size + pos->value_size + 4;
      if (log)
        fprintf(log, "FOOTER: header %p new_used_size=%zu buf_size=%zu\n", (void *) pos, new_used_size, buf_size);
      if (new_used_size > buf_size)
      {
        if (log)
        {
          fprintf(log, "EARLY RETURN: not enough buffer for footer (needed=%zu available=%zu) -> MHD_NO\n",
                  new_used_size, buf_size);
          fclose(log);
        }
        return MHD_NO;
      }
      memcpy (buf + used_size, pos->header, pos->header_size);
      used_size += pos->header_size;
      buf[used_size++] = ':';
      buf[used_size++] = ' ';
      memcpy (buf + used_size, pos->value, pos->value_size);
      used_size += pos->value_size;
      buf[used_size++] = '\r';
      buf[used_size++] = '\n';
      mhd_assert (used_size == new_used_size);
      if (log)
        fprintf(log, "APPENDED footer header=%p used_size now=%zu\n", (void *) pos, used_size);
    }
  }
  if (used_size + 2 > buf_size)
  {
    if (log)
    {
      fprintf(log, "EARLY RETURN: not enough space for final CRLF (used_size=%zu buf_size=%zu) -> MHD_NO\n",
              used_size, buf_size);
      fclose(log);
    }
    return MHD_NO;
  }
  buf[used_size++] = '\r';
  buf[used_size++] = '\n';

  if (log)
    fprintf(log, "FINALIZED footer write, final used_size=%zu\n", used_size);

  c->write_buffer_append_offset += used_size;
  mhd_assert (c->write_buffer_append_offset <= c->write_buffer_size);

  if (log)
  {
    fprintf(log, "UPDATE: write_buffer_append_offset=%zu write_buffer_size=%zu -> returning MHD_YES\n",
            (size_t) c->write_buffer_append_offset, (size_t) c->write_buffer_size);
    fclose(log);
  }

  return MHD_YES;
}
