static enum MHD_Result
build_connection_chunked_response_footer (struct MHD_Connection *connection)
{
  char *buf;           /**< the buffer to write footers to */
  size_t buf_size;     /**< the size of the @a buf */
  size_t used_size;    /**< the used size of the @a buf */
  struct MHD_Connection *const c = connection; /**< a short alias */
  struct MHD_HTTP_Res_Header *pos;
  FILE *fp = fopen("/tmp/logs/1288.txt", "a");

  if (fp)
  {
    fprintf(fp, "ENTER build_connection_chunked_response_footer: connection=%p, state=%d, chunked=%d, response=%p, write_buffer=%p, append_off=%zu, write_buffer_size=%zu\n",
            (void *)connection,
            (int)connection->state,
            (int)connection->rp.props.chunked,
            (void *)connection->rp.response,
            (void *)connection->write_buffer,
            connection->write_buffer_append_offset,
            connection->write_buffer_size);
  }

  mhd_assert (connection->rp.props.chunked);
  /* TODO: allow combining of the final footer with the last chunk,
   * modify the next assert. */
  mhd_assert (MHD_CONNECTION_CHUNKED_BODY_SENT == connection->state);
  mhd_assert (NULL != c->rp.response);

  buf_size = connection_maximize_write_buffer (c);
  if (fp)
    fprintf(fp, "after connection_maximize_write_buffer: buf_size=%zu\n", buf_size);
  /* '5' is the minimal size of chunked footer ("0\r\n\r\n") */
  if (buf_size < 5)
  {
    if (fp)
    {
      fprintf(fp, "EARLY RETURN: buf_size < 5 (buf_size=%zu) -> MHD_NO\n", buf_size);
      fclose(fp);
    }
    return MHD_NO;
  }
  mhd_assert (NULL != c->write_buffer);
  buf = c->write_buffer + c->write_buffer_append_offset;
  if (fp)
    fprintf(fp, "computed buf=%p (base=%p + off=%zu)\n", (void *)buf, (void *)c->write_buffer, c->write_buffer_append_offset);
  mhd_assert (NULL != buf);
  used_size = 0;
  buf[used_size++] = '0';
  buf[used_size++] = '\r';
  buf[used_size++] = '\n';

  if (fp)
    fprintf(fp, "after initial chunked terminator: used_size=%zu\n", used_size);

  for (pos = c->rp.response->first_header; NULL != pos; pos = pos->next)
  {
    if (MHD_FOOTER_KIND == pos->kind)
    {
      size_t new_used_size; /* resulting size with this header */
      /* '4' is colon, space, linefeeds */
      new_used_size = used_size + pos->header_size + pos->value_size + 4;
      if (fp)
        fprintf(fp, "footer header found: header_size=%zu, value_size=%zu, new_used_size=%zu, buf_size=%zu\n",
                pos->header_size, pos->value_size, new_used_size, buf_size);
      if (new_used_size > buf_size)
      {
        if (fp)
        {
          fprintf(fp, "EARLY RETURN: new_used_size > buf_size (new_used_size=%zu, buf_size=%zu) -> MHD_NO\n",
                  new_used_size, buf_size);
          fclose(fp);
        }
        return MHD_NO;
      }
      memcpy (buf + used_size, pos->header, pos->header_size);
      used_size += pos->header_size;
      buf[used_size++] = ':';
      buf[used_size++] = ' ';
      memcpy (buf + used_size, pos->value, pos->value_size);
      used_size += pos->value_size;
      buf[used_size++] = '\r';
      buf[used_size++] = '\n';
      if (fp)
        fprintf(fp, "after appending header: used_size=%zu (expected new_used_size=%zu)\n",
                used_size, new_used_size);
      mhd_assert (used_size == new_used_size);
    }
  }
  if (used_size + 2 > buf_size)
  {
    if (fp)
    {
      fprintf(fp, "EARLY RETURN: used_size + 2 > buf_size (used_size=%zu, buf_size=%zu) -> MHD_NO\n",
              used_size, buf_size);
      fclose(fp);
    }
    return MHD_NO;
  }
  buf[used_size++] = '\r';
  buf[used_size++] = '\n';

  if (fp)
    fprintf(fp, "before append_offset update: used_size=%zu, old_append_offset=%zu, write_buffer_size=%zu\n",
            used_size, c->write_buffer_append_offset, c->write_buffer_size);

  c->write_buffer_append_offset += used_size;

  if (fp)
  {
    fprintf(fp, "after append_offset update: new_append_offset=%zu, write_buffer_size=%zu, relation(new<=size)=%d\n",
            c->write_buffer_append_offset, c->write_buffer_size,
            (int)(c->write_buffer_append_offset <= c->write_buffer_size));
  }

  mhd_assert (c->write_buffer_append_offset <= c->write_buffer_size);

  if (fp)
  {
    fprintf(fp, "EXIT build_connection_chunked_response_footer: returning MHD_YES\n");
    fclose(fp);
  }

  return MHD_YES;
}
