static enum MHD_Result
build_connection_chunked_response_footer (struct MHD_Connection *connection)
{
  char *buf;           /**< the buffer to write footers to */
  size_t buf_size;     /**< the size of the @a buf */
  size_t used_size;    /**< the used size of the @a buf */
  struct MHD_Connection *const c = connection; /**< a short alias */
  struct MHD_HTTP_Res_Header *pos;
  FILE *log = fopen("/tmp/logs/1283.txt", "a");

  if (log)
  {
    fprintf(log, "ENTER build_connection_chunked_response_footer: connection=%p\n", (void *)connection);
    /* attempt to log some visible state from connection (best-effort) */
    fprintf(log, "  connection->state=%d, connection->rp.response=%p, write_buffer=%p, write_buffer_append_offset=%zu, write_buffer_size=%zu\n",
            (int)connection->state,
            (void *)connection->rp.response,
            (void *)connection->write_buffer,
            (size_t)connection->write_buffer_append_offset,
            (size_t)connection->write_buffer_size);
    fflush(log);
  }

  mhd_assert (connection->rp.props.chunked);
  /* TODO: allow combining of the final footer with the last chunk,
   * modify the next assert. */
  mhd_assert (MHD_CONNECTION_CHUNKED_BODY_SENT == connection->state);
  mhd_assert (NULL != c->rp.response);

  buf_size = connection_maximize_write_buffer (c);
  if (log)
  {
    fprintf(log, "  buffer_size after connection_maximize_write_buffer: buf_size=%zu\n", buf_size);
    fflush(log);
  }

  /* '5' is the minimal size of chunked footer ("0\r\n\r\n") */
  if (buf_size < 5)
  {
    if (log)
    {
      fprintf(log, "  early return: buf_size < 5 (%zu) -> MHD_NO\n", buf_size);
      fclose(log);
    }
    return MHD_NO;
  }
  mhd_assert (NULL != c->write_buffer);
  buf = c->write_buffer + c->write_buffer_append_offset;
  mhd_assert (NULL != buf);
  used_size = 0;
  buf[used_size++] = '0';
  buf[used_size++] = '\r';
  buf[used_size++] = '\n';

  if (log)
  {
    fprintf(log, "  initial used_size after writing '0\\r\\n': %zu\n", used_size);
    fflush(log);
  }

  for (pos = c->rp.response->first_header; NULL != pos; pos = pos->next)
  {
    if (MHD_FOOTER_KIND == pos->kind)
    {
      size_t new_used_size; /* resulting size with this header */
      /* '4' is colon, space, linefeeds */
      new_used_size = used_size + pos->header_size + pos->value_size + 4;

      if (log)
      {
        fprintf(log, "  processing header pos=%p: kind=%d, header=%p, header_size=%zu, value=%p, value_size=%zu\n",
                (void *)pos,
                (int)pos->kind,
                (void *)pos->header,
                (size_t)pos->header_size,
                (void *)pos->value,
                (size_t)pos->value_size);
        fprintf(log, "    computed new_used_size=%zu (used_size=%zu + header_size=%zu + value_size=%zu + 4)\n",
                new_used_size, used_size, pos->header_size, pos->value_size);
        fflush(log);
      }

      if (new_used_size > buf_size)
      {
        if (log)
        {
          fprintf(log, "    check failed: new_used_size (%zu) > buf_size (%zu) -> MHD_NO\n", new_used_size, buf_size);
          fclose(log);
        }
        return MHD_NO;
      }

      if (log)
      {
        fprintf(log, "    copying header bytes: dest=%p, src=%p, n=%zu\n",
                (void *)(buf + used_size), (void *)pos->header, pos->header_size);
        fflush(log);
      }

      memcpy (buf + used_size, pos->header, pos->header_size);
      used_size += pos->header_size;
      buf[used_size++] = ':';
      buf[used_size++] = ' ';

      if (log)
      {
        fprintf(log, "    copying value bytes: dest=%p, src=%p, n=%zu\n",
                (void *)(buf + used_size), (void *)pos->value, pos->value_size);
        fflush(log);
      }

      memcpy (buf + used_size, pos->value, pos->value_size);
      used_size += pos->value_size;
      buf[used_size++] = '\r';
      buf[used_size++] = '\n';

      if (log)
      {
        fprintf(log, "    after writing header+value used_size=%zu, expected new_used_size=%zu\n", used_size, new_used_size);
        fflush(log);
      }

      mhd_assert (used_size == new_used_size);
    }
  }
  if (used_size + 2 > buf_size)
  {
    if (log)
    {
      fprintf(log, "  final check failed: used_size + 2 (%zu) > buf_size (%zu) -> MHD_NO\n", used_size + 2, buf_size);
      fclose(log);
    }
    return MHD_NO;
  }
  buf[used_size++] = '\r';
  buf[used_size++] = '\n';

  if (log)
  {
    fprintf(log, "  wrote final CRLF, final used_size=%zu\n", used_size);
    fflush(log);
  }

  c->write_buffer_append_offset += used_size;

  if (log)
  {
    fprintf(log, "  new write_buffer_append_offset=%zu (<= write_buffer_size %zu expected)\n",
            c->write_buffer_append_offset, c->write_buffer_size);
    fprintf(log, "EXIT build_connection_chunked_response_footer -> MHD_YES\n");
    fclose(log);
  }

  mhd_assert (c->write_buffer_append_offset <= c->write_buffer_size);

  return MHD_YES;
}
