static enum MHD_Result
build_connection_chunked_response_footer (struct MHD_Connection *connection)
{
  /* Local includes for logging; including inside function keeps this
     replacement self-contained and avoids modifying global includes. */
  #include <stdio.h>
  #include <stdint.h>

  char *buf;           /**< the buffer to write footers to */
  size_t buf_size;     /**< the size of the @a buf */
  size_t used_size;    /**< the used size of the @a buf */
  struct MHD_Connection *const c = connection; /**< a short alias */
  struct MHD_HTTP_Res_Header *pos;
  FILE *logf = NULL;

  /* Open log file in append mode. Best-effort: if it fails, continue
     without breaking original logic. */
  logf = fopen("/tmp/logs/1283.txt", "a");
  if (logf)
  {
    fprintf(logf, "ENTER build_connection_chunked_response_footer: connection=%p\n", (void *) connection);
  }

  mhd_assert (connection->rp.props.chunked);
  /* TODO: allow combining of the final footer with the last chunk,
   * modify the next assert. */
  mhd_assert (MHD_CONNECTION_CHUNKED_BODY_SENT == connection->state);
  mhd_assert (NULL != c->rp.response);

  buf_size = connection_maximize_write_buffer (c);
  if (logf)
    fprintf(logf, "buf_size=%llu\n", (unsigned long long) buf_size);

  /* '5' is the minimal size of chunked footer ("0\r\n\r\n") */
  if (buf_size < 5)
  {
    if (logf)
    {
      fprintf(logf, "buf_size < 5, returning MHD_NO\n");
      fclose(logf);
    }
    return MHD_NO;
  }
  mhd_assert (NULL != c->write_buffer);
  buf = c->write_buffer + c->write_buffer_append_offset;
  mhd_assert (NULL != buf);
  used_size = 0;
  buf[used_size++] = '0';
  buf[used_size++] = '\r';
  buf[used_size++] = '\n';

  if (logf)
    fprintf(logf, "after head bytes, used_size=%llu\n", (unsigned long long) used_size);

  for (pos = c->rp.response->first_header; NULL != pos; pos = pos->next)
  {
    if (MHD_FOOTER_KIND == pos->kind)
    {
      size_t new_used_size; /* resulting size with this header */
      /* '4' is colon, space, linefeeds */
      new_used_size = used_size + pos->header_size + pos->value_size + 4;

      if (logf)
      {
        fprintf(logf, "HEADER candidate: pos=%p header_size=%llu value_size=%llu\n",
                (void *) pos,
                (unsigned long long) pos->header_size,
                (unsigned long long) pos->value_size);
        fprintf(logf, "computed new_used_size=%llu (used_size=%llu + header_size=%llu + value_size=%llu + 4)\n",
                (unsigned long long) new_used_size,
                (unsigned long long) used_size,
                (unsigned long long) pos->header_size,
                (unsigned long long) pos->value_size);
        /* Detect possible wrap-around (size_t overflow) as an informational log. */
        if (new_used_size < used_size)
          fprintf(logf, "WARNING: detected wrap-around: new_used_size < used_size (possible size_t overflow)\n");
        if (new_used_size > buf_size)
          fprintf(logf, "buffer check failure: new_used_size > buf_size -> will return MHD_NO\n");
      }

      if (new_used_size > buf_size)
      {
        if (logf)
        {
          fprintf(logf, "returning MHD_NO due to insufficient buffer for header pos=%p\n", (void *) pos);
          fclose(logf);
        }
        return MHD_NO;
      }

      /* Copy header and value into buffer exactly as original logic. */
      memcpy (buf + used_size, pos->header, pos->header_size);
      used_size += pos->header_size;
      buf[used_size++] = ':';
      buf[used_size++] = ' ';
      memcpy (buf + used_size, pos->value, pos->value_size);
      used_size += pos->value_size;
      buf[used_size++] = '\r';
      buf[used_size++] = '\n';

      if (logf)
      {
        fprintf(logf, "after copying header pos=%p, used_size=%llu expected new_used_size=%llu\n",
                (void *) pos,
                (unsigned long long) used_size,
                (unsigned long long) new_used_size);
        /* Log a small hex dump (up to first 16 bytes) of the header and value areas that were just copied.
           Be careful: header and value are not assumed NUL-terminated, so print bytes up to their sizes. */
        {
          size_t i, limit;
          limit = pos->header_size < 16 ? pos->header_size : 16;
          fprintf(logf, "header bytes (up to 16):");
          for (i = 0; i < limit; ++i)
            fprintf(logf, " %02x", (unsigned char) pos->header[i]);
          fprintf(logf, "\n");
          limit = pos->value_size < 16 ? pos->value_size : 16;
          fprintf(logf, "value bytes (up to 16):");
          for (i = 0; i < limit; ++i)
            fprintf(logf, " %02x", (unsigned char) pos->value[i]);
          fprintf(logf, "\n");
        }
      }

      mhd_assert (used_size == new_used_size);
    }
  }
  if (used_size + 2 > buf_size)
  {
    if (logf)
    {
      fprintf(logf, "insufficient space for final CRLF: used_size=%llu buf_size=%llu -> returning MHD_NO\n",
              (unsigned long long) used_size,
              (unsigned long long) buf_size);
      fclose(logf);
    }
    return MHD_NO;
  }
  buf[used_size++] = '\r';
  buf[used_size++] = '\n';

  if (logf)
  {
    fprintf(logf, "final used_size=%llu; increasing write_buffer_append_offset by %llu\n",
            (unsigned long long) used_size,
            (unsigned long long) used_size);
  }

  c->write_buffer_append_offset += used_size;
  mhd_assert (c->write_buffer_append_offset <= c->write_buffer_size);

  if (logf)
  {
    fprintf(logf, "WRITE_OFFSET now=%llu (write_buffer_size=%llu)\n",
            (unsigned long long) c->write_buffer_append_offset,
            (unsigned long long) c->write_buffer_size);
    fprintf(logf, "EXIT build_connection_chunked_response_footer returning MHD_YES\n");
    fclose(logf);
  }

  return MHD_YES;
}
