static enum MHD_Result
build_connection_chunked_response_footer (struct MHD_Connection *connection)
{
  char *buf;           /**< the buffer to write footers to */
  size_t buf_size;     /**< the size of the @a buf */
  size_t used_size;    /**< the used size of the @a buf */
  struct MHD_Connection *const c = connection; /**< a short alias */
  struct MHD_HTTP_Res_Header *pos;

  /* Logging probe: open log file for append. We log control flow and state
   * information relevant to the footer-build loop to reproduce the
   * pointer-const reasoning and show how 'pos' is only read. */
  {
    FILE *f = fopen("/tmp/logs/1285.txt", "a");
    if (f)
      {
        fprintf (f, "ENTER build_connection_chunked_response_footer: connection=%p\n", (void *)connection);
        if (connection)
          {
            fprintf (f, "  state=%d, chunked=%d, rp.response=%p\n",
                     (int)connection->state,
                     (int)connection->rp.props.chunked,
                     (void *)connection->rp.response);
          }
        fclose (f);
      }
  }

  mhd_assert (connection->rp.props.chunked);
  /* TODO: allow combining of the final footer with the last chunk,
   * modify the next assert. */
  mhd_assert (MHD_CONNECTION_CHUNKED_BODY_SENT == connection->state);
  mhd_assert (NULL != c->rp.response);

  buf_size = connection_maximize_write_buffer (c);
  /* '5' is the minimal size of chunked footer ("0\r\n\r\n") */
  if (buf_size < 5)
    {
      FILE *f = fopen("/tmp/logs/1285.txt", "a");
      if (f)
        {
          fprintf (f, "EARLY RETURN (buf_size < 5): buf_size=%zu\n", buf_size);
          fclose (f);
        }
      return MHD_NO;
    }
  mhd_assert (NULL != c->write_buffer);
  buf = c->write_buffer + c->write_buffer_append_offset;
  mhd_assert (NULL != buf);
  used_size = 0;
  buf[used_size++] = '0';
  buf[used_size++] = '\r';
  buf[used_size++] = '\n';

  /* Log current buffer and used size after writing chunk terminator */
  {
    FILE *f = fopen("/tmp/logs/1285.txt", "a");
    if (f)
      {
        fprintf (f, "AFTER INIT: buf_size=%zu, used_size=%zu, write_buffer=%p, write_buffer_append_offset=%zu\n",
                 buf_size, used_size, (void *)c->write_buffer, c->write_buffer_append_offset);
        fclose (f);
      }
  }

  for (pos = c->rp.response->first_header; NULL != pos; pos = pos->next)
  {
    /* Log iteration state for each header node. This shows that 'pos' is
     * only read (kind, header, value, sizes, next) and not modified. */
    {
      FILE *f = fopen("/tmp/logs/1285.txt", "a");
      if (f)
        {
          fprintf (f, "LOOP ITER: pos=%p, pos->next=%p, kind=%d, header_size=%zu, value_size=%zu, header=%p, value=%p\n",
                   (void *)pos, (void *)pos->next, (int)pos->kind, pos->header_size, pos->value_size,
                   (void *)pos->header, (void *)pos->value);

          /* Dump a small hex preview of header (up to 64 bytes) */
          if (pos->header && pos->header_size > 0)
            {
              size_t i, lim = pos->header_size < 64 ? pos->header_size : 64;
              fprintf (f, "  header_preview (%zu bytes):", lim);
              for (i = 0; i < lim; ++i)
                fprintf (f, " %02x", (unsigned char)pos->header[i]);
              if (pos->header_size > lim)
                fprintf (f, " ... (total %zu)", pos->header_size);
              fprintf (f, "\n");
            }

          /* Dump a small hex preview of value (up to 64 bytes) */
          if (pos->value && pos->value_size > 0)
            {
              size_t i, lim = pos->value_size < 64 ? pos->value_size : 64;
              fprintf (f, "  value_preview  (%zu bytes):", lim);
              for (i = 0; i < lim; ++i)
                fprintf (f, " %02x", (unsigned char)pos->value[i]);
              if (pos->value_size > lim)
                fprintf (f, " ... (total %zu)", pos->value_size);
              fprintf (f, "\n");
            }

          fclose (f);
        }
    }

    if (MHD_FOOTER_KIND == pos->kind)
    {
      size_t new_used_size; /* resulting size with this header */
      /* '4' is colon, space, linefeeds */
      new_used_size = used_size + pos->header_size + pos->value_size + 4;
      if (new_used_size > buf_size)
        {
          FILE *f = fopen("/tmp/logs/1285.txt", "a");
          if (f)
            {
              fprintf (f, "EARLY RETURN (insufficient space for header): new_used_size=%zu, buf_size=%zu\n",
                       new_used_size, buf_size);
              fclose (f);
            }
          return MHD_NO;
        }
      memcpy (buf + used_size, pos->header, pos->header_size);
      used_size += pos->header_size;
      buf[used_size++] = ':';
      buf[used_size++] = ' ';
      memcpy (buf + used_size, pos->value, pos->value_size);
      used_size += pos->value_size;
      buf[used_size++] = '\r';
      buf[used_size++] = '\n';
      mhd_assert (used_size == new_used_size);

      /* Log after adding a footer header */
      {
        FILE *f = fopen("/tmp/logs/1285.txt", "a");
        if (f)
          {
            fprintf (f, "ADDED FOOTER: pos=%p, new_used_size=%zu, buf_size=%zu\n",
                     (void *)pos, used_size, buf_size);
            fclose (f);
          }
      }
    }
  }
  if (used_size + 2 > buf_size)
    {
      FILE *f = fopen("/tmp/logs/1285.txt", "a");
      if (f)
        {
          fprintf (f, "EARLY RETURN (final CRLF would overflow): used_size=%zu, buf_size=%zu\n",
                   used_size, buf_size);
          fclose (f);
        }
      return MHD_NO;
    }
  buf[used_size++] = '\r';
  buf[used_size++] = '\n';

  c->write_buffer_append_offset += used_size;
  mhd_assert (c->write_buffer_append_offset <= c->write_buffer_size);

  /* Final logging: what we wrote and offsets */
  {
    FILE *f = fopen("/tmp/logs/1285.txt", "a");
    if (f)
      {
        fprintf (f, "EXIT build_connection_chunked_response_footer: wrote=%zu, write_buffer_append_offset=%zu, write_buffer_size=%zu\n",
                 used_size, c->write_buffer_append_offset, c->write_buffer_size);
        /* Optionally, print a tiny preview of the footer region we appended */
        if (c->write_buffer && used_size > 0)
          {
            size_t preview = used_size < 64 ? used_size : 64;
            size_t i;
            fprintf (f, "  appended_preview (%zu bytes):", preview);
            for (i = 0; i < preview; ++i)
              fprintf (f, " %02x", (unsigned char)c->write_buffer[c->write_buffer_append_offset - used_size + i]);
            if (used_size > preview)
              fprintf (f, " ... (total %zu)", used_size);
            fprintf (f, "\n");
          }
        fclose (f);
      }
  }

  return MHD_YES;
}
