static enum MHD_Result
build_connection_chunked_response_footer (struct MHD_Connection *connection)
{
  char *buf;           /**< the buffer to write footers to */
  size_t buf_size;     /**< the size of the @a buf */
  size_t used_size;    /**< the used size of the @a buf */
  struct MHD_Connection *const c = connection; /**< a short alias */
  struct MHD_HTTP_Res_Header *pos;
  FILE *logf = NULL;

  /* Open log file as early as possible to record entry and any early returns. */
  logf = fopen("/tmp/logs/1285.txt", "a");
  if (NULL != logf)
  {
    fprintf(logf, "ENTER build_connection_chunked_response_footer: connection=%p\n", (void *)connection);
    fflush(logf);
  }

  mhd_assert (connection->rp.props.chunked);
  /* TODO: allow combining of the final footer with the last chunk,
   * modify the next assert. */
  mhd_assert (MHD_CONNECTION_CHUNKED_BODY_SENT == connection->state);
  mhd_assert (NULL != c->rp.response);

  buf_size = connection_maximize_write_buffer (c);
  if (NULL != logf)
    fprintf(logf, "buf_size=%zu\n", buf_size);

  /* '5' is the minimal size of chunked footer ("0\r\n\r\n") */
  if (buf_size < 5)
  {
    if (NULL != logf)
    {
      fprintf(logf, "EARLY RETURN: buf_size < 5 -> %zu\n", buf_size);
      fclose(logf);
    }
    return MHD_NO;
  }
  mhd_assert (NULL != c->write_buffer);
  buf = c->write_buffer + c->write_buffer_append_offset;
  mhd_assert (NULL != buf);
  used_size = 0;
  buf[used_size++] = '0';
  buf[used_size++] = '\r';
  buf[used_size++] = '\n';

  if (NULL != logf)
    fprintf(logf, "begin headers iteration: first_header=%p\n", (void *)c->rp.response->first_header);

  for (pos = c->rp.response->first_header; NULL != pos; pos = pos->next)
  {
    if (NULL != logf)
    {
      /* Safely cap printed lengths to avoid extremely large log lines. */
      int hcap = (pos->header_size > 1024u) ? 1024 : (int)pos->header_size;
      int vcap = (pos->value_size > 1024u) ? 1024 : (int)pos->value_size;
      fprintf(logf, "iter header: pos=%p kind=%d header_size=%zu value_size=%zu header_ptr=%p value_ptr=%p\n",
              (void *)pos, (int)pos->kind, pos->header_size, pos->value_size, (void *)pos->header, (void *)pos->value);
      if (pos->header_size > 0 && NULL != pos->header)
        fprintf(logf, "  header_preview=\"%.*s\"\n", hcap, pos->header);
      if (pos->value_size > 0 && NULL != pos->value)
        fprintf(logf, "  value_preview=\"%.*s\"\n", vcap, pos->value);
      fflush(logf);
    }

    if (MHD_FOOTER_KIND == pos->kind)
    {
      size_t new_used_size; /* resulting size with this header */
      /* '4' is colon, space, linefeeds */
      new_used_size = used_size + pos->header_size + pos->value_size + 4;
      if (NULL != logf)
        fprintf(logf, "  footer candidate: new_used_size=%zu used_size=%zu buf_size=%zu\n", new_used_size, used_size, buf_size);
      if (new_used_size > buf_size)
      {
        if (NULL != logf)
        {
          fprintf(logf, "EARLY RETURN: new_used_size > buf_size -> %zu > %zu\n", new_used_size, buf_size);
          fclose(logf);
        }
        return MHD_NO;
      }
      memcpy (buf + used_size, pos->header, pos->header_size);
      used_size += pos->header_size;
      buf[used_size++] = ':';
      buf[used_size++] = ' ';
      memcpy (buf + used_size, pos->value, pos->value_size);
      used_size += pos->value_size;
      buf[used_size++] = '\r';
      buf[used_size++] = '\n';
      mhd_assert (used_size == new_used_size);

      if (NULL != logf)
        fprintf(logf, "  appended footer -> used_size=%zu\n", used_size);
    }
  }
  if (used_size + 2 > buf_size)
  {
    if (NULL != logf)
    {
      fprintf(logf, "EARLY RETURN: used_size + 2 > buf_size -> %zu + 2 > %zu\n", used_size, buf_size);
      fclose(logf);
    }
    return MHD_NO;
  }
  buf[used_size++] = '\r';
  buf[used_size++] = '\n';

  if (NULL != logf)
    fprintf(logf, "final used_size before append offset update=%zu\n", used_size);

  c->write_buffer_append_offset += used_size;
  mhd_assert (c->write_buffer_append_offset <= c->write_buffer_size);

  if (NULL != logf)
  {
    fprintf(logf, "EXIT build_connection_chunked_response_footer: success, write_buffer_append_offset=%zu\n", c->write_buffer_append_offset);
    fclose(logf);
  }

  return MHD_YES;
}
