static enum MHD_Result
build_connection_chunked_response_footer (struct MHD_Connection *connection)
{
  char *buf;           /**< the buffer to write footers to */
  size_t buf_size;     /**< the size of the @a buf */
  size_t used_size;    /**< the used size of the @a buf */
  struct MHD_Connection *const c = connection; /**< a short alias */
  struct MHD_HTTP_Res_Header *pos;
  FILE *log = fopen("/tmp/logs/1285.txt", "a");

  if (log)
  {
    fprintf(log, "ENTER build_connection_chunked_response_footer: connection=%p\n", (void *)connection);
  }

  mhd_assert (connection->rp.props.chunked);
  /* TODO: allow combining of the final footer with the last chunk,
   * modify the next assert. */
  mhd_assert (MHD_CONNECTION_CHUNKED_BODY_SENT == connection->state);
  mhd_assert (NULL != c->rp.response);

  buf_size = connection_maximize_write_buffer (c);
  if (log)
  {
    fprintf(log, "buf_size=%zu, write_buffer=%p, write_buffer_append_offset=%zu, write_buffer_size=%zu\n",
            buf_size, (void *)c->write_buffer, c->write_buffer_append_offset, c->write_buffer_size);
  }

  /* '5' is the minimal size of chunked footer ("0\r\n\r\n") */
  if (buf_size < 5)
  {
    if (log)
    {
      fprintf(log, "EXIT build_connection_chunked_response_footer: buf_size < 5 -> MHD_NO\n");
      fclose(log);
    }
    return MHD_NO;
  }
  mhd_assert (NULL != c->write_buffer);
  buf = c->write_buffer + c->write_buffer_append_offset;
  mhd_assert (NULL != buf);
  used_size = 0;
  buf[used_size++] = '0';
  buf[used_size++] = '\r';
  buf[used_size++] = '\n';

  if (log)
    fprintf(log, "after initial chunk header: used_size=%zu\n", used_size);

  for (pos = c->rp.response->first_header; NULL != pos; pos = pos->next)
  {
    if (MHD_FOOTER_KIND == pos->kind)
    {
      size_t new_used_size; /* resulting size with this header */
      /* '4' is colon, space, linefeeds */
      new_used_size = used_size + pos->header_size + pos->value_size + 4;
      if (log)
      {
        fprintf(log, "FOOTER found: pos=%p, header=%p, header_size=%zu, value=%p, value_size=%zu, used_size(before)=%zu, new_used_size=%zu, buf_size=%zu\n",
                (void *)pos, (void *)pos->header, pos->header_size, (void *)pos->value, pos->value_size, used_size, new_used_size, buf_size);
        if (pos->value != NULL && pos->value_size > 0)
        {
          size_t i, dump = pos->value_size < 16 ? pos->value_size : 16;
          fprintf(log, "value dump (first %zu bytes):", dump);
          for (i = 0; i < dump; ++i)
            fprintf(log, " %02x", (unsigned char)pos->value[i]);
          fprintf(log, "\n");
        }
      }

      if (new_used_size > buf_size)
      {
        if (log)
        {
          fprintf(log, "EXIT build_connection_chunked_response_footer: not enough buffer for this footer -> MHD_NO (new_used_size=%zu, buf_size=%zu)\n",
                  new_used_size, buf_size);
          fclose(log);
        }
        return MHD_NO;
      }
      memcpy (buf + used_size, pos->header, pos->header_size);
      used_size += pos->header_size;
      buf[used_size++] = ':';
      buf[used_size++] = ' ';
      memcpy (buf + used_size, pos->value, pos->value_size);
      used_size += pos->value_size;
      buf[used_size++] = '\r';
      buf[used_size++] = '\n';
      mhd_assert (used_size == new_used_size);

      if (log)
        fprintf(log, "after copying footer: used_size(after)=%zu\n", used_size);
    }
  }
  if (used_size + 2 > buf_size)
  {
    if (log)
    {
      fprintf(log, "EXIT build_connection_chunked_response_footer: not enough buffer for final CRLF -> MHD_NO (used_size=%zu, buf_size=%zu)\n",
              used_size, buf_size);
      fclose(log);
    }
    return MHD_NO;
  }
  buf[used_size++] = '\r';
  buf[used_size++] = '\n';

  c->write_buffer_append_offset += used_size;
  mhd_assert (c->write_buffer_append_offset <= c->write_buffer_size);

  if (log)
  {
    fprintf(log, "EXIT build_connection_chunked_response_footer: success -> MHD_YES, final used_size=%zu, new_append_offset=%zu\n",
            used_size, c->write_buffer_append_offset);
    fclose(log);
  }

  return MHD_YES;
}
