static enum MHD_Result
build_header_response (struct MHD_Connection *connection)
{
  struct MHD_Connection *const c = connection; /**< a short alias */
  struct MHD_Response *const r = c->rp.response; /**< a short alias */
  char *buf;                                     /**< the output buffer */
  size_t pos;                                    /**< append offset in the @a buf */
  size_t buf_size;                               /**< the size of the @a buf */
  size_t el_size;                                /**< the size of current element to be added to the @a buf */
  unsigned rcode;                                /**< the response code */
  bool use_conn_close;                           /**< Use "Connection: close" header */
  bool use_conn_k_alive;                         /**< Use "Connection: Keep-Alive" header */
  enum MHD_Result retval = MHD_NO; /* default, will be set before exit */
  FILE *logf = NULL;

  /* open log file early so we can record control flow */
  logf = fopen("/tmp/logs/455.txt", "a");
  if (logf)
  {
    fprintf(logf, "ENTER build_header_response: connection=%p, c=%p, r=%p\n",
            (void *)connection, (void *)c, (void *)r);
    /* Log key fields that demonstrate the alleged violation (reads only) */
    if (r)
    {
      fprintf(logf, " r->flags=0x%08x, r->flags_auto=0x%08x, r->upgrade_handler=%p, r->total_size=%llu, r->flags_insanity_content_length_check=%u\n",
              (unsigned) r->flags,
              (unsigned) r->flags_auto,
              (void *) r->upgrade_handler,
              (unsigned long long) r->total_size,
              (unsigned) (0 != (r->flags & MHD_RF_INSANITY_HEADER_CONTENT_LENGTH)));
    }
    if (c)
    {
      fprintf(logf, " c->keepalive=%d, c->rq.http_ver=%d, c->rp.responseCode=%u, c->rp.responseIcy=%d\n",
              (int) c->keepalive,
              (int) c->rq.http_ver,
              (unsigned) c->rp.responseCode,
              (int) c->rp.responseIcy);
      fprintf(logf, " c->rp.props.set=%d, chunked=%d, use_reply_body_headers=%d, send_reply_body=%d\n",
              (int) c->rp.props.set,
              (int) c->rp.props.chunked,
              (int) c->rp.props.use_reply_body_headers,
              (int) c->rp.props.send_reply_body);
    }
    fflush(logf);
  }

  mhd_assert (NULL != r);

  /* ** Adjust response properties ** */
  setup_reply_properties (c);

  mhd_assert (c->rp.props.set);
  mhd_assert ((MHD_CONN_MUST_CLOSE == c->keepalive) || \
              (MHD_CONN_USE_KEEPALIVE == c->keepalive) || \
              (MHD_CONN_MUST_UPGRADE == c->keepalive));
#ifdef UPGRADE_SUPPORT
  mhd_assert ((NULL == r->upgrade_handler) || \
              (MHD_CONN_MUST_UPGRADE == c->keepalive));
#else  /* ! UPGRADE_SUPPORT */
  mhd_assert (MHD_CONN_MUST_UPGRADE != c->keepalive);
#endif /* ! UPGRADE_SUPPORT */
  mhd_assert ((! c->rp.props.chunked) || c->rp.props.use_reply_body_headers);
  mhd_assert ((! c->rp.props.send_reply_body) || \
              c->rp.props.use_reply_body_headers);
#ifdef UPGRADE_SUPPORT
  mhd_assert (NULL == r->upgrade_handler || \
              ! c->rp.props.use_reply_body_headers);
#endif /* UPGRADE_SUPPORT */

  check_connection_reply (c);

  rcode = (unsigned) c->rp.responseCode;
  if (MHD_CONN_MUST_CLOSE == c->keepalive)
  {
    /* The closure of connection must be always indicated by header
     * to avoid hung connections */
    use_conn_close = true;
    use_conn_k_alive = false;
    if (logf) { fprintf(logf, "branch: MUST_CLOSE -> use_conn_close=1\n"); fflush(logf); }
  }
  else if (MHD_CONN_USE_KEEPALIVE == c->keepalive)
  {
    use_conn_close = false;
    /* Add "Connection: keep-alive" if request is HTTP/1.0 or
     * if reply is HTTP/1.0
     * For HTTP/1.1 add header only if explicitly requested by app
     * (by response flag), as "Keep-Alive" is default for HTTP/1.1. */
    if ((0 != (r->flags & MHD_RF_SEND_KEEP_ALIVE_HEADER)) ||
        (MHD_HTTP_VER_1_0 == c->rq.http_ver) ||
        (0 != (r->flags & MHD_RF_HTTP_1_0_SERVER)))
      use_conn_k_alive = true;
    else
      use_conn_k_alive = false;
    if (logf) { fprintf(logf, "branch: USE_KEEPALIVE -> use_conn_k_alive=%d\n", (int) use_conn_k_alive); fflush(logf); }
  }
  else
  {
    use_conn_close = false;
    use_conn_k_alive = false;
    if (logf) { fprintf(logf, "branch: other keepalive -> no connection header\n"); fflush(logf); }
  }

  /* ** Actually build the response header ** */

  /* Get all space available */
  connection_maximize_write_buffer (c);
  buf = c->write_buffer;
  pos = c->write_buffer_append_offset;
  buf_size = c->write_buffer_size;
  if (0 == buf_size)
  {
    if (logf) { fprintf(logf, "early exit: buf_size==0\n"); fflush(logf); }
    retval = MHD_NO;
    goto finish;
  }
  mhd_assert (NULL != buf);

  /* * The status line * */

  /* The HTTP version */
  if (! c->rp.responseIcy)
  { /* HTTP reply */
    if (0 == (r->flags & MHD_RF_HTTP_1_0_SERVER))
    { /* HTTP/1.1 reply */
      /* Use HTTP/1.1 responses for HTTP/1.0 clients.
       * See https://datatracker.ietf.org/doc/html/rfc7230#section-2.6 */
      if (! buffer_append_s (buf, &pos, buf_size, MHD_HTTP_VERSION_1_1))
      {
        if (logf) { fprintf(logf, "buffer_append_s failed: HTTP_VERSION_1_1\n"); fflush(logf); }
        retval = MHD_NO; goto finish;
      }
    }
    else
    { /* HTTP/1.0 reply */
      if (! buffer_append_s (buf, &pos, buf_size, MHD_HTTP_VERSION_1_0))
      {
        if (logf) { fprintf(logf, "buffer_append_s failed: HTTP_VERSION_1_0\n"); fflush(logf); }
        retval = MHD_NO; goto finish;
      }
    }
  }
  else
  { /* ICY reply */
    if (! buffer_append_s (buf, &pos, buf_size, "ICY"))
    {
      if (logf) { fprintf(logf, "buffer_append_s failed: ICY\n"); fflush(logf); }
      retval = MHD_NO; goto finish;
    }
  }

  /* The response code */
  if (buf_size < pos + 5) /* space + code + space */
  {
    if (logf) { fprintf(logf, "insufficient buffer for response code: pos=%zu, buf_size=%zu\n", pos, buf_size); fflush(logf); }
    retval = MHD_NO; goto finish;
  }
  buf[pos++] = ' ';
  pos += MHD_uint16_to_str ((uint16_t) rcode, buf + pos,
                            buf_size - pos);
  buf[pos++] = ' ';

  /* The reason phrase */
  el_size = MHD_get_reason_phrase_len_for (rcode);
  if (0 == el_size)
  {
    if (! buffer_append_s (buf, &pos, buf_size, "Non-Standard Status"))
    {
      if (logf) { fprintf(logf, "buffer_append_s failed: Non-Standard Status\n"); fflush(logf); }
      retval = MHD_NO; goto finish;
    }
  }
  else if (! buffer_append (buf, &pos, buf_size,
                            MHD_get_reason_phrase_for (rcode),
                            el_size))
  {
    if (logf) { fprintf(logf, "buffer_append failed: reason phrase\n"); fflush(logf); }
    retval = MHD_NO; goto finish;
  }

  /* The linefeed */
  if (buf_size < pos + 2)
  {
    if (logf) { fprintf(logf, "insufficient buffer for linefeed after reason phrase: pos=%zu, buf_size=%zu\n", pos, buf_size); fflush(logf); }
    retval = MHD_NO; goto finish;
  }
  buf[pos++] = '\r';
  buf[pos++] = '\n';

  /* * The headers * */

  /* Main automatic headers */

  /* The "Date:" header */
  if ( (0 == (r->flags_auto & MHD_RAF_HAS_DATE_HDR)) &&
       (0 == (c->daemon->options & MHD_USE_SUPPRESS_DATE_NO_CLOCK)) )
  {
    /* Additional byte for unused zero-termination */
    if (buf_size < pos + 38)
    {
      if (logf) { fprintf(logf, "insufficient buffer for date header: pos=%zu, buf_size=%zu\n", pos, buf_size); fflush(logf); }
      retval = MHD_NO; goto finish;
    }
    if (get_date_header (buf + pos))
      pos += 37;
  }
  /* The "Connection:" header */
  mhd_assert (! use_conn_close || ! use_conn_k_alive);
  mhd_assert (! use_conn_k_alive || ! use_conn_close);
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    if (use_conn_close)
    {
      if (! buffer_append_s (buf, &pos, buf_size,
                             MHD_HTTP_HEADER_CONNECTION ": close\r\n"))
      {
        if (logf) { fprintf(logf, "buffer_append_s failed: Connection: close\n"); fflush(logf); }
        retval = MHD_NO; goto finish;
      }
    }
    else if (use_conn_k_alive)
    {
      if (! buffer_append_s (buf, &pos, buf_size,
                             MHD_HTTP_HEADER_CONNECTION ": Keep-Alive\r\n"))
      {
        if (logf) { fprintf(logf, "buffer_append_s failed: Connection: Keep-Alive\n"); fflush(logf); }
        retval = MHD_NO; goto finish;
      }
    }
  }

  /* User-defined headers */

  if (! add_user_headers (buf, &pos, buf_size, r,
                          ! c->rp.props.chunked,
                          (! c->rp.props.use_reply_body_headers) &&
                          (0 ==
                           (r->flags & MHD_RF_INSANITY_HEADER_CONTENT_LENGTH)),
                          use_conn_close,
                          use_conn_k_alive))
  {
    if (logf) { fprintf(logf, "add_user_headers failed\n"); fflush(logf); }
    retval = MHD_NO; goto finish;
  }

  /* Other automatic headers */

  if ( (c->rp.props.use_reply_body_headers) &&
       (0 == (r->flags & MHD_RF_HEAD_ONLY_RESPONSE)) )
  {
    /* Body-specific headers */

    if (c->rp.props.chunked)
    { /* Chunked encoding is used */
      if (0 == (r->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED))
      { /* No chunked encoding header set by user */
        if (! buffer_append_s (buf, &pos, buf_size,
                               MHD_HTTP_HEADER_TRANSFER_ENCODING ": " \
                               "chunked\r\n"))
        {
          if (logf) { fprintf(logf, "buffer_append_s failed: transfer-encoding chunked\n"); fflush(logf); }
          retval = MHD_NO; goto finish;
        }
      }
    }
    else /* Chunked encoding is not used */
    {
      if (MHD_SIZE_UNKNOWN != r->total_size)
      { /* The size is known */
        if (0 == (r->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH))
        { /* The response does not have "Content-Length" header */
          if (! buffer_append_s (buf, &pos, buf_size,
                                 MHD_HTTP_HEADER_CONTENT_LENGTH ": "))
          {
            if (logf) { fprintf(logf, "buffer_append_s failed: Content-Length header start\n"); fflush(logf); }
            retval = MHD_NO; goto finish;
          }
          el_size = MHD_uint64_to_str (r->total_size, buf + pos,
                                       buf_size - pos);
          if (0 == el_size)
          {
            if (logf) { fprintf(logf, "MHD_uint64_to_str failed for total_size=%llu\n", (unsigned long long) r->total_size); fflush(logf); }
            retval = MHD_NO; goto finish;
          }
          pos += el_size;

          if (buf_size < pos + 2)
          {
            if (logf) { fprintf(logf, "insufficient buffer for CRLF after content length: pos=%zu, buf_size=%zu\n", pos, buf_size); fflush(logf); }
            retval = MHD_NO; goto finish;
          }
          buf[pos++] = '\r';
          buf[pos++] = '\n';
        }
      }
    }
  }

  /* * Header termination * */
  if (buf_size < pos + 2)
  {
    if (logf) { fprintf(logf, "insufficient buffer for header termination: pos=%zu, buf_size=%zu\n", pos, buf_size); fflush(logf); }
    retval = MHD_NO; goto finish;
  }
  buf[pos++] = '\r';
  buf[pos++] = '\n';

  c->write_buffer_append_offset = pos;
  retval = MHD_YES;

finish:
  if (logf)
  {
    fprintf(logf, "EXIT build_header_response: retval=%d, final pos=%zu, buf_size=%zu, c->write_buffer_append_offset=%zu\n",
            (int) retval, (size_t) pos, (size_t) buf_size, (size_t) c->write_buffer_append_offset);
    /* Re-log some read-only fields to show they were not modified here */
    if (r)
      fprintf(logf, " final r->flags=0x%08x, r->flags_auto=0x%08x, r->upgrade_handler=%p, r->total_size=%llu\n",
              (unsigned) r->flags,
              (unsigned) r->flags_auto,
              (void *) r->upgrade_handler,
              (unsigned long long) r->total_size);
    fflush(logf);
    fclose(logf);
  }
  return retval;
}
