_MHD_static_inline void
calc_userdigest (struct DigestAlgorithm *da,
                 const char *username, const size_t username_len,
                 const char *realm, const size_t realm_len,
                 const char *password,
                 uint8_t *ha1_bin)
{
  /* Logging probe: write control flow and state to /tmp/logs/465.txt */
  FILE *log = fopen("/tmp/logs/465.txt", "a");
  if (log != NULL)
  {
    fprintf(log, "calc_userdigest: entry\n");
    fprintf(log, "  da=%p\n", (void *)da);
    fprintf(log, "  username=%p username_len=%zu\n", (const void *)username, username_len);
    fprintf(log, "  realm=%p realm_len=%zu\n", (const void *)realm, realm_len);
    fprintf(log, "  password=%p\n", (const void *)password);

    /* Print username content up to username_len as readable chars (non-printable in hex) */
    fprintf(log, "  username content: '");
    if (username != NULL)
    {
      size_t i;
      for (i = 0U; i < username_len; ++i)
      {
        unsigned char c = (unsigned char)username[i];
        if (c >= 32U && c < 127U)
        {
          fputc((int)c, log);
        }
        else
        {
          fprintf(log, "\\x%02x", (unsigned int)c);
        }
      }
    }
    else
    {
      fprintf(log, "(null)");
    }
    fprintf(log, "'\n");

    /* Print realm content up to realm_len similarly */
    fprintf(log, "  realm content: '");
    if (realm != NULL)
    {
      size_t i;
      for (i = 0U; i < realm_len; ++i)
      {
        unsigned char c = (unsigned char)realm[i];
        if (c >= 32U && c < 127U)
        {
          fputc((int)c, log);
        }
        else
        {
          fprintf(log, "\\x%02x", (unsigned int)c);
        }
      }
    }
    else
    {
      fprintf(log, "(null)");
    }
    fprintf(log, "'\n");

    /* Print a safe prefix of password (up to 64 bytes or until NUL) to avoid over-read */
    fprintf(log, "  password preview: '");
    if (password != NULL)
    {
      size_t max_preview = 64U;
      size_t i = 0U;
      for (i = 0U; i < max_preview; ++i)
      {
        unsigned char c = (unsigned char)password[i];
        if (c == 0U)
        {
          break; /* stop at NUL */
        }
        if (c >= 32U && c < 127U)
        {
          fputc((int)c, log);
        }
        else
        {
          fprintf(log, "\\x%02x", (unsigned int)c);
        }
      }
      if (i == 0U)
      {
        /* empty string */
      }
      else if (i >= max_preview && password[max_preview] != 0)
      {
        fprintf(log, " (truncated)");
      }
    }
    else
    {
      fprintf(log, "(null)");
    }
    fprintf(log, "'\n");

    fclose(log);
  }

  /* Preserve original behavior */
  mhd_assert (! da->hashing);
  digest_update (da, username, username_len);
  digest_update_with_colon (da);
  digest_update (da, realm, realm_len);
  digest_update_with_colon (da);
  /* The suspected MISRA violation occurs here if digest_update_str takes a non-const pointer. */
  digest_update_str (da, password);
  digest_calc_hash (da, ha1_bin);
}
