static void
calculate_nonce (uint64_t nonce_time,
                 enum MHD_HTTP_Method mthd_e,
                 const char *method,
                 const char *rnd,
                 size_t rnd_size,
                 const struct sockaddr_storage *saddr,
                 size_t saddr_size,
                 const char *uri,
                 size_t uri_len,
                 const struct MHD_HTTP_Req_Header *first_header,
                 const char *realm,
                 size_t realm_len,
                 unsigned int bind_options,
                 struct DigestAlgorithm *da,
                 char *nonce)
{
  /* Probe/log file to capture control flow and state for the violation scenario. */
  FILE *log = fopen("/tmp/logs/1789.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER calculate_nonce: nonce_time=%llu, rnd_size=%zu, saddr_size=%zu, uri_len=%zu, realm_len=%zu, bind_options=0x%08x\n",
            (unsigned long long) nonce_time,
            rnd_size,
            saddr_size,
            uri_len,
            realm_len,
            bind_options);
    fflush(log);
  }

  mhd_assert (! da->hashing);
  if (1)
  {
    if (log)
    {
      fprintf(log, "Taking invariant if(1) -- timestamp/hash update block (this demonstrates the invariant controlling expression)\n");
      fflush(log);
    }

    /* Add the timestamp to the hash calculation */
    uint8_t timestamp[TIMESTAMP_BIN_SIZE];
    /* If the nonce_time is milliseconds, then the same 48 bit value will repeat
     * every 8 919 years, which is more than enough to mitigate a replay attack */
#if TIMESTAMP_BIN_SIZE != 6
#error The code needs to be updated here
#endif
    timestamp[0] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 0)));
    timestamp[1] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 1)));
    timestamp[2] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 2)));
    timestamp[3] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 3)));
    timestamp[4] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 4)));
    timestamp[5] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 5)));

    if (log)
    {
      size_t _i;
      fprintf(log, "Computed timestamp bytes:");
      for (_i = 0; _i < (size_t) TIMESTAMP_BIN_SIZE; ++_i)
        fprintf(log, " %02x", (unsigned) timestamp[_i]);
      fprintf(log, "\n");
      fprintf(log, "Timestamp will be hex-encoded at nonce offset %zu (digest_get_size(da)*2)\n", (size_t) digest_get_size(da) * 2);
      fflush(log);
    }

    MHD_bin_to_hex (timestamp,
                    sizeof (timestamp),
                    nonce + digest_get_size (da) * 2);
    digest_update (da,
                   timestamp,
                   sizeof (timestamp));

    if (log)
    {
      fprintf(log, "Completed timestamp update into digest.\n");
      fflush(log);
    }
  }
  if (rnd_size > 0)
  {
    if (log)
    {
      fprintf(log, "rnd_size > 0: adding random data to hash, rnd_size=%zu\n", rnd_size);
      fflush(log);
    }
    /* Add the unique random value to the hash calculation */
    digest_update_with_colon (da);
    digest_update (da,
                   rnd,
                   rnd_size);
  }
  else
  {
    if (log)
    {
      fprintf(log, "rnd_size == 0: skipping random data in hash\n");
      fflush(log);
    }
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) &&
       (0 != saddr_size) )
  {
    if (log)
    {
      fprintf(log, "Binding mode NONE and saddr_size != 0: adding full client address to hash (saddr_size=%zu)\n", saddr_size);
      fflush(log);
    }
    /* Add full client address including source port to make unique nonces
     * for requests received exactly at the same time */
    digest_update_with_colon (da);
    digest_update (da,
                   saddr,
                   saddr_size);
  }
  if ( (0 != (bind_options & MHD_DAUTH_BIND_NONCE_CLIENT_IP)) &&
       (0 != saddr_size) )
  {
    if (log)
    {
      fprintf(log, "Binding includes CLIENT_IP and saddr_size != 0: adding client IP to hash\n");
      fflush(log);
    }
    /* Add the client's IP address to the hash calculation */
    digest_update_with_colon (da);
    if (AF_INET == saddr->ss_family)
      digest_update (da,
                     &((const struct sockaddr_in *) saddr)->sin_addr,
                     sizeof(((const struct sockaddr_in *) saddr)->sin_addr));
#ifdef HAVE_INET6
    else if (AF_INET6 == saddr->ss_family)
      digest_update (da,
                     &((const struct sockaddr_in6 *) saddr)->sin6_addr,
                     sizeof(((const struct sockaddr_in6 *) saddr)->sin6_addr));
#endif /* HAVE_INET6 */
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI)))
  {
    if (log)
    {
      fprintf(log, "Including method in hash. mthd_e=%d\n", (int) mthd_e);
      fflush(log);
    }
    /* Add the request method to the hash calculation */
    digest_update_with_colon (da);
    if (MHD_HTTP_MTHD_OTHER != mthd_e)
    {
      uint8_t mthd_for_hash;
      if (MHD_HTTP_MTHD_HEAD != mthd_e)
        mthd_for_hash = (uint8_t) mthd_e;
      else /* Treat HEAD method in the same way as GET method */
        mthd_for_hash = (uint8_t) MHD_HTTP_MTHD_GET;
      digest_update (da,
                     &mthd_for_hash,
                     sizeof(mthd_for_hash));
    }
    else
      digest_update_str (da, method);
  }

  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI))
  {
    if (log)
    {
      fprintf(log, "Including URI in hash: uri_len=%zu\n", uri_len);
      fflush(log);
    }
    /* Add the request URI to the hash calculation */
    digest_update_with_colon (da);

    digest_update (da,
                   uri,
                   uri_len);
  }
  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI_PARAMS))
  {
    /* Add the request URI parameters to the hash calculation */
    const struct MHD_HTTP_Req_Header *h;

    if (log)
    {
      fprintf(log, "Including URI params in hash: iterating request headers\n");
      fflush(log);
    }

    digest_update_with_colon (da);
    for (h = first_header; NULL != h; h = h->next)
    {
      if (MHD_GET_ARGUMENT_KIND != h->kind)
        continue;
      digest_update (da, "\0", 2);
      if (0 != h->header_size)
        digest_update (da, h->header, h->header_size);
      digest_update (da, "", 1);
      if (0 != h->value_size)
        digest_update (da, h->value, h->value_size);
    }
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_REALM)))
  {
    if (log)
    {
      fprintf(log, "Including realm in hash: realm_len=%zu\n", realm_len);
      fflush(log);
    }
    /* Add the realm to the hash calculation */
    digest_update_with_colon (da);
    digest_update (da,
                   realm,
                   realm_len);
  }
  if (1)
  {
    if (log)
    {
      fprintf(log, "Taking invariant if(1) -- final hash calculation block (this demonstrates the invariant controlling expression)\n");
      fflush(log);
    }
    uint8_t hash[MAX_DIGEST];
    digest_calc_hash (da, hash);
    MHD_bin_to_hex (hash,
                    digest_get_size (da),
                    nonce);

    if (log)
    {
      size_t _ds = digest_get_size(da);
      fprintf(log, "Computed hash (first %zu bytes -> %zu hex chars): ", _ds, _ds * 2);
      /* write the nonce hex string as binary to the log so we capture exact output */
      fwrite(nonce, 1, _ds * 2, log);
      fprintf(log, "\n");
      fflush(log);
    }
  }

  if (log)
  {
    fprintf(log, "LEAVE calculate_nonce\n");
    fclose(log);
  }
}
