static void
calculate_nonce (uint64_t nonce_time,
                 enum MHD_HTTP_Method mthd_e,
                 const char *method,
                 const char *rnd,
                 size_t rnd_size,
                 const struct sockaddr_storage *saddr,
                 size_t saddr_size,
                 const char *uri,
                 size_t uri_len,
                 const struct MHD_HTTP_Req_Header *first_header,
                 const char *realm,
                 size_t realm_len,
                 unsigned int bind_options,
                 struct DigestAlgorithm *da,
                 char *nonce)
{
  /* Preserve original assertion */
  mhd_assert (! da->hashing);

  /* Log entry to help reproduce/control the violation scenario. Append mode
     so multiple calls are recorded. */
  {
    FILE *log = fopen("/tmp/logs/1783.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "ENTER calculate_nonce: nonce_time=%llu, rnd_size=%zu, saddr=%p, saddr_size=%zu, bind_options=0x%x, uri_len=%zu, realm_len=%zu\n",
              (unsigned long long) nonce_time,
              rnd_size,
              (const void *) saddr,
              saddr_size,
              (unsigned int) bind_options,
              uri_len,
              realm_len);
      fclose(log);
    }
  }

  if (1)
  {
    /* Add the timestamp to the hash calculation */
    uint8_t timestamp[TIMESTAMP_BIN_SIZE];
    /* If the nonce_time is milliseconds, then the same 48 bit value will repeat
     * every 8 919 years, which is more than enough to mitigate a replay attack */
#if TIMESTAMP_BIN_SIZE != 6
#error The code needs to be updated here
#endif
    timestamp[0] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 0)));
    timestamp[1] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 1)));
    timestamp[2] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 2)));
    timestamp[3] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 3)));
    timestamp[4] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 4)));
    timestamp[5] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 5)));
    MHD_bin_to_hex (timestamp,
                    sizeof (timestamp),
                    nonce + digest_get_size (da) * 2);
    digest_update (da,
                   timestamp,
                   sizeof (timestamp));
  }
  if (rnd_size > 0)
  {
    /* Add the unique random value to the hash calculation */
    digest_update_with_colon (da);
    digest_update (da,
                   rnd,
                   rnd_size);
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) &&
       (0 != saddr_size) )
  {
    /* Add full client address including source port to make unique nonces
     * for requests received exactly at the same time */
    /* Log what will be included */
    {
      FILE *log = fopen("/tmp/logs/1783.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "branch: bind none + full saddr: saddr=%p, saddr_size=%zu\n",
                (const void *) saddr,
                saddr_size);
        fclose(log);
      }
    }

    digest_update_with_colon (da);
    digest_update (da,
                   saddr,
                   saddr_size);
  }
  if ( (0 != (bind_options & MHD_DAUTH_BIND_NONCE_CLIENT_IP)) &&
       (0 != saddr_size) )
  {
    /* Add the client's IP address to the hash calculation */
    digest_update_with_colon (da);

    /* Log the saddr family and sizes before performing the same accesses as
       in the original code to capture the state that can lead to an
       out-of-bounds read when saddr_size is smaller than expected. */
    {
      FILE *log = fopen("/tmp/logs/1783.txt", "a");
      if (NULL != log)
      {
        if (NULL == saddr)
        {
          fprintf(log, "WARNING: saddr is NULL but saddr_size=%zu\n", saddr_size);
        }
        else
        {
          /* Note: do not dereference beyond saddr->ss_family; reading this
             field is the intended behavior of the original code. */
          fprintf(log, "client_ip branch: saddr=%p, saddr_size=%zu, ss_family=%d\n",
                  (const void *) saddr,
                  saddr_size,
                  (int) saddr->ss_family);

#ifdef HAVE_INET6
          if (AF_INET == saddr->ss_family)
          {
            fprintf(log, "AF_INET path: will read sizeof(sin_addr)=%zu bytes from &((struct sockaddr_in*)saddr)->sin_addr\n",
                    sizeof(((const struct sockaddr_in *) saddr)->sin_addr));
          }
          else if (AF_INET6 == saddr->ss_family)
          {
            fprintf(log, "AF_INET6 path: will read sizeof(sin6_addr)=%zu bytes from &((struct sockaddr_in6*)saddr)->sin6_addr\n",
                    sizeof(((const struct sockaddr_in6 *) saddr)->sin6_addr));
            /* Additional diagnostic: compare supplied saddr_size to the
               expected size for a full sockaddr_in6. This mirrors the
               condition that leads to the reported violation (truncated
               buffer). We intentionally do NOT change behavior; only log. */
            fprintf(log, "diagnostic: supplied saddr_size=%zu, sizeof(struct sockaddr_in6)=%zu\n",
                    saddr_size,
                    sizeof (struct sockaddr_in6));
          }
          else
          {
            fprintf(log, "unknown ss_family=%d\n", (int) saddr->ss_family);
          }
#endif /* HAVE_INET6 */
        }
        fclose(log);
      }
    }

    if (AF_INET == saddr->ss_family)
      digest_update (da,
                     &((const struct sockaddr_in *) saddr)->sin_addr,
                     sizeof(((const struct sockaddr_in *) saddr)->sin_addr));
#ifdef HAVE_INET6
    else if (AF_INET6 == saddr->ss_family)
      digest_update (da,
                     &((const struct sockaddr_in6 *) saddr)->sin6_addr,
                     sizeof(((const struct sockaddr_in6 *) saddr)->sin6_addr));
#endif /* HAVE_INET6 */
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI)))
  {
    /* Add the request method to the hash calculation */
    digest_update_with_colon (da);
    if (MHD_HTTP_MTHD_OTHER != mthd_e)
    {
      uint8_t mthd_for_hash;
      if (MHD_HTTP_MTHD_HEAD != mthd_e)
        mthd_for_hash = (uint8_t) mthd_e;
      else /* Treat HEAD method in the same way as GET method */
        mthd_for_hash = (uint8_t) MHD_HTTP_MTHD_GET;
      digest_update (da,
                     &mthd_for_hash,
                     sizeof(mthd_for_hash));
    }
    else
      digest_update_str (da, method);
  }

  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI))
  {
    /* Add the request URI to the hash calculation */
    digest_update_with_colon (da);

    digest_update (da,
                   uri,
                   uri_len);
  }
  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI_PARAMS))
  {
    /* Add the request URI parameters to the hash calculation */
    const struct MHD_HTTP_Req_Header *h;

    digest_update_with_colon (da);
    for (h = first_header; NULL != h; h = h->next)
    {
      if (MHD_GET_ARGUMENT_KIND != h->kind)
        continue;
      digest_update (da, "\0", 2);
      if (0 != h->header_size)
        digest_update (da, h->header, h->header_size);
      digest_update (da, "", 1);
      if (0 != h->value_size)
        digest_update (da, h->value, h->value_size);
    }
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_REALM)))
  {
    /* Add the realm to the hash calculation */
    digest_update_with_colon (da);
    digest_update (da,
                   realm,
                   realm_len);
  }
  if (1)
  {
    uint8_t hash[MAX_DIGEST];
    digest_calc_hash (da, hash);
    MHD_bin_to_hex (hash,
                    digest_get_size (da),
                    nonce);

    /* Log completion and the resulting nonce prefix (hex of hash) so tests
       can correlate inputs to outputs. We log only the prefix to avoid
       leaking the full secret in logs if any. */
    {
      FILE *log = fopen("/tmp/logs/1783.txt", "a");
      if (NULL != log)
      {
        /* Print first up to 32 chars of nonce for diagnostic purposes */
        size_t show = 32;
        if (show > strlen(nonce))
          show = strlen(nonce);
        fprintf(log, "EXIT calculate_nonce: nonce_prefix_len=%zu, nonce_prefix=\"");
        if (show > 0)
        {
          /* Write the prefix safely */
          fwrite(nonce, 1, show, log);
        }
        fprintf(log, "\"\n");
        fclose(log);
      }
    }
  }
}
