static void
calculate_nonce (uint64_t nonce_time,
                 enum MHD_HTTP_Method mthd_e,
                 const char *method,
                 const char *rnd,
                 size_t rnd_size,
                 const struct sockaddr_storage *saddr,
                 size_t saddr_size,
                 const char *uri,
                 size_t uri_len,
                 const struct MHD_HTTP_Req_Header *first_header,
                 const char *realm,
                 size_t realm_len,
                 unsigned int bind_options,
                 struct DigestAlgorithm *da,
                 char *nonce)
{
  /* Enter probe: log entrance and key parameters */
  {
    FILE *log = fopen("/tmp/logs/1783.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "ENTER calculate_nonce: this=%p nonce=%p nonce_time=%llu mthd_e=%d method=%p rnd=%p rnd_size=%zu saddr=%p saddr_size=%zu uri=%p uri_len=%zu first_header=%p realm=%p realm_len=%zu bind_options=0x%X da=%p\n",
              (void *) &nonce, (void *) nonce,
              (unsigned long long) nonce_time,
              (int) mthd_e,
              (void *) method, (void *) rnd, rnd_size,
              (void *) saddr, saddr_size,
              (void *) uri, uri_len,
              (void *) first_header,
              (void *) realm, realm_len,
              (unsigned) bind_options,
              (void *) da);
      /* If we can, log the digest size reported by the algorithm */
      if (NULL != da)
        fprintf(log, "  digest_get_size(da)=%zu TIMESTAMP_BIN_SIZE=%d sizeof(timestamp)=%zu\n",
                (size_t) digest_get_size(da), (int) TIMESTAMP_BIN_SIZE, (size_t) sizeof(uint8_t) * (size_t) TIMESTAMP_BIN_SIZE);
      fclose(log);
    }
  }

  mhd_assert (! da->hashing);
  if (1)
  {
    /* Add the timestamp to the hash calculation */
    uint8_t timestamp[TIMESTAMP_BIN_SIZE];
    /* If the nonce_time is milliseconds, then the same 48 bit value will repeat
     * every 8 919 years, which is more than enough to mitigate a replay attack */
#if TIMESTAMP_BIN_SIZE != 6
#error The code needs to be updated here
#endif
    timestamp[0] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 0)));
    timestamp[1] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 1)));
    timestamp[2] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 2)));
    timestamp[3] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 3)));
    timestamp[4] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 4)));
    timestamp[5] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 5)));

    /* Log details about the upcoming write of the timestamp hex representation. */
    {
      FILE *log = fopen("/tmp/logs/1783.txt", "a");
      if (NULL != log)
      {
        size_t ts_bin_size = sizeof(timestamp);
        size_t ts_hex_len = ts_bin_size * 2; /* two hex chars per byte */
        size_t dg_sz = digest_get_size(da);
        size_t dest_offset = dg_sz * 2; /* digest hex already occupies this many bytes */
        void *dest_addr = (void *) (nonce + dest_offset);
        fprintf(log, "MHD_bin_to_hex(timestamp): ts_bin_size=%zu ts_hex_len=%zu digest_get_size=%zu dest_offset=%zu nonce_ptr=%p dest_addr=%p will_write_end=%p\n",
                ts_bin_size, ts_hex_len, dg_sz, dest_offset,
                (void *) nonce, dest_addr, (void *) ((char *) dest_addr + (ts_hex_len ? (ts_hex_len - 1) : 0)));
        fclose(log);
      }
    }

    MHD_bin_to_hex (timestamp,
                    sizeof (timestamp),
                    nonce + digest_get_size (da) * 2);
    digest_update (da,
                   timestamp,
                   sizeof (timestamp));

    /* Log that timestamp hex write completed (we do not probe the destination contents to avoid further UB) */
    {
      FILE *log = fopen("/tmp/logs/1783.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "After MHD_bin_to_hex(timestamp): wrote timestamp hex at nonce+%zu (no read performed)\n",
                (size_t) (digest_get_size(da) * 2));
        fclose(log);
      }
    }
  }
  if (rnd_size > 0)
  {
    /* Add the unique random value to the hash calculation */
    digest_update_with_colon (da);
    digest_update (da,
                   rnd,
                   rnd_size);
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) &&
       (0 != saddr_size) )
  {
    /* Add full client address including source port to make unique nonces
     * for requests received exactly at the same time */
    digest_update_with_colon (da);
    digest_update (da,
                   saddr,
                   saddr_size);
  }
  if ( (0 != (bind_options & MHD_DAUTH_BIND_NONCE_CLIENT_IP)) &&
       (0 != saddr_size) )
  {
    /* Add the client's IP address to the hash calculation */
    digest_update_with_colon (da);
    if (AF_INET == saddr->ss_family)
      digest_update (da,
                     &((const struct sockaddr_in *) saddr)->sin_addr,
                     sizeof(((const struct sockaddr_in *) saddr)->sin_addr));
#ifdef HAVE_INET6
    else if (AF_INET6 == saddr->ss_family)
      digest_update (da,
                     &((const struct sockaddr_in6 *) saddr)->sin6_addr,
                     sizeof(((const struct sockaddr_in6 *) saddr)->sin6_addr));
#endif /* HAVE_INET6 */
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI)))
  {
    /* Add the request method to the hash calculation */
    digest_update_with_colon (da);
    if (MHD_HTTP_MTHD_OTHER != mthd_e)
    {
      uint8_t mthd_for_hash;
      if (MHD_HTTP_MTHD_HEAD != mthd_e)
        mthd_for_hash = (uint8_t) mthd_e;
      else /* Treat HEAD method in the same way as GET method */
        mthd_for_hash = (uint8_t) MHD_HTTP_MTHD_GET;
      digest_update (da,
                     &mthd_for_hash,
                     sizeof(mthd_for_hash));
    }
    else
      digest_update_str (da, method);
  }

  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI))
  {
    /* Add the request URI to the hash calculation */
    digest_update_with_colon (da);

    digest_update (da,
                   uri,
                   uri_len);
  }
  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI_PARAMS))
  {
    /* Add the request URI parameters to the hash calculation */
    const struct MHD_HTTP_Req_Header *h;

    digest_update_with_colon (da);
    for (h = first_header; NULL != h; h = h->next)
    {
      if (MHD_GET_ARGUMENT_KIND != h->kind)
        continue;
      digest_update (da, "\0", 2);
      if (0 != h->header_size)
        digest_update (da, h->header, h->header_size);
      digest_update (da, "", 1);
      if (0 != h->value_size)
        digest_update (da, h->value, h->value_size);
    }
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_REALM)))
  {
    /* Add the realm to the hash calculation */
    digest_update_with_colon (da);
    digest_update (da,
                   realm,
                   realm_len);
  }
  if (1)
  {
    uint8_t hash[MAX_DIGEST];
    digest_calc_hash (da, hash);

    /* Log details about the upcoming write of the hash hex representation. */
    {
      FILE *log = fopen("/tmp/logs/1783.txt", "a");
      if (NULL != log)
      {
        size_t dg_sz = digest_get_size(da);
        size_t hash_bin_size = dg_sz;
        size_t hash_hex_len = hash_bin_size * 2;
        fprintf(log, "MHD_bin_to_hex(hash): hash_bin_size=%zu hash_hex_len=%zu digest_get_size=%zu nonce_ptr=%p will_write_end=%p\n",
                hash_bin_size, hash_hex_len, dg_sz, (void *) nonce, (void *) ((char *) nonce + (hash_hex_len ? (hash_hex_len - 1) : 0)));
        fclose(log);
      }
    }

    MHD_bin_to_hex (hash,
                    digest_get_size (da),
                    nonce);

    /* Final log: indicate completion */
    {
      FILE *log = fopen("/tmp/logs/1783.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "EXIT calculate_nonce: wrote hash hex at nonce (no read performed).\n");
        fclose(log);
      }
    }
  }
}
