static void
calculate_nonce (uint64_t nonce_time,
                 enum MHD_HTTP_Method mthd_e,
                 const char *method,
                 const char *rnd,
                 size_t rnd_size,
                 const struct sockaddr_storage *saddr,
                 size_t saddr_size,
                 const char *uri,
                 size_t uri_len,
                 const struct MHD_HTTP_Req_Header *first_header,
                 const char *realm,
                 size_t realm_len,
                 unsigned int bind_options,
                 struct DigestAlgorithm *da,
                 char *nonce)
{
  /* Logging probe: append-only so multiple calls accumulate evidence */
  FILE *log = fopen("/tmp/logs/1785.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER calculate_nonce: nonce_time=%llu mthd_e=%d method=%p rnd=%p rnd_size=%zu saddr=%p saddr_size=%zu uri=%p uri_len=%zu first_header=%p realm=%p realm_len=%zu bind_options=0x%X da=%p nonce=%p\n",
            (unsigned long long) nonce_time,
            (int) mthd_e,
            (const void *) method,
            (const void *) rnd,
            rnd_size,
            (const void *) saddr,
            saddr_size,
            (const void *) uri,
            uri_len,
            (const void *) first_header,
            (const void *) realm,
            realm_len,
            (unsigned int) bind_options,
            (void *) da,
            (void *) nonce);
    fflush(log);
  }

  mhd_assert (! da->hashing);
  if (1)
  {
    /* Add the timestamp to the hash calculation */
    uint8_t timestamp[TIMESTAMP_BIN_SIZE];
    /* If the nonce_time is milliseconds, then the same 48 bit value will repeat
     * every 8 919 years, which is more than enough to mitigate a replay attack */
#if TIMESTAMP_BIN_SIZE != 6
#error The code needs to be updated here
#endif
    timestamp[0] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 0)));
    timestamp[1] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 1)));
    timestamp[2] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 2)));
    timestamp[3] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 3)));
    timestamp[4] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 4)));
    timestamp[5] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 5)));
    if (log)
    {
      fprintf(log, " timestamp bytes: %02x %02x %02x %02x %02x %02x\n",
              timestamp[0], timestamp[1], timestamp[2], timestamp[3], timestamp[4], timestamp[5]);
      fflush(log);
    }
    MHD_bin_to_hex (timestamp,
                    sizeof (timestamp),
                    nonce + digest_get_size (da) * 2);
    digest_update (da,
                   timestamp,
                   sizeof (timestamp));
  }
  if (rnd_size > 0)
  {
    /* Add the unique random value to the hash calculation */
    if (log)
    {
      fprintf(log, " rnd present: rnd=%p rnd_size=%zu -> calling digest_update with rnd\n",
              (const void *) rnd, rnd_size);
      fflush(log);
    }
    digest_update_with_colon (da);
    digest_update (da,
                   rnd,
                   rnd_size);
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) &&
       (0 != saddr_size) )
  {
    /* Add full client address including source port to make unique nonces
     * for requests received exactly at the same time */
    if (log)
    {
      fprintf(log, " bind none & saddr_size != 0: saddr=%p saddr_size=%zu -> adding full client address\n",
              (const void *) saddr, saddr_size);
      fflush(log);
    }
    digest_update_with_colon (da);
    digest_update (da,
                   saddr,
                   saddr_size);
  }
  if ( (0 != (bind_options & MHD_DAUTH_BIND_NONCE_CLIENT_IP)) &&
       (0 != saddr_size) )
  {
    /* Add the client's IP address to the hash calculation */
    if (log)
    {
      fprintf(log, " bind by client IP & saddr_size != 0: saddr=%p family=%d\n",
              (const void *) saddr, (saddr ? (int) saddr->ss_family : -1));
      fflush(log);
    }
    digest_update_with_colon (da);
    if (AF_INET == saddr->ss_family)
      digest_update (da,
                     &((const struct sockaddr_in *) saddr)->sin_addr,
                     sizeof(((const struct sockaddr_in *) saddr)->sin_addr));
#ifdef HAVE_INET6
    else if (AF_INET6 == saddr->ss_family)
      digest_update (da,
                     &((const struct sockaddr_in6 *) saddr)->sin6_addr,
                     sizeof(((const struct sockaddr_in6 *) saddr)->sin6_addr));
#endif /* HAVE_INET6 */
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI)))
  {
    /* Add the request method to the hash calculation */
    if (log)
    {
      fprintf(log, " adding method to hash: mthd_e=%d (OTHER=%d HEAD=%d GET=%d) method_ptr=%p\n",
              (int) mthd_e,
              (int) MHD_HTTP_MTHD_OTHER,
              (int) MHD_HTTP_MTHD_HEAD,
              (int) MHD_HTTP_MTHD_GET,
              (const void *) method);
      fflush(log);
    }
    digest_update_with_colon (da);
    if (MHD_HTTP_MTHD_OTHER != mthd_e)
    {
      uint8_t mthd_for_hash;
      if (MHD_HTTP_MTHD_HEAD != mthd_e)
        mthd_for_hash = (uint8_t) mthd_e;
      else /* Treat HEAD method in the same way as GET method */
        mthd_for_hash = (uint8_t) MHD_HTTP_MTHD_GET;
      digest_update (da,
                     &mthd_for_hash,
                     sizeof(mthd_for_hash));
    }
    else
    {
      if (log)
      {
        fprintf(log, "  method is OTHER, calling digest_update_str with method='%s'\n",
                (method ? method : "(null)"));
        fflush(log);
      }
      digest_update_str (da, method);
    }
  }

  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI))
  {
    /* Add the request URI to the hash calculation */
    if (log)
    {
      fprintf(log, " adding URI: uri=%p uri_len=%zu\n",
              (const void *) uri, uri_len);
      fflush(log);
    }
    digest_update_with_colon (da);

    digest_update (da,
                   uri,
                   uri_len);
  }
  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI_PARAMS))
  {
    /* Add the request URI parameters to the hash calculation */
    const struct MHD_HTTP_Req_Header *h;

    if (log)
    {
      fprintf(log, " adding URI params: iterate headers starting at %p\n",
              (const void *) first_header);
      fflush(log);
    }
    digest_update_with_colon (da);
    for (h = first_header; NULL != h; h = h->next)
    {
      if (MHD_GET_ARGUMENT_KIND != h->kind)
        continue;
      if (log)
      {
        fprintf(log, "  header node: addr=%p kind=%d header=%p header_size=%zu value=%p value_size=%zu next=%p\n",
                (const void *) h,
                (int) h->kind,
                (const void *) h->header,
                (size_t) h->header_size,
                (const void *) h->value,
                (size_t) h->value_size,
                (const void *) h->next);
        fflush(log);
      }
      digest_update (da, "\0", 2);
      if (0 != h->header_size)
        digest_update (da, h->header, h->header_size);
      digest_update (da, "", 1);
      if (0 != h->value_size)
        digest_update (da, h->value, h->value_size);
    }
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_REALM)))
  {
    /* Add the realm to the hash calculation */
    if (log)
    {
      fprintf(log, " adding realm: realm=%p realm_len=%zu\n",
              (const void *) realm, realm_len);
      fflush(log);
    }
    digest_update_with_colon (da);
    digest_update (da,
                   realm,
                   realm_len);
  }
  if (1)
  {
    uint8_t hash[MAX_DIGEST];
    if (log)
    {
      fprintf(log, " finalizing hash (digest_calc_hash)\n");
      fflush(log);
    }
    digest_calc_hash (da, hash);
    MHD_bin_to_hex (hash,
                    digest_get_size (da),
                    nonce);
    if (log)
    {
      fprintf(log, " finalized nonce (hex) stored at %p, first bytes: %02x %02x %02x %02x\n",
              (void *) nonce,
              (unsigned) (unsigned char) hash[0],
              (unsigned) (unsigned char) hash[1],
              (unsigned) (unsigned char) hash[2],
              (unsigned) (unsigned char) hash[3]);
      fflush(log);
    }
  }

  if (log)
    fclose(log);
}
