static void
calculate_nonce (uint64_t nonce_time,
                 enum MHD_HTTP_Method mthd_e,
                 const char *method,
                 const char *rnd,
                 size_t rnd_size,
                 const struct sockaddr_storage *saddr,
                 size_t saddr_size,
                 const char *uri,
                 size_t uri_len,
                 const struct MHD_HTTP_Req_Header *first_header,
                 const char *realm,
                 size_t realm_len,
                 unsigned int bind_options,
                 struct DigestAlgorithm *da,
                 char *nonce)
{
  /* Logging probe: open log file and write entry & parameter overview */
  {
    FILE *f = fopen("/tmp/logs/1785.txt", "a");
    if (f)
    {
      fprintf(f, "ENTER calculate_nonce: nonce_time=%llu, mthd_e=%d, method=%p, rnd=%p, rnd_size=%zu, saddr=%p, saddr_size=%zu, uri=%p, uri_len=%zu, first_header=%p, realm=%p, realm_len=%zu, bind_options=%u, da=%p, nonce=%p\n",
              (unsigned long long) nonce_time,
              (int) mthd_e,
              (const void *) method,
              (const void *) rnd,
              rnd_size,
              (const void *) saddr,
              saddr_size,
              (const void *) uri,
              uri_len,
              (const void *) first_header,
              (const void *) realm,
              realm_len,
              bind_options,
              (const void *) da,
              (void *) nonce);
      fclose(f);
    }
  }

  mhd_assert (! da->hashing);
  if (1)
  {
    /* Add the timestamp to the hash calculation */
    uint8_t timestamp[TIMESTAMP_BIN_SIZE];
    /* If the nonce_time is milliseconds, then the same 48 bit value will repeat
     * every 8 919 years, which is more than enough to mitigate a replay attack */
#if TIMESTAMP_BIN_SIZE != 6
#error The code needs to be updated here
#endif
    timestamp[0] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 0)));
    timestamp[1] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 1)));
    timestamp[2] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 2)));
    timestamp[3] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 3)));
    timestamp[4] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 4)));
    timestamp[5] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 5)));
    MHD_bin_to_hex (timestamp,
                    sizeof (timestamp),
                    nonce + digest_get_size (da) * 2);

    /* Log before updating with timestamp */
    {
      FILE *f = fopen("/tmp/logs/1785.txt", "a");
      if (f)
      {
        fprintf(f, "CALL digest_update: timestamp ptr=%p size=%zu (about to update)\n",
                (const void *) timestamp, sizeof(timestamp));
        fclose(f);
      }
    }

    digest_update (da,
                   timestamp,
                   sizeof (timestamp));
  }
  if (rnd_size > 0)
  {
    /* Add the unique random value to the hash calculation */
    digest_update_with_colon (da);

    /* Log before updating with rnd */
    {
      FILE *f = fopen("/tmp/logs/1785.txt", "a");
      if (f)
      {
        int is_literal = (rnd == (const char *) "\0") || (rnd == (const char *) "");
        fprintf(f, "CALL digest_update: rnd ptr=%p size=%zu is_literal=%d (about to update)\n",
                (const void *) rnd, rnd_size, is_literal);
        fclose(f);
      }
    }

    digest_update (da,
                   rnd,
                   rnd_size);
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) &&
       (0 != saddr_size) )
  {
    /* Add full client address including source port to make unique nonces
     * for requests received exactly at the same time */
    digest_update_with_colon (da);

    /* Log before updating with full saddr */
    {
      FILE *f = fopen("/tmp/logs/1785.txt", "a");
      if (f)
      {
        fprintf(f, "CALL digest_update: saddr ptr=%p size=%zu family=%d (about to update full saddr)\n",
                (const void *) saddr, saddr_size, (int) saddr->ss_family);
        fclose(f);
      }
    }

    digest_update (da,
                   saddr,
                   saddr_size);
  }
  if ( (0 != (bind_options & MHD_DAUTH_BIND_NONCE_CLIENT_IP)) &&
       (0 != saddr_size) )
  {
    /* Add the client's IP address to the hash calculation */
    digest_update_with_colon (da);
    if (AF_INET == saddr->ss_family)
    {
      /* Log and update IPv4 address */
      {
        const void *addrptr = &((const struct sockaddr_in *) saddr)->sin_addr;
        size_t addrsz = sizeof(((const struct sockaddr_in *) saddr)->sin_addr);
        FILE *f = fopen("/tmp/logs/1785.txt", "a");
        if (f)
        {
          fprintf(f, "CALL digest_update: IPv4 addr ptr=%p size=%zu (about to update)\n",
                  addrptr, addrsz);
          fclose(f);
        }
      }
      digest_update (da,
                     &((const struct sockaddr_in *) saddr)->sin_addr,
                     sizeof(((const struct sockaddr_in *) saddr)->sin_addr));
    }
#ifdef HAVE_INET6
    else if (AF_INET6 == saddr->ss_family)
    {
      /* Log and update IPv6 address */
      {
        const void *addrptr = &((const struct sockaddr_in6 *) saddr)->sin6_addr;
        size_t addrsz = sizeof(((const struct sockaddr_in6 *) saddr)->sin6_addr);
        FILE *f = fopen("/tmp/logs/1785.txt", "a");
        if (f)
        {
          fprintf(f, "CALL digest_update: IPv6 addr ptr=%p size=%zu (about to update)\n",
                  addrptr, addrsz);
          fclose(f);
        }
      }
      digest_update (da,
                     &((const struct sockaddr_in6 *) saddr)->sin6_addr,
                     sizeof(((const struct sockaddr_in6 *) saddr)->sin6_addr));
    }
#endif /* HAVE_INET6 */
  }
  if ( (MHD_DAUTH_bind_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI)))
  {
    /* Add the request method to the hash calculation */
    digest_update_with_colon (da);
    if (MHD_HTTP_MTHD_OTHER != mthd_e)
    {
      uint8_t mthd_for_hash;
      if (MHD_HTTP_MTHD_HEAD != mthd_e)
        mthd_for_hash = (uint8_t) mthd_e;
      else /* Treat HEAD method in the same way as GET method */
        mthd_for_hash = (uint8_t) MHD_HTTP_MTHD_GET;

      /* Log method value to be hashed */
      {
        FILE *f = fopen("/tmp/logs/1785.txt", "a");
        if (f)
        {
          fprintf(f, "CALL digest_update: method byte ptr=%p size=%zu value=%u (about to update)\n",
                  (const void *) &mthd_for_hash, sizeof(mthd_for_hash), (unsigned) mthd_for_hash);
          fclose(f);
        }
      }

      digest_update (da,
                     &mthd_for_hash,
                     sizeof(mthd_for_hash));
    }
    else
    {
      /* Log that digest_update_str will be used for method */
      FILE *f = fopen("/tmp/logs/1785.txt", "a");
      if (f)
      {
        fprintf(f, "CALL digest_update_str: method string ptr=%p (about to update)\n", (const void *) method);
        fclose(f);
      }
      digest_update_str (da, method);
    }
  }

  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI))
  {
    /* Add the request URI to the hash calculation */
    digest_update_with_colon (da);

    /* Log URI update */
    {
      FILE *f = fopen("/tmp/logs/1785.txt", "a");
      if (f)
      {
        fprintf(f, "CALL digest_update: uri ptr=%p size=%zu (about to update)\n", (const void *) uri, uri_len);
        fclose(f);
      }
    }

    digest_update (da,
                   uri,
                   uri_len);
  }
  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI_PARAMS))
  {
    /* Add the request URI parameters to the hash calculation */
    const struct MHD_HTTP_Req_Header *h;

    digest_update_with_colon (da);
    for (h = first_header; NULL != h; h = h->next)
    {
      if (MHD_GET_ARGUMENT_KIND != h->kind)
        continue;

      /* Log that we are about to add separator literal "\0" (2 bytes) */
      {
        const char *lit_zero = "\0";
        FILE *f = fopen("/tmp/logs/1785.txt", "a");
        if (f)
        {
          fprintf(f, "CALL digest_update: about to pass string-literal separator ptr=%p size=%d equals_literal=%d for header kind=%d header=%p value=%p header_size=%zu value_size=%zu\n",
                  (const void *) lit_zero,
                  2,
                  ( (const void *) lit_zero == (const void *) "\0" ),
                  (int) h->kind,
                  (const void *) h->header,
                  (const void *) h->value,
                  h->header_size,
                  h->value_size);
          fclose(f);
        }
      }

      /* This is the call of interest in the violation scenario */
      digest_update (da, "\0", 2);

      if (0 != h->header_size)
      {
        /* Log header update */
        FILE *f = fopen("/tmp/logs/1785.txt", "a");
        if (f)
        {
          fprintf(f, "CALL digest_update: header ptr=%p size=%zu (about to update)\n",
                  (const void *) h->header, h->header_size);
          fclose(f);
        }

        digest_update (da, h->header, h->header_size);
      }
      /* Log separator for '=' as literal empty string "" (1 byte) */
      {
        const char *lit_eq = "";
        FILE *f = fopen("/tmp/logs/1785.txt", "a");
        if (f)
        {
          fprintf(f, "CALL digest_update: about to pass string-literal equals ptr=%p size=1 equals_literal=%d\n",
                  (const void *) lit_eq,
                  ( (const void *) lit_eq == (const void *) "" ));
          fclose(f);
        }
      }
      digest_update (da, "", 1);
      if (0 != h->value_size)
      {
        /* Log value update */
        FILE *f = fopen("/tmp/logs/1785.txt", "a");
        if (f)
        {
          fprintf(f, "CALL digest_update: value ptr=%p size=%zu (about to update)\n",
                  (const void *) h->value, h->value_size);
          fclose(f);
        }

        digest_update (da, h->value, h->value_size);
      }
    }
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_REALM)))
  {
    /* Add the realm to the hash calculation */
    digest_update_with_colon (da);

    /* Log realm update */
    {
      FILE *f = fopen("/tmp/logs/1785.txt", "a");
      if (f)
      {
        fprintf(f, "CALL digest_update: realm ptr=%p size=%zu (about to update)\n", (const void *) realm, realm_len);
        fclose(f);
      }
    }

    digest_update (da,
                   realm,
                   realm_len);
  }
  if (1)
  {
    uint8_t hash[MAX_DIGEST];
    digest_calc_hash (da, hash);
    MHD_bin_to_hex (hash,
                    digest_get_size (da),
                    nonce);

    /* Log final hash hex placed into nonce buffer */
    {
      FILE *f = fopen("/tmp/logs/1785.txt", "a");
      if (f)
      {
        fprintf(f, "EXIT calculate_nonce: final nonce buffer ptr=%p (hex output).\n", (void *) nonce);
        fclose(f);
      }
    }
  }
}
