static void
calculate_nonce (uint64_t nonce_time,
                 enum MHD_HTTP_Method mthd_e,
                 const char *method,
                 const char *rnd,
                 size_t rnd_size,
                 const struct sockaddr_storage *saddr,
                 size_t saddr_size,
                 const char *uri,
                 size_t uri_len,
                 const struct MHD_HTTP_Req_Header *first_header,
                 const char *realm,
                 size_t realm_len,
                 unsigned int bind_options,
                 struct DigestAlgorithm *da,
                 char *nonce)
{
  /* Logging probe: record entry and parameter state to assist debugging the
     potential const-discard violation when saddr (const-qualified) is passed
     to functions that may accept non-const pointers. Logs are appended to
     /tmp/logs/1785.txt. */
  {
    FILE *log = fopen("/tmp/logs/1785.txt", "a");
    if (log)
    {
      fprintf(log, "ENTER calculate_nonce: nonce_time=%" PRIu64 ", mthd_e=%d, method=%p, rnd=%p, rnd_size=%zu, saddr=%p, saddr_size=%zu, uri=%p, uri_len=%zu, realm=%p, realm_len=%zu, bind_options=0x%x, da=%p, nonce=%p\n",
              nonce_time, (int) mthd_e, (const void *) method,
              (const void *) rnd, rnd_size, (const void *) saddr,
              saddr_size, (const void *) uri, uri_len,
              (const void *) realm, realm_len, bind_options, (void *) da,
              (void *) nonce);
      /* Note about const-qualification in this function signature */
      fprintf(log, "NOTE: saddr parameter is declared as 'const struct sockaddr_storage *' in calculate_nonce.\n");
      fclose(log);
    }
  }

  mhd_assert (! da->hashing);
  if (1)
  {
    /* Add the timestamp to the hash calculation */
    uint8_t timestamp[TIMESTAMP_BIN_SIZE];
    /* If the nonce_time is milliseconds, then the same 48 bit value will repeat
     * every 8 919 years, which is more than enough to mitigate a replay attack */
#if TIMESTAMP_BIN_SIZE != 6
#error The code needs to be updated here
#endif
    timestamp[0] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 0)));
    timestamp[1] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 1)));
    timestamp[2] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 2)));
    timestamp[3] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 3)));
    timestamp[4] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 4)));
    timestamp[5] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 5)));
    MHD_bin_to_hex (timestamp,
                    sizeof (timestamp),
                    nonce + digest_get_size (da) * 2);
    digest_update (da,
                   timestamp,
                   sizeof (timestamp));
  }
  if (rnd_size > 0)
  {
    /* Add the unique random value to the hash calculation */
    digest_update_with_colon (da);
    digest_update (da,
                   rnd,
                   rnd_size);
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) &&
       (0 != saddr_size) )
  {
    /* Add full client address including source port to make unique nonces
     * for requests received exactly at the same time */
    digest_update_with_colon (da);

    /* Logging probe: about to pass the (const) saddr pointer to digest_update. */
    {
      FILE *log = fopen("/tmp/logs/1785.txt", "a");
      if (log)
      {
        fprintf(log, "CALL digest_update with full saddr: saddr=%p, saddr_size=%zu\n",
                (const void *) saddr, saddr_size);
        fprintf(log, "(Reminder: saddr is const-qualified in calculate_nonce signature)\n");
        fclose(log);
      }
    }

    digest_update (da,
                   saddr,
                   saddr_size);
  }
  if ( (0 != (bind_options & MHD_DAUTH_BIND_NONCE_CLIENT_IP)) &&
       (0 != saddr_size) )
  {
    /* Add the client's IP address to the hash calculation */
    digest_update_with_colon (da);
    if (AF_INET == saddr->ss_family)
    {
      /* Logging probe: AF_INET branch — passing address of sin_addr derived from const saddr */
      {
        const void *addr_ptr = &((const struct sockaddr_in *) saddr)->sin_addr;
        size_t addr_sz = sizeof(((const struct sockaddr_in *) saddr)->sin_addr);
        FILE *log = fopen("/tmp/logs/1785.txt", "a");
        if (log)
        {
          fprintf(log, "CALL digest_update with IPv4 address: family=AF_INET (%d), saddr=%p, addr_ptr=%p, addr_size=%zu\n",
                  (int) saddr->ss_family, (const void *) saddr, addr_ptr, addr_sz);
          fprintf(log, "(Reminder: the address passed here is derived from a 'const struct sockaddr_storage *' parameter)\n");
          fclose(log);
        }
      }

      digest_update (da,
                     &((const struct sockaddr_in *) saddr)->sin_addr,
                     sizeof(((const struct sockaddr_in *) saddr)->sin_addr));
    }
#ifdef HAVE_INET6
    else if (AF_INET6 == saddr->ss_family)
    {
      /* Logging probe: AF_INET6 branch — passing address of sin6_addr derived from const saddr */
      {
        const void *addr_ptr = &((const struct sockaddr_in6 *) saddr)->sin6_addr;
        size_t addr_sz = sizeof(((const struct sockaddr_in6 *) saddr)->sin6_addr);
        FILE *log = fopen("/tmp/logs/1785.txt", "a");
        if (log)
        {
          fprintf(log, "CALL digest_update with IPv6 address: family=AF_INET6 (%d), saddr=%p, addr_ptr=%p, addr_size=%zu\n",
                  (int) saddr->ss_family, (const void *) saddr, addr_ptr, addr_sz);
          fprintf(log, "(Reminder: the address passed here is derived from a 'const struct sockaddr_storage *' parameter)\n");
          fclose(log);
        }
      }

      digest_update (da,
                     &((const struct sockaddr_in6 *) saddr)->sin6_addr,
                     sizeof(((const struct sockaddr_in6 *) saddr)->sin6_addr));
    }
#endif /* HAVE_INET6 */
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI)))
  {
    /* Add the request method to the hash calculation */
    digest_update_with_colon (da);
    if (MHD_HTTP_MTHD_OTHER != mthd_e)
    {
      uint8_t mthd_for_hash;
      if (MHD_HTTP_MTHD_HEAD != mthd_e)
        mthd_for_hash = (uint8_t) mthd_e;
      else /* Treat HEAD method in the same way as GET method */
        mthd_for_hash = (uint8_t) MHD_HTTP_MTHD_GET;
      digest_update (da,
                     &mthd_for_hash,
                     sizeof(mthd_for_hash));
    }
    else
      digest_update_str (da, method);
  }

  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI))
  {
    /* Add the request URI to the hash calculation */
    digest_update_with_colon (da);

    digest_update (da,
                   uri,
                   uri_len);
  }
  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI_PARAMS))
  {
    /* Add the request URI parameters to the hash calculation */
    const struct MHD_HTTP_Req_Header *h;

    digest_update_with_colon (da);
    for (h = first_header; NULL != h; h = h->next)
    {
      if (MHD_GET_ARGUMENT_KIND != h->kind)
        continue;
      digest_update (da, "\0", 2);
      if (0 != h->header_size)
        digest_update (da, h->header, h->header_size);
      digest_update (da, "", 1);
      if (0 != h->value_size)
        digest_update (da, h->value, h->value_size);
    }
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_REALM)))
  {
    /* Add the realm to the hash calculation */
    digest_update_with_colon (da);
    digest_update (da,
                   realm,
                   realm_len);
  }
  if (1)
  {
    uint8_t hash[MAX_DIGEST];
    digest_calc_hash (da, hash);
    MHD_bin_to_hex (hash,
                    digest_get_size (da),
                    nonce);

    /* Logging probe: record final nonce hex and that function is exiting. */
    {
      FILE *log = fopen("/tmp/logs/1785.txt", "a");
      if (log)
      {
        fprintf(log, "EXIT calculate_nonce: final nonce (hex prefix)='%.*s', da=%p\n",
                32, nonce, (void *) da);
        fclose(log);
      }
    }
  }
}
