static bool
check_argument_match (struct MHD_Connection *connection,
                      char *args)
{
  struct MHD_HTTP_Req_Header *pos;
  enum MHD_Result ret;
  struct test_header_param param;
  FILE *log = fopen("/tmp/logs/4375.txt", "a");

  /* Fallback if the logs directory does not exist */
  if (NULL == log)
  {
    log = fopen("/tmp/4375.txt", "a");
  }

  if (log)
  {
    fprintf(log, "ENTER: check_argument_match connection=%p args=%p\n", (void *)connection, (void *)args);
    fflush(log);
  }

  param.connection = connection;
  param.num_headers = 0;

  if (log)
  {
    fprintf(log, "AFTER_ASSIGN: param.connection=%p param.num_headers=%d\n", (void *)param.connection, param.num_headers);
    fflush(log);
  }

  ret = MHD_parse_arguments_ (connection,
                              MHD_GET_ARGUMENT_KIND,
                              args,
                              &test_header,
                              &param);

  if (log)
  {
    fprintf(log, "PARSE_RET: ret=%d param.num_headers=%d\n", (int)ret, param.num_headers);
    fflush(log);
  }

  if (MHD_NO == ret)
  {
    if (log)
    {
      fprintf(log, "EARLY_RETURN: MHD_parse_arguments_ returned MHD_NO\n");
      fclose(log);
    }
    return false;
  }

  /* also check that the number of headers matches */
  for (pos = connection->rq.headers_received; NULL != pos; pos = pos->next)
  {
    if (log)
    {
      /* log the header node pointer and its kind to show which headers are being inspected */
      fprintf(log, "LOOP_HDR: pos=%p kind=%d\n", (void *)pos, (int)pos->kind);
      fflush(log);
    }

    if (MHD_GET_ARGUMENT_KIND != pos->kind)
      continue;

    param.num_headers--;

    if (log)
    {
      fprintf(log, "DECREMENT: param.num_headers=%d\n", param.num_headers);
      fflush(log);
    }
  }

  if (0 != param.num_headers)
  {
    if (log)
    {
      fprintf(log, "COUNT_MISMATCH: final param.num_headers=%d -> returning false\n", param.num_headers);
      fclose(log);
    }
    /* argument count mismatch */
    return false;
  }

  if (log)
  {
    fprintf(log, "EXIT_SUCCESS: returning true\n");
    fclose(log);
  }

  return true;
}
