static bool
check_argument_match (struct MHD_Connection *connection,
                      char *args)
{
  struct MHD_HTTP_Req_Header *pos;
  enum MHD_Result ret;
  struct test_header_param param;
  FILE *logf = NULL;

  /* Attempt to open the requested log file. If that fails, fall back to /tmp/4375.txt
     so that there's at least some trace output in environments without /tmp/logs. */
  logf = fopen("/tmp/logs/4375.txt", "a");
  if (logf == NULL)
  {
    logf = fopen("/tmp/4375.txt", "a");
  }

  if (logf != NULL)
  {
    fprintf(logf, "ENTER check_argument_match: connection=%p, args_ptr=%p\n", (void *)connection, (void *)args);
    if (args != NULL)
    {
      /* Print a preview of the args string for debugging. Guarding against huge output. */
      char buf_preview[257] = {0};
      size_t i;
      for (i = 0; i < 256 && args[i] != '\0'; ++i)
        buf_preview[i] = args[i];
      buf_preview[i] = '\0';
      fprintf(logf, "args_preview=\"%s\"\n", buf_preview);
    }
    else
    {
      fprintf(logf, "args is NULL\n");
    }
  }

  param.connection = connection;
  param.num_headers = 0;

  if (logf != NULL)
    fprintf(logf, "Calling MHD_parse_arguments_: initial param.num_headers=%d\n", param.num_headers);

  ret = MHD_parse_arguments_ (connection,
                              MHD_GET_ARGUMENT_KIND,
                              args,
                              &test_header,
                              &param);

  if (logf != NULL)
  {
    fprintf(logf, "Returned from MHD_parse_arguments_: ret=%d, param.num_headers=%d\n", (int)ret, param.num_headers);
    fflush(logf);
  }

  if (MHD_NO == ret)
  {
    if (logf != NULL)
    {
      fprintf(logf, "MHD_parse_arguments_ returned MHD_NO; exiting with false\n");
      fclose(logf);
    }
    return false;
  }

  /* also check that the number of headers matches */
  for (pos = connection->rq.headers_received; NULL != pos; pos = pos->next)
  {
    if (logf != NULL)
      fprintf(logf, "Visiting header: pos=%p, kind=%d\n", (void *)pos, (int)pos->kind);

    if (MHD_GET_ARGUMENT_KIND != pos->kind)
      continue;
    param.num_headers--;

    if (logf != NULL)
      fprintf(logf, "Decremented param.num_headers -> %d\n", param.num_headers);
  }

  if (0 != param.num_headers)
  {
    /* argument count mismatch */
    if (logf != NULL)
    {
      fprintf(logf, "argument count mismatch: final param.num_headers=%d; exiting with false\n", param.num_headers);
      fclose(logf);
    }
    return false;
  }

  if (logf != NULL)
  {
    fprintf(logf, "argument count matches; exiting with true\n");
    fclose(logf);
  }

  return true;
}
