static bool
check_argument_match (struct MHD_Connection *connection,
                      char *args)
{
  struct MHD_HTTP_Req_Header *pos;
  enum MHD_Result ret;
  struct test_header_param param;
  FILE *logf = NULL;

  /* Attempt to open log file; if the directory does not exist try to create it. */
  logf = fopen("/tmp/logs/4375.txt", "a");
  if (NULL == logf)
  {
    /* Provide prototype to mkdir to avoid implicit-declaration errors; permission bits as int. */
    int mkdir(const char *, int);
    (void)mkdir("/tmp/logs", 0777);
    logf = fopen("/tmp/logs/4375.txt", "a");
  }

  if (NULL != logf)
  {
    fprintf(logf, "ENTER check_argument_match: connection=%p, args=%p\n", (void *)connection, (void *)args);
    if (args != NULL)
      fprintf(logf, "  args (first 128 bytes): %.*s\n", 128, args);
  }

  param.connection = connection;
  param.num_headers = 0;
  if (NULL != logf)
    fprintf(logf, "  Calling MHD_parse_arguments_ with param at %p\n", (void *)&param);

  ret = MHD_parse_arguments_ (connection,
                              MHD_GET_ARGUMENT_KIND,
                              args,
                              &test_header,
                              &param);

  if (NULL != logf)
    fprintf(logf, "  MHD_parse_arguments_ returned %d, param.num_headers=%d\n",
            (int)ret, (int)param.num_headers);

  if (MHD_NO == ret)
  {
    if (NULL != logf)
    {
      fprintf(logf, "  Early return: MHD_NO\n");
      fclose(logf);
    }
    return false;
  }

  /* also check that the number of headers matches */
  for (pos = connection->rq.headers_received; NULL != pos; pos = pos->next)
  {
    if (NULL != logf)
      fprintf(logf, "    header node %p: kind=%d, next=%p\n", (void *)pos, (int)pos->kind, (void *)pos->next);

    if (MHD_GET_ARGUMENT_KIND != pos->kind)
      continue;

    param.num_headers--;
    if (NULL != logf)
      fprintf(logf, "      matched GET_ARGUMENT_KIND, decremented num_headers -> %d\n", (int)param.num_headers);
  }

  if (0 != param.num_headers)
  {
    /* argument count mismatch */
    if (NULL != logf)
    {
      fprintf(logf, "  Final mismatch: param.num_headers=%d, returning false\n", (int)param.num_headers);
      fclose(logf);
    }
    return false;
  }

  if (NULL != logf)
  {
    fprintf(logf, "  Success: param.num_headers=%d, returning true\n", (int)param.num_headers);
    fclose(logf);
  }

  return true;
}
