static void
check_connection_reply (struct MHD_Connection *connection)
{
  struct MHD_Connection *const c = connection; /**< a short alias */
  struct MHD_Response *const r = c->rp.response;  /**< a short alias */

  /* Open a log file for evidence. Append mode so multiple runs accumulate data. */
  FILE *log = fopen("/tmp/logs/4005.txt", "a");
  if (log)
    {
      fprintf(log, "ENTER check_connection_reply: connection=%p, c=%p, r=%p\n",
              (void *) connection, (void *) c, (void *) r);
    }

  mhd_assert (c->rp.props.set);
#ifdef HAVE_MESSAGES
  /* Log the relevant member values that demonstrate read-only usage. */
  if (log)
    {
      fprintf(log,
              "STATE: use_reply_body_headers=%d, total_size=%llu, flags_auto=0x%X, responseCode=%u, daemon=%p\n",
              (int) (c->rp.props.use_reply_body_headers),
              (unsigned long long) (r->total_size),
              (unsigned) (r->flags_auto),
              (unsigned) (c->rp.responseCode),
              (void *) (c->daemon));
    }

  if ( (! c->rp.props.use_reply_body_headers) &&
       (0 != r->total_size) )
  {
    if (log)
      fprintf(log,
              "PATH: non-empty response body ignored; responseCode=%u, total_size=%llu\n",
              (unsigned) (c->rp.responseCode),
              (unsigned long long) (r->total_size));

    MHD_DLOG (c->daemon,
              _ ("This reply with response code %u cannot use reply body. "
                 "Non-empty response body is ignored and not used.\n"),
              (unsigned) (c->rp.responseCode));
  }
  if ( (! c->rp.props.use_reply_body_headers) &&
       (0 != (r->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH)) )
  {
    if (log)
      fprintf(log,
              "PATH: application-defined Content-Length header violates spec; responseCode=%u, flags_auto=0x%X\n",
              (unsigned) (c->rp.responseCode),
              (unsigned) (r->flags_auto));

    MHD_DLOG (c->daemon,
              _ ("This reply with response code %u cannot use reply body. "
                 "Application defined \"Content-Length\" header violates"
                 "HTTP specification.\n"),
              (unsigned) (c->rp.responseCode));
  }

  if (log)
    fclose(log);
#else
  if (log)
    {
      fprintf(log, "HAVE_MESSAGES not defined: no member accesses logged beyond pointer values.\n");
      fclose(log);
    }
  (void) c; /* Mute compiler warning */
  (void) r; /* Mute compiler warning */
#endif
}
