static enum MHD_CheckNonceNC_
check_nonce_nc (struct MHD_Connection *connection,
                const char *nonce,
                size_t noncelen,
                uint64_t nonce_time,
                uint64_t nc)
{
  /* Open log for append. Be conservative: proceed if fopen fails. */
  FILE *log = fopen("/tmp/logs/4485.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER check_nonce_nc: this=%p, connection=%p, connection->daemon=%p, nonce=%p, noncelen=%zu, nonce_time=%llu, nc=%llu\n",
            (void *) &check_nonce_nc, (void *) connection,
            (void *) (connection ? connection->daemon : NULL), (const void *) nonce,
            noncelen, (unsigned long long) nonce_time, (unsigned long long) nc);
    fflush(log);
  }

  struct MHD_Daemon *daemon = MHD_get_master (connection->daemon);
  struct MHD_NonceNc *nn;
  uint32_t mod;
  enum MHD_CheckNonceNC_ ret;

  if (log)
  {
    fprintf(log, "master daemon=%p\n", (void *) daemon);
    fflush(log);
  }

  mhd_assert (0 != noncelen);
  mhd_assert (0 != nc);
  if (MAX_DIGEST_NONCE_LENGTH < noncelen)
  {
    if (log)
    {
      fprintf(log, "EARLY RETURN: noncelen (%zu) > MAX_DIGEST_NONCE_LENGTH\n", noncelen);
      fclose(log);
    }
    return MHD_CHECK_NONCENC_WRONG; /* This should be impossible, but static analysis
                      tools have a hard time with it *and* this also
                      protects against unsafe modifications that may
                      happen in the future... */
  }
  mod = daemon->nonce_nc_size;
  if (log)
    fprintf(log, "daemon->nonce_nc_size = %u\n", (unsigned) mod);
  if (0 == mod)
  {
    if (log)
    {
      fprintf(log, "EARLY RETURN: mod == 0 (no array)\n");
      fclose(log);
    }
    return MHD_CHECK_NONCENC_STALE;  /* no array! */
  }
  if (nc >= UINT32_MAX - 64)
  {
    if (log)
    {
      fprintf(log, "EARLY RETURN: nc (" ULL_FMT ") >= UINT32_MAX - 64\n", (unsigned long long) nc);
      /* Undef ULL_FMT locally: we'll avoid macro use above; use plain fallback below */
      fclose(log);
    }
    return MHD_CHECK_NONCENC_STALE;  /* Overflow, unrealistically high value */
  }

  /* Compute index and slot pointer, log details */
  {
    uint32_t idx = get_nonce_nc_idx (mod, nonce, noncelen);
    if (log)
      fprintf(log, "get_nonce_nc_idx -> %u\n", (unsigned) idx);
    nn = &daemon->nnc[idx];
    if (log)
    {
      /* Log a few bytes of the slot to help reproduce mismatch/stale cases */
      fprintf(log, "nn=%p, nn->nc=%u, nn->nmask=0x%016llx, nn->nonce[0]=%u, nn->nonce[noncelen]=%u\n",
              (void *) nn,
              (unsigned) nn->nc,
              (unsigned long long) nn->nmask,
              (unsigned) (unsigned char) nn->nonce[0],
              (unsigned) (unsigned char) nn->nonce[noncelen]);
      fflush(log);
    }
  }

  MHD_mutex_lock_chk_ (&daemon->nnc_lock);
  if (log)
  {
    fprintf(log, "locked nnc_lock\n");
    fflush(log);
  }

  mhd_assert (0 == nn->nonce[noncelen]); /* The old value must be valid */

  if ( (0 != memcmp (nn->nonce, nonce, noncelen)) ||
       (0 != nn->nonce[noncelen]) )
  { /* The nonce in the slot does not match nonce from the client */
    if (log)
    {
      fprintf(log, "Branch: nonce mismatch or terminator mismatch (memcmp=%d, term=%u)\n",
              (int) (0 != memcmp (nn->nonce, nonce, noncelen)), (unsigned) (unsigned char) nn->nonce[noncelen]);
      fflush(log);
    }
    if (0 == nn->nonce[0])
    { /* The slot was never used, while the client's nonce value should be
       * recorded when it was generated by MHD */
      ret = MHD_CHECK_NONCENC_WRONG;
      if (log)
      {
        fprintf(log, "Sub-branch: slot never used -> WRONG\n");
        fflush(log);
      }
    }
    else if (0 != nn->nonce[noncelen])
    { /* The value is the slot is wrong */
      ret =  MHD_CHECK_NONCENC_STALE;
      if (log)
      {
        fprintf(log, "Sub-branch: terminator != 0 -> STALE\n");
        fflush(log);
      }
    }
    else
    {
      uint64_t slot_ts; /**< The timestamp in the slot */
      if (! get_nonce_timestamp (nn->nonce, noncelen, &slot_ts))
      {
        mhd_assert (0); /* The value is the slot is wrong */
        ret = MHD_CHECK_NONCENC_STALE;
        if (log)
        {
          fprintf(log, "Sub-branch: get_nonce_timestamp failed -> STALE\n");
          fflush(log);
        }
      }
      else
      {
        /* Unsigned value, will be large if nonce_time is less than slot_ts */
        const uint64_t ts_diff = TRIM_TO_TIMESTAMP (nonce_time - slot_ts);
        if (log)
          fprintf(log, "slot_ts=%llu, ts_diff=%llu\n", (unsigned long long) slot_ts, (unsigned long long) ts_diff);
        if ((REUSE_TIMEOUT * 1000) >= ts_diff)
        {
          /* The nonce from the client may not have been placed in the slot
           * because another nonce in that slot has not yet expired. */
          ret = MHD_CHECK_NONCENC_STALE;
          if (log)
          {
            fprintf(log, "Timing sub-branch: ts_diff <= REUSE_TIMEOUT*1000 -> STALE\n");
            fflush(log);
          }
        }
        else if (TRIM_TO_TIMESTAMP (UINT64_MAX) / 2 >= ts_diff)
        {
          /* Too large value means that nonce_time is less than slot_ts.
           * The nonce from the client may have been overwritten by the newer
           * nonce. */
          ret = MHD_CHECK_NONCENC_STALE;
          if (log)
          {
            fprintf(log, "Timing sub-branch: ts_diff indicates nonce_time < slot_ts -> STALE\n");
            fflush(log);
          }
        }
        else
        {
          /* The nonce from the client should be generated after the nonce
           * in the slot has been expired, the nonce must be recorded, but
           * it's not. */
          ret = MHD_CHECK_NONCENC_WRONG;
          if (log)
          {
            fprintf(log, "Timing sub-branch: slot expired but nonce not recorded -> WRONG\n");
            fflush(log);
          }
        }
      }
    }
  }
  else if (nc > nn->nc)
  {
    if (log)
      fprintf(log, "Branch: nc (%llu) > nn->nc (%u)\n", (unsigned long long) nc, (unsigned) nn->nc);

    /* 'nc' is larger, shift bitmask and bump limit */
    const uint32_t jump_size = (uint32_t) nc - nn->nc;
    if (log) fprintf(log, "jump_size = %u\n", (unsigned) jump_size);
    if (64 > jump_size)
    {
      /* small jump, less than mask width */
      if (log) fprintf(log, "small jump (<64) before nmask=0x%016llx\n", (unsigned long long) nn->nmask);
      nn->nmask <<= jump_size;
      /* Set bit for the old 'nc' value */
      nn->nmask |= (UINT64_C (1) << (jump_size - 1));
      if (log) fprintf(log, "after shift nmask=0x%016llx\n", (unsigned long long) nn->nmask);
    }
    else if (64 == jump_size)
      nn->nmask = (UINT64_C (1) << 63);
    else
      nn->nmask = 0;                /* big jump, unset all bits in the mask */
    nn->nc = (uint32_t) nc;
    ret = MHD_CHECK_NONCENC_OK;
    if (log) fprintf(log, "Updated nn->nc=%u, ret=OK\n", (unsigned) nn->nc);
  }
  else if (nc < nn->nc)
  {
    if (log) fprintf(log, "Branch: nc (%llu) < nn->nc (%u)\n", (unsigned long long) nc, (unsigned) nn->nc);
    /* Note that we use 64 here, as we do not store the
       bit for 'nn->nc' itself in 'nn->nmask' */
    if ( (nc + 64 >= nn->nc) &&
         (0 == ((UINT64_C (1) << (nn->nc - nc - 1)) & nn->nmask)) )
    {
      /* Out-of-order nonce, but within 64-bit bitmask, set bit */
      nn->nmask |= (UINT64_C (1) << (nn->nc - nc - 1));
      ret = MHD_CHECK_NONCENC_OK;
      if (log) fprintf(log, "Out-of-order within 64-bit window -> OK, new nmask=0x%016llx\n", (unsigned long long) nn->nmask);
    }
    else
      /* 'nc' was already used or too old (more then 64 values ago) */
    {
      ret = MHD_CHECK_NONCENC_STALE;
      if (log) fprintf(log, "Out-of-order too old or already used -> STALE\n");
    }
  }
  else /* if (nc == nn->nc) */
  {
    /* 'nc' was already used */
    ret = MHD_CHECK_NONCENC_STALE;
    if (log) fprintf(log, "Equal nc == nn->nc -> STALE\n");
  }

  MHD_mutex_unlock_chk_ (&daemon->nnc_lock);
  if (log)
  {
    fprintf(log, "unlocked nnc_lock, returning %d\n", (int) ret);
    fclose(log);
  }

  return ret;
}
