static bool
check_uri_match (struct MHD_Connection *connection, char *uri, size_t uri_len)
{
  FILE *log = fopen("/tmp/logs/3852.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER check_uri_match: connection=%p uri=%p original_uri_len=%zu\n",
            (void *)connection, (void *)uri, uri_len);
  }

  char *qmark;
  char *args;
  struct MHD_Daemon *const daemon = connection->daemon;

  if (log)
  {
    fprintf(log, "daemon=%p unescape_cb_present=%d\n",
            (void *)daemon, (int)(daemon && daemon->unescape_callback));
  }

  /* Preserve original behavior: null-terminate at uri_len (original code) */
  uri[uri_len] = 0;
  if (log)
    fprintf(log, "After null-terminate: uri[original_len] set to '\0', uri_preview='%s'\n", uri);

  qmark = memchr (uri,
                  '?',
                  uri_len);
  if (log)
    fprintf(log, "memchr for '?' returned qmark=%p\n", (void *)qmark);

  if (NULL != qmark)
    *qmark = '\0';

  if (log)
    fprintf(log, "After qmark handling: uri='%s' (qmark %s)\n",
            uri, (qmark ? "found" : "not found"));

  /* Need to unescape URI before comparing with connection->url */
  /* Intentionally preserve the original code that assigns into the parameter
     'uri_len' to reproduce the reported MISRA Rule 17.8 violation. Insert
     logging before and after to capture the control-flow/state for evidence. */
  if (log)
    fprintf(log, "Calling unescape_callback with uri=%p\n", (void *)uri);

  uri_len = daemon->unescape_callback (daemon->unescape_callback_cls,
                                       connection,
                                       uri);

  if (log)
  {
    fprintf(log, "unescape_callback returned new_uri_len=%zu (assigned to parameter uri_len)\n",
            uri_len);
    fprintf(log, "(NOTE) Assignment to non-pointer parameter 'uri_len' occurred — reproduces Rule 17.8 scenario.\n");
  }

  if (log)
    fprintf(log, "Comparing uri_len=%zu with connection->rq.url_len=%zu\n",
            uri_len, connection->rq.url_len);

  if ((uri_len != connection->rq.url_len) ||
      (0 != memcmp (uri, connection->rq.url, uri_len)))
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Authentication failed, URI does not match.\n"));
#endif
    if (log)
    {
      fprintf(log, "Authentication failed: URI does not match (returning false)\n");
      fclose(log);
    }
    return false;
  }

  args = (NULL != qmark) ? (qmark + 1) : uri + uri_len;
  if (log)
  {
    /* args may be an empty string; print a small preview safely */
    fprintf(log, "Computed args pointer=%p\n", (void *)args);
    if (args && *args)
      fprintf(log, "args_preview='%s'\n", args);
    else
      fprintf(log, "args_preview=(empty)\n");
  }

  if (! check_argument_match (connection,
                              args) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Authentication failed, arguments do not match.\n"));
#endif
    if (log)
    {
      fprintf(log, "Authentication failed: arguments do not match (returning false)\n");
      fclose(log);
    }
    return false;
  }

  if (log)
  {
    fprintf(log, "check_uri_match completed successfully (returning true)\n");
    fclose(log);
  }
  return true;
}
