static bool
check_uri_match (struct MHD_Connection *connection, char *uri, size_t uri_len)
{
  char *qmark;
  char *args;
  struct MHD_Daemon *const daemon = connection->daemon;
  FILE *log = fopen("/tmp/logs/3853.txt", "a");

  if (log)
  {
    fprintf(log, "[check_uri_match] entry: connection=%p, daemon=%p, uri=%p, initial_uri_len=%zu\n",
            (void *)connection, (void *)daemon, (void *)uri, uri_len);
    if (daemon)
      fprintf(log, "[check_uri_match] daemon->unescape_callback=%p, daemon->unescape_callback_cls=%p\n",
              (void *)daemon->unescape_callback, (void *)daemon->unescape_callback_cls);
    fflush(log);
  }

  /* preserve original behavior: null-terminate at uri_len */
  uri[uri_len] = 0;
  if (log)
  {
    fprintf(log, "[check_uri_match] wrote terminating null at uri[uri_len] (uri_len=%zu)\n", uri_len);
    fflush(log);
  }

  qmark = memchr (uri,
                  '?',
                  uri_len);
  if (log)
  {
    fprintf(log, "[check_uri_match] memchr returned qmark=%p\n", (void *)qmark);
    fflush(log);
  }

  if (NULL != qmark)
  {
    *qmark = '\0';
    if (log)
    {
      fprintf(log, "[check_uri_match] found '?', set *qmark='\\0' (qmark=%p)\n", (void *)qmark);
      fflush(log);
    }
  }

  /* Need to unescape URI before comparing with connection->url */
  uri_len = daemon->unescape_callback (daemon->unescape_callback_cls,
                                       connection,
                                       uri);
  if (log)
  {
    fprintf(log, "[check_uri_match] unescape_callback returned uri_len=%zu\n", uri_len);
    fprintf(log, "[check_uri_match] connection->rq.url=%p, connection->rq.url_len=%zu\n",
            (void *)connection->rq.url, connection->rq.url_len);
    fflush(log);
  }

  /* perform comparison and log result */
  int cmp = 0;
  if (uri_len > 0)
    cmp = memcmp (uri, connection->rq.url, uri_len);
  else
    cmp = 0; /* memcmp of zero bytes should be equal */

  if (log)
  {
    fprintf(log, "[check_uri_match] memcmp(uri, connection->rq.url, %zu) returned %d\n",
            uri_len, cmp);
    fprintf(log, "[check_uri_match] lengths: uri_len=%zu, connection->rq.url_len=%zu\n",
            uri_len, connection->rq.url_len);
    fflush(log);
  }

  if ((uri_len != connection->rq.url_len) ||
      (0 != cmp))
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Authentication failed, URI does not match.\n"));
#endif
    if (log)
    {
      fprintf(log, "[check_uri_match] authentication failed: length or content mismatch -> returning false\n");
      fclose(log);
    }
    return false;
  }

  args = (NULL != qmark) ? (qmark + 1) : uri + uri_len;

  if (log)
  {
    fprintf(log, "[check_uri_match] args pointer = %p\n", (void *)args);
    fflush(log);
  }

  if (! check_argument_match (connection,
                              args) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Authentication failed, arguments do not match.\n"));
#endif
    if (log)
    {
      fprintf(log, "[check_uri_match] check_argument_match returned false -> returning false\n");
      fclose(log);
    }
    return false;
  }

  if (log)
  {
    fprintf(log, "[check_uri_match] all checks passed -> returning true\n");
    fclose(log);
  }
  return true;
}
