static bool
check_uri_match (struct MHD_Connection *connection, char *uri, size_t uri_len)
{
  /* Logging probe: append to log file so multiple runs accumulate evidence */
  FILE *log = fopen("/tmp/logs/3855.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER check_uri_match: connection=%p uri=%p uri_len=%zu\n",
            (void *)connection, (void *)uri, uri_len);
    fflush(log);
  }

  char *qmark;
  char *args;
  struct MHD_Daemon *const daemon = connection->daemon;

  if (log)
    fprintf(log, "daemon=%p, daemon->unescape_callback=%p, daemon->unescape_callback_cls=%p\n",
            (void *)daemon,
            (void *)daemon->unescape_callback,
            (void *)daemon->unescape_callback_cls);

  /* Preserve original behaviour: null-terminate the provided URI buffer */
  uri[uri_len] = 0;
  if (log)
    fprintf(log, "After null-terminate: uri[0]=%c (or 0 if NUL)\n",
            (uri[0] != '\0') ? uri[0] : '0');

  qmark = memchr (uri,
                  '?',
                  uri_len);
  if (log)
    fprintf(log, "memchr for '?' returned qmark=%p\n", (void *)qmark);

  if (NULL != qmark)
  {
    *qmark = '\0';
    if (log)
      fprintf(log, "Found '?', set *qmark='\0' at offset %ld\n", (long)(qmark - uri));
  }

  /* Need to unescape URI before comparing with connection->url */
  uri_len = daemon->unescape_callback (daemon->unescape_callback_cls,
                                       connection,
                                       uri);
  if (log)
    fprintf(log, "unescape_callback returned uri_len=%zu, connection->rq.url_len=%zu\n",
            uri_len, connection->rq.url_len);

  if ((uri_len != connection->rq.url_len) ||
      (0 != memcmp (uri, connection->rq.url, uri_len)))
  {
    if (log)
    {
      fprintf(log, "URI mismatch: compared length=%zu, expected=%zu, memcmp=%d\n",
              uri_len, connection->rq.url_len,
              (int) (0 != memcmp (uri, connection->rq.url, uri_len)));
      /* Log snippets of the strings for easier reproduction (null-terminated after earlier step) */
      fprintf(log, "uri (as string)='%.*s'\n", (int)uri_len, uri);
      fprintf(log, "connection->rq.url (as string)='%.*s'\n", (int)connection->rq.url_len, connection->rq.url);
      fflush(log);
    }
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Authentication failed, URI does not match.\n"));
#endif
    if (log)\n      fclose(log);
    return false;
  }

  /* Violation scenario: 'args' is declared non-const but only used for read access. */
  args = (NULL != qmark) ? (qmark + 1) : uri + uri_len;
  if (log)
  {
    fprintf(log, "args pointer set to %p (offset %ld)\n",
            (void *)args,
            (long)(args - uri));
    /* Print a short preview of the args (may be empty) */
    fprintf(log, "args preview='%s'\n", args);
    fflush(log);
  }

  if (! check_argument_match (connection,
                              args) )
  {
    if (log)
    {
      fprintf(log, "check_argument_match returned false for args=%p\n", (void *)args);
      fflush(log);
    }
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Authentication failed, arguments do not match.\n"));
#endif
    if (log)
      fclose(log);
    return false;
  }

  if (log)
  {
    fprintf(log, "check_uri_match returning true (all matched)\n");
    fclose(log);
  }
  return true;
}
