static bool
check_uri_match (struct MHD_Connection *connection, char *uri, size_t uri_len)
{
  char *qmark;
  char *args;
  struct MHD_Daemon *const daemon = connection->daemon;
  FILE *log = fopen("/tmp/logs/3855.txt", "a");

  if (log)
  {
    fprintf(log, "ENTER check_uri_match: connection=%p, uri=%p, uri_len=%zu, daemon=%p\n",
            (void *)connection, (void *)uri, uri_len, (void *)daemon);
    fflush(log);
  }

  /* keep original behavior: null-terminate at uri[uri_len] */
  uri[uri_len] = 0;
  if (log)
  {
    /* log up to first 64 bytes of the (now temporarily) null-terminated uri */
    size_t i, limit = uri_len < 64 ? uri_len : 64;
    fprintf(log, "  after null-terminate: uri (len=%zu):\n    ", uri_len);
    for (i = 0; i < limit; ++i)
      fprintf(log, "%02x ", (unsigned char)uri[i]);
    if (uri_len > limit)
      fprintf(log, "... (%zu bytes total)", uri_len);
    fprintf(log, "\n");
    fflush(log);
  }

  qmark = memchr (uri,
                  '?',
                  uri_len);
  if (NULL != qmark)
  {
    if (log)
    {
      fprintf(log, "  found '?' at offset %td (qmark=%p). Will terminate uri at that point.\n",
              (ptrdiff_t)(qmark - uri), (void *)qmark);
      fflush(log);
    }
    *qmark = '\0';
  }
  else
  {
    if (log)
    {
      fprintf(log, "  no '?' found in first %zu bytes.\n", uri_len);
      fflush(log);
    }
  }

  /* Need to unescape URI before comparing with connection->url */
  {
    size_t old_len = uri_len;
    if (log)
      fprintf(log, "  calling unescape_callback (cls=%p)\n", (void *)daemon->unescape_callback_cls);

    uri_len = daemon->unescape_callback (daemon->unescape_callback_cls,
                                         connection,
                                         uri);

    if (log)
    {
      fprintf(log, "  unescape_callback returned new uri_len=%zu (old=%zu)\n", uri_len, old_len);
      /* log up to first 64 bytes of the (possibly modified) uri buffer as hex */
      size_t i, limit = uri_len < 64 ? uri_len : 64;
      fprintf(log, "    uri (post-unescape, up to %zu bytes):\n    ", limit);
      for (i = 0; i < limit; ++i)
        fprintf(log, "%02x ", (unsigned char)uri[i]);
      if (uri_len > limit)
        fprintf(log, "... (%zu bytes total)", uri_len);
      fprintf(log, "\n");
      fflush(log);
    }
  }

  /* Compare lengths first, only call memcmp when lengths match (preserve short-circuit behavior) */
  if ((uri_len != connection->rq.url_len) ||
      (0 != memcmp (uri, connection->rq.url, uri_len)))
  {
    if (log)
    {
      fprintf(log, "  AUTH FAILURE: ");
      fprintf(log, "uri_len=%zu, connection->rq.url_len=%zu, connection->rq.url=%p\n",
              uri_len, connection->rq.url_len, (void *)connection->rq.url);
      if (uri_len == connection->rq.url_len)
      {
        /* lengths equal -> memcmp was executed. Log memcmp result as hex of first min bytes */
        size_t i, limit = uri_len < 64 ? uri_len : 64;
        fprintf(log, "    compared first %zu bytes (hex):\n    ", limit);
        for (i = 0; i < limit; ++i)
          fprintf(log, "%02x ", (unsigned char)uri[i]);
        fprintf(log, "\n    vs\n    ");
        for (i = 0; i < limit; ++i)
          fprintf(log, "%02x ", (unsigned char)connection->rq.url[i]);
        fprintf(log, "\n");
      }
      else
      {
        fprintf(log, "    memcmp not performed due to length mismatch.\n");
      }
      fflush(log);
    }
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Authentication failed, URI does not match.\n"));
#endif
    if (log)
    {
      fprintf(log, "EXIT check_uri_match: returning false (URI mismatch)\n\n");
      fclose(log);
    }
    return false;
  }

  args = (NULL != qmark) ? (qmark + 1) : uri + uri_len;

  if (log)
  {
    fprintf(log, "  args=%p (offset %td from uri).\n",
            (void *)args, (ptrdiff_t)(args - uri));
    /* attempt to log a small portion of args (may be empty) */
    {
      size_t i, limit = 32;
      fprintf(log, "    args (up to %zu bytes as hex):\n    ", limit);
      for (i = 0; i < limit && args[i] != '\0'; ++i)
        fprintf(log, "%02x ", (unsigned char)args[i]);
      if (i == 0)
        fprintf(log, "(empty)\n");
      else
        fprintf(log, "\n");
      fflush(log);
    }
  }

  if (! check_argument_match (connection,
                              args) )
  {
    if (log)
    {
      fprintf(log, "  AUTH FAILURE: arguments did not match.\n");
      fprintf(log, "EXIT check_uri_match: returning false (args mismatch)\n\n");
      fclose(log);
    }
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Authentication failed, arguments do not match.\n"));
#endif
    return false;
  }

  if (log)
  {
    fprintf(log, "EXIT check_uri_match: returning true (match)\n\n");
    fclose(log);
  }
  return true;
}
