static bool
connection_check_timedout (struct MHD_Connection *c)
{
  const uint64_t timeout = c->connection_timeout_ms;
  uint64_t now;
  uint64_t since_actv;

  /* Probe: open log file and record entry + basic state */
  {
    FILE *log = fopen("/tmp/logs/1625.txt", "a");
    if (log != NULL)
    {
      fprintf(log, "ENTER connection_check_timedout: c=%p suspended=%d timeout=%" PRIu64 " last_activity=%" PRIu64 "\n",
              (void *)c, (int)c->suspended, c->connection_timeout_ms, c->last_activity);
      fclose(log);
    }
  }

  if (c->suspended)
  {
    FILE *log = fopen("/tmp/logs/1625.txt", "a");
    if (log != NULL)
    {
      fprintf(log, "BRANCH: suspended -> returning false\n");
      fclose(log);
    }
    return false;
  }
  if (0 == timeout)
  {
    FILE *log = fopen("/tmp/logs/1625.txt", "a");
    if (log != NULL)
    {
      fprintf(log, "BRANCH: timeout == 0 -> returning false\n");
      fclose(log);
    }
    return false;
  }
  now = MHD_monotonic_msec_counter ();
  since_actv = now - c->last_activity;

  {
    FILE *log = fopen("/tmp/logs/1625.txt", "a");
    if (log != NULL)
    {
      fprintf(log, "Computed now=%" PRIu64 " since_actv=%" PRIu64 " (timeout=%" PRIu64 ")\n",
              now, since_actv, timeout);
      fclose(log);
    }
  }

  /* Keep the next lines in sync with #connection_get_wait() to avoid
   * undesired side-effects like busy-waiting. */
  if (timeout < since_actv)
  {
    FILE *log = fopen("/tmp/logs/1625.txt", "a");
    if (log != NULL)
    {
      fprintf(log, "BRANCH: timeout < since_actv -> potential timed out (since_actv=%" PRIu64 ")\n",
              since_actv);
      fclose(log);
    }

    if (UINT64_MAX / 2 < since_actv)
    {
      const uint64_t jump_back = c->last_activity - now;

      {
        FILE *log2 = fopen("/tmp/logs/1625.txt", "a");
        if (log2 != NULL)
        {
          fprintf(log2, "SUBBRANCH: huge since_actv (>%" PRIu64 ") -> jump_back=%" PRIu64 "\n",
                  (uint64_t)(UINT64_MAX / 2), jump_back);
          fclose(log2);
        }
      }

      /* Very unlikely that it is more than quarter-million years pause.
       * More likely that system clock jumps back. */
      if (5000 >= jump_back)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (c->daemon,
                  _ ("Detected system clock %u milliseconds jump back.\n"),
                  (unsigned int) jump_back);
#endif
        {
          FILE *log3 = fopen("/tmp/logs/1625.txt", "a");
          if (log3 != NULL)
          {
            fprintf(log3, "SUBSUBBRANCH: small jump_back=%" PRIu64 " (<=5000) -> treating as not timed out, returning false\n",
                    jump_back);
            fclose(log3);
          }
        }
        return false;
      }
#ifdef HAVE_MESSAGES
      MHD_DLOG (c->daemon,
                _ ("Detected too large system clock %" PRIu64 " milliseconds "
                   "jump back.\n"),
                jump_back);
#endif
      {
        FILE *log4 = fopen("/tmp/logs/1625.txt", "a");
        if (log4 != NULL)
        {
          fprintf(log4, "SUBSUBBRANCH: large jump_back=%" PRIu64 " -> continue and return true\n",
                  jump_back);
          fclose(log4);
        }
      }
    }

    {
      FILE *log5 = fopen("/tmp/logs/1625.txt", "a");
      if (log5 != NULL)
      {
        fprintf(log5, "FINAL: timed out -> returning true\n");
        fclose(log5);
      }
    }
    return true;
  }

  {
    FILE *log = fopen("/tmp/logs/1625.txt", "a");
    if (log != NULL)
    {
      fprintf(log, "FINAL: not timed out -> returning false\n");
      fclose(log);
    }
  }
  return false;
}
