static uint64_t
connection_get_wait (struct MHD_Connection *c)
{
  const uint64_t now = MHD_monotonic_msec_counter ();
  const uint64_t since_actv = now - c->last_activity;
  const uint64_t timeout = c->connection_timeout_ms;
  uint64_t mseconds_left;
  FILE *logf = fopen("/tmp/logs/3948.txt", "a");

  if (logf)
  {
    fprintf(logf,
            "connection_get_wait ENTER: conn=%p now=%llu last_activity=%llu since_actv=%llu timeout=%llu\n",
            (void *)c,
            (unsigned long long) now,
            (unsigned long long) c->last_activity,
            (unsigned long long) since_actv,
            (unsigned long long) timeout);
    fflush(logf);

    fprintf(logf, "about to assert(0 != timeout) ; timeout=%llu\n", (unsigned long long) timeout);
    fflush(logf);
  }

  mhd_assert (0 != timeout);

  if (logf)
  {
    fprintf(logf, "after assert; timeout=%llu\n", (unsigned long long) timeout);
    fflush(logf);
  }
  /* Keep the next lines in sync with #connection_check_timedout() to avoid
   * undesired side-effects like busy-waiting. */
  if (timeout < since_actv)
  {
    if (logf)
    {
      fprintf(logf, "branch: timeout < since_actv (timeout=%llu < since_actv=%llu)\n",
              (unsigned long long) timeout, (unsigned long long) since_actv);
      fflush(logf);
    }

    if (UINT64_MAX / 2 < since_actv)
    {
      const uint64_t jump_back = c->last_activity - now;
      /* Very unlikely that it is more than quarter-million years pause.
       * More likely that system clock jumps back. */
      if (logf)
      {
        fprintf(logf, "branch: UINT64_MAX/2 < since_actv (since_actv=%llu) -> jump_back=%llu\n",
                (unsigned long long) since_actv, (unsigned long long) jump_back);
        fflush(logf);
      }
      if (5000 >= jump_back)
      { /* Jump back is less than 5 seconds, try to recover. */
        if (logf)
        {
          fprintf(logf, "recovery: small jump_back=%llu -> returning 100\n", (unsigned long long) jump_back);
          fclose(logf);
        }
        return 100; /* Set wait time to 0.1 seconds */
      }
      /* Too large jump back */
    }
    if (logf)
    {
      fprintf(logf, "timeout occurred -> returning 0\n");
      fclose(logf);
    }
    return 0; /* Connection has timed out */
  }
  else if (since_actv == timeout)
  {
    /* Exact match for timeout and time from last activity.
     * Maybe this is just a precise match or this happens because the timer
     * resolution is too low.
     * Set wait time to 0.1 seconds to avoid busy-waiting with low
     * timer resolution as connection is not timed-out yet. */
    if (logf)
    {
      fprintf(logf, "since_actv == timeout -> returning 100 (avoid busy-wait)\n");
      fclose(logf);
    }
    return 100;
  }
  mseconds_left = timeout - since_actv;

  if (logf)
  {
    fprintf(logf, "normal path -> mseconds_left=%llu ; returning that\n", (unsigned long long) mseconds_left);
    fclose(logf);
  }

  return mseconds_left;
}
