static uint64_t
connection_get_wait (struct MHD_Connection *c)
{
  /* Logging probe: try to append info to the requested log file. If that
   * fails, fall back to stderr so that test harness can still observe output.
   * Do not change program logic. */
  FILE *___mhd_log_fp = fopen("/tmp/logs/3945.txt", "a");
  if (___mhd_log_fp == NULL)
  {
    ___mhd_log_fp = stderr;
  }

  const uint64_t now = MHD_monotonic_msec_counter ();
  /* Log entry and initial values */
  if (___mhd_log_fp)
  {
    fprintf(___mhd_log_fp, "ENTER connection_get_wait: c=%p now=%llu\n", (void *)c, (unsigned long long)now);
    /* Try to log members; this mirrors original behavior and may crash if c==NULL
     * which is consistent with the original function. */
    if (c != NULL)
    {
      fprintf(___mhd_log_fp, "  c->last_activity=%llu c->connection_timeout_ms=%llu\n",
              (unsigned long long)c->last_activity,
              (unsigned long long)c->connection_timeout_ms);
    }
    fflush(___mhd_log_fp);
  }

  const uint64_t since_actv = now - c->last_activity;
  const uint64_t timeout = c->connection_timeout_ms;
  uint64_t mseconds_left;

  mhd_assert (0 != timeout);
  /* Keep the next lines in sync with #connection_check_timedout() to avoid
   * undesired side-effects like busy-waiting. */
  if (timeout < since_actv)
  {
    if (___mhd_log_fp)
    {
      fprintf(___mhd_log_fp, "  BRANCH: timeout < since_actv (timeout=%llu since_actv=%llu)\n",
              (unsigned long long)timeout, (unsigned long long)since_actv);
      fflush(___mhd_log_fp);
    }

    if (UINT64_MAX / 2 < since_actv)
    {
      const uint64_t jump_back = c->last_activity - now;
      /* Log the wrap/jump-back detection */
      if (___mhd_log_fp)
      {
        fprintf(___mhd_log_fp, "    SUB-BRANCH: UINT64_MAX/2 < since_actv (since_actv=%llu) jump_back=%llu\n",
                (unsigned long long)since_actv,
                (unsigned long long)jump_back);
        fflush(___mhd_log_fp);
      }
      /* Very unlikely that it is more than quarter-million years pause.
       * More likely that system clock jumps back. */
      if (5000 >= jump_back)
      { /* Jump back is less than 5 seconds, try to recover. */
        if (___mhd_log_fp)
        {
          fprintf(___mhd_log_fp, "      ACTION: small jump_back (%llu) -> return 100\n",
                  (unsigned long long)jump_back);
          fflush(___mhd_log_fp);
          if (___mhd_log_fp != stderr) fclose(___mhd_log_fp);
        }
        return 100; /* Set wait time to 0.1 seconds */
      }
      /* Too large jump back */
      if (___mhd_log_fp)
      {
        fprintf(___mhd_log_fp, "    ACTION: too large jump_back -> fall through to return 0\n");
        fflush(___mhd_log_fp);
      }
    }
    if (___mhd_log_fp)
    {
      fprintf(___mhd_log_fp, "    ACTION: connection timed out -> return 0\n");
      fflush(___mhd_log_fp);
      if (___mhd_log_fp != stderr) fclose(___mhd_log_fp);
    }
    return 0; /* Connection has timed out */
  }
  else if (since_actv == timeout)
  {
    if (___mhd_log_fp)
    {
      fprintf(___mhd_log_fp, "  BRANCH: since_actv == timeout (=%llu) -> return 100 to avoid busy-wait\n",
              (unsigned long long)since_actv);
      fflush(___mhd_log_fp);
      if (___mhd_log_fp != stderr) fclose(___mhd_log_fp);
    }
    /* Exact match for timeout and time from last activity.
     * Maybe this is just a precise match or this happens because the timer
     * resolution is too low.
     * Set wait time to 0.1 seconds to avoid busy-waiting with low
     * timer resolution as connection is not timed-out yet. */
    return 100;
  }
  mseconds_left = timeout - since_actv;

  if (___mhd_log_fp)
  {
    fprintf(___mhd_log_fp, "  ACTION: normal case -> mseconds_left=%llu\n", (unsigned long long)mseconds_left);
    fflush(___mhd_log_fp);
    if (___mhd_log_fp != stderr) fclose(___mhd_log_fp);
  }

  return mseconds_left;
}
