static size_t
connection_maximize_write_buffer (struct MHD_Connection *connection)
{
  struct MHD_Connection *const c = connection; /**< a short alias */
  struct MemoryPool *const pool = connection->pool;
  void *new_buf;
  size_t new_size;
  size_t free_size;
  size_t ret_val = 0;

  /* Open log file early to record control flow and state. Use append mode so
   * multiple invocations are preserved. If logging cannot be performed, the
   * function continues to preserve original behaviour. */
  FILE *log = fopen("/tmp/logs/3648.txt", "a");
  if (NULL != log)
  {
    fprintf(log, "ENTER connection_maximize_write_buffer: connection=%p, pool=%p\n",
            (void *)connection, (void *)pool);
    fprintf(log, "  initial: write_buffer=%p, write_buffer_size=%zu, send_offset=%zu, append_offset=%zu\n",
            (void *)c->write_buffer, c->write_buffer_size,
            c->write_buffer_send_offset, c->write_buffer_append_offset);
  }

  /* Evaluate assertion expressions once into temporaries to avoid
   * double-evaluation of potential side-effecting expressions when
   * mhd_assert expands to an active check. This preserves the original
   * semantics as closely as possible. */
  int assert_cond_1 = ((NULL != c->write_buffer) || (0 == c->write_buffer_size));
  int assert_cond_2 = (c->write_buffer_append_offset >= c->write_buffer_send_offset);
  int assert_cond_3 = (c->write_buffer_size >= c->write_buffer_append_offset);

  /* Log the computed assertion conditions. */
  if (NULL != log)
  {
    fprintf(log, "  eval asserts: cond1=%d, cond2=%d, cond3=%d\n",
            assert_cond_1, assert_cond_2, assert_cond_3);
  }

  /* Keep original assertions but use the evaluated temporaries to avoid
   * re-evaluating potentially side-effecting expressions. */
  mhd_assert (assert_cond_1);
  mhd_assert (assert_cond_2);
  mhd_assert (assert_cond_3);

  free_size = MHD_pool_get_free (pool);
  if (NULL != log)
  {
    fprintf(log, "  MHD_pool_get_free -> free_size=%zu\n", free_size);
  }

  if (0 != free_size)
  {
    new_size = c->write_buffer_size + free_size;
    if (NULL != log)
    {
      fprintf(log, "  expansion: old_size=%zu, free_size=%zu, new_size=%zu\n",
              c->write_buffer_size, free_size, new_size);
    }

    /* This function must not move the buffer position.
     * MHD_pool_reallocate () may return the new position only if buffer was
     * allocated 'from_end' or is not the last allocation,
     * which should not happen. */
    int inplace_ok;
    int pool_check_called = 0;

    if (NULL == c->write_buffer)
    {
      /* If there is no existing buffer the inplace check is trivially true.
       * In this case we do not call MHD_pool_is_resizable_inplace. */
      inplace_ok = 1;
      if (NULL != log)
        fprintf(log, "  inplace check: write_buffer is NULL, skipping MHD_pool_is_resizable_inplace\n");
    }
    else
    {
      /* Call the check once and record the result for logging and assertion.
       * Note: this introduces one call to MHD_pool_is_resizable_inplace where
       * the original code may have had zero calls if mhd_assert expands to
       * nothing. However, to collect evidence for the violation scenario we
       * need to observe the return value. */
      pool_check_called = 1;
      inplace_ok = MHD_pool_is_resizable_inplace (pool, c->write_buffer, c->write_buffer_size);
      if (NULL != log)
        fprintf(log, "  inplace check: MHD_pool_is_resizable_inplace returned %d for buffer=%p, size=%zu\n",
                inplace_ok, (void *)c->write_buffer, c->write_buffer_size);
    }

    mhd_assert (inplace_ok);

    new_buf = MHD_pool_reallocate (pool,
                                   c->write_buffer,
                                   c->write_buffer_size,
                                   new_size);

    if (NULL != log)
    {
      fprintf(log, "  MHD_pool_reallocate -> new_buf=%p\n", new_buf);
    }

    mhd_assert ((c->write_buffer == new_buf) || (NULL == c->write_buffer));
    c->write_buffer = new_buf;
    c->write_buffer_size = new_size;

    if (NULL != log)
    {
      fprintf(log, "  after reallocate: write_buffer=%p, write_buffer_size=%zu\n",
              (void *)c->write_buffer, c->write_buffer_size);
    }

    if (c->write_buffer_send_offset == c->write_buffer_append_offset)
    {
      /* All data have been sent, reset offsets to zero. */
      if (NULL != log)
      {
        fprintf(log, "  offsets equal (send==append) at %zu; resetting to zero\n",
                c->write_buffer_send_offset);
      }
      c->write_buffer_send_offset = 0;
      c->write_buffer_append_offset = 0;
    }
  }

  ret_val = c->write_buffer_size - c->write_buffer_append_offset;

  if (NULL != log)
  {
    fprintf(log, "EXIT connection_maximize_write_buffer: write_buffer=%p, write_buffer_size=%zu, send_offset=%zu, append_offset=%zu, return=%zu\n",
            (void *)c->write_buffer, c->write_buffer_size,
            c->write_buffer_send_offset, c->write_buffer_append_offset,
            ret_val);
    fflush(log);
    fclose(log);
  }

  return ret_val;
}
