static void
connection_reset (struct MHD_Connection *connection,
                  bool reuse)
{
  struct MHD_Connection *const c = connection; /**< a short alias */
  struct MHD_Daemon *const d = connection->daemon;

  /* Open log file (append) to record control flow and relevant state. */
  FILE *log = fopen("/tmp/logs/2788.txt", "a");
  if (log)
  {
    fprintf(log, "connection_reset: enter; connection=%p reuse=%d daemon=%p\n", (void *)c, (int)reuse, (void *)d);
    /* Log some key fields that are relevant to the suspected dead code. */
    fprintf(log, "  before: stop_with_error=%d read_buffer=%p read_buffer_size=%zu read_buffer_offset=%zu write_buffer=%p\n",
            (int)c->stop_with_error,
            (void *)c->read_buffer,
            (size_t)c->read_buffer_size,
            (size_t)c->read_buffer_offset,
            (void *)c->write_buffer);
  }

  if (! reuse)
  {
    if (log)
      fprintf(log, "  branch: non-reuse -> calling MHD_connection_close_\n");

    /* Next function will destroy response, notify client,
     * destroy memory pool, and set connection state to "CLOSED" */
    MHD_connection_close_ (c,
                           c->stop_with_error ?
                           MHD_REQUEST_TERMINATED_WITH_ERROR :
                           MHD_REQUEST_TERMINATED_COMPLETED_OK);

    if (log)
    {
      /* Record the state immediately after the call to MHD_connection_close_.
         This lets a tester determine whether the subsequent assignments are
         redundant (dead) because the close already cleared or invalidated
         these fields. */
      fprintf(log, "  after MHD_connection_close_: read_buffer=%p read_buffer_size=%zu read_buffer_offset=%zu write_buffer=%p state=%d keepalive=%d\n",
              (void *)c->read_buffer,
              (size_t)c->read_buffer_size,
              (size_t)c->read_buffer_offset,
              (void *)c->write_buffer,
              (int)c->state,
              (int)c->keepalive);
    }

    c->read_buffer = NULL;
    c->read_buffer_size = 0;
    c->read_buffer_offset = 0;
    c->write_buffer = NULL;
    c->write_buffer_size = 0;
    c->write_buffer_send_offset = 0;
    c->write_buffer_append_offset = 0;

    if (log)
      fprintf(log, "  after manual reset assignments: read_buffer=%p read_buffer_size=%zu read_buffer_offset=%zu write_buffer=%p\n",
              (void *)c->read_buffer,
              (size_t)c->read_buffer_size,
              (size_t)c->read_buffer_offset,
              (void *)c->write_buffer);
  }
  else
  {
    /* Reset connection to process the next request */
    size_t new_read_buf_size;
    mhd_assert (! c->stop_with_error);
    mhd_assert (! c->discard_request);

    if ( (NULL != d->notify_completed) &&
         (c->rq.client_aware) )
      d->notify_completed (d->notify_completed_cls,
                           c,
                           &c->rq.client_context,
                           MHD_REQUEST_TERMINATED_COMPLETED_OK);
    c->rq.client_aware = false;

    if (NULL != c->rp.response)
      MHD_destroy_response (c->rp.response);
    c->rp.response = NULL;

    c->keepalive = MHD_CONN_KEEPALIVE_UNKOWN;
    c->state = MHD_CONNECTION_INIT;
    c->event_loop_info =
      (0 == c->read_buffer_offset) ?
      MHD_EVENT_LOOP_INFO_READ : MHD_EVENT_LOOP_INFO_PROCESS;

    memset (&c->rq, 0, sizeof(c->rq));

    /* iov (if any) will be deallocated by MHD_pool_reset */
    memset (&c->rp, 0, sizeof(c->rp));

    c->write_buffer = NULL;
    c->write_buffer_size = 0;
    c->write_buffer_send_offset = 0;
    c->write_buffer_append_offset = 0;
    c->continue_message_write_offset = 0;

    /* Reset the read buffer to the starting size,
       preserving the bytes we have already read. */
    new_read_buf_size = c->daemon->pool_size / 2;
    if (c->read_buffer_offset > new_read_buf_size)
      new_read_buf_size = c->read_buffer_offset;

    if (log)
      fprintf(log, "  branch: reuse -> about to call MHD_pool_reset; read_buffer=%p read_buffer_offset=%zu new_read_buf_size=%zu\n",
              (void *)c->read_buffer,
              (size_t)c->read_buffer_offset,
              (size_t)new_read_buf_size);

    c->read_buffer
      = MHD_pool_reset (c->pool,
                        c->read_buffer,
                        c->read_buffer_offset,
                        new_read_buf_size);
    c->read_buffer_size = new_read_buf_size;

    if (log)
      fprintf(log, "  after MHD_pool_reset: read_buffer=%p read_buffer_size=%zu\n",
              (void *)c->read_buffer,
              (size_t)c->read_buffer_size);
  }

  c->rq.client_context = NULL;

  if (log)
  {
    fprintf(log, "connection_reset: exit; final client_context=%p state=%d keepalive=%d\n",
            (void *)c->rq.client_context,
            (int)c->state,
            (int)c->keepalive);
    fclose(log);
  }
}
