_MHD_static_inline void
digest_calc_hash (struct DigestAlgorithm *da, uint8_t *digest)
{
  /* Probe log file to capture control flow and state for debugging the
     potential unreachable-code scenario described in MISRA Rule 2.1. */
  FILE *mhd_log = fopen("/tmp/logs/676.txt", "a");
  if (mhd_log)
  {
    fprintf(mhd_log, "ENTER digest_calc_hash: da=%p algo=%d uninitialised=%d algo_selected=%d ready_for_hashing=%d hashing=%d\n",
            (void *)da,
            (int) da->algo,
            (int) da->uninitialised,
            (int) da->algo_selected,
            (int) da->ready_for_hashing,
            (int) da->hashing);
    fflush(mhd_log);
  }

  mhd_assert (! da->uninitialised);
  mhd_assert (da->algo_selected);
  mhd_assert (da->ready_for_hashing);
#ifdef MHD_MD5_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_MD5 == da->algo)
  {
    if (mhd_log)
    {
      fprintf(mhd_log, "BRANCH: MD5 selected\n");
      fflush(mhd_log);
    }
#ifdef MHD_MD5_HAS_FINISH
    MHD_MD5_finish (&da->ctx.md5_ctx, digest);
#ifdef _DEBUG
    da->ready_for_hashing = false;
#endif /* _DEBUG */
#else  /* ! MHD_MD5_HAS_FINISH */
    MHD_MD5_finish_reset (&da->ctx.md5_ctx, digest);
#ifdef _DEBUG
    da->ready_for_hashing = true;
#endif /* _DEBUG */
#endif /* ! MHD_MD5_HAS_FINISH */
  }
  else
#endif /* MHD_MD5_SUPPORT */
#ifdef MHD_SHA256_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_SHA256 == da->algo)
  {
    if (mhd_log)
    {
      fprintf(mhd_log, "BRANCH: SHA256 selected\n");
      fflush(mhd_log);
    }
#ifdef MHD_SHA256_HAS_FINISH
    MHD_SHA256_finish (&da->ctx.sha256_ctx, digest);
#ifdef _DEBUG
    da->ready_for_hashing = false;
#endif /* _DEBUG */
#else  /* ! MHD_SHA256_HAS_FINISH */
    MHD_SHA256_finish_reset (&da->ctx.sha256_ctx, digest);
#ifdef _DEBUG
    da->ready_for_hashing = true;
#endif /* _DEBUG */
#endif /* ! MHD_SHA256_HAS_FINISH */
  }
  else
#endif /* MHD_SHA256_SUPPORT */
#ifdef MHD_SHA512_256_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_SHA512_256 == da->algo)
  {
    if (mhd_log)
    {
      fprintf(mhd_log, "BRANCH: SHA512_256 selected\n");
      fflush(mhd_log);
    }
    MHD_SHA512_256_finish (&da->ctx.sha512_256_ctx, digest);
#ifdef _DEBUG
    da->ready_for_hashing = false;
#endif /* _DEBUG */
  }
  else
#endif /* MHD_SHA512_256_SUPPORT */
  {
    /* No supported algorithm matched. Log the event immediately before
       asserting. This write and fflush provide evidence that the
       code reached the failing assertion; if mhd_assert(0) aborts then
       subsequent statements (e.g. setting da->hashing = false) will be
       unreachable and this will be visible in the log. */
    if (mhd_log)
    {
      fprintf(mhd_log, "NO BRANCH MATCHED: about to call mhd_assert(0) -- da=%p algo=%d ready_for_hashing=%d\n",
              (void *)da,
              (int) da->algo,
              (int) da->ready_for_hashing);
      fflush(mhd_log);
    }
    mhd_assert (0);   /* Should not happen */
  }
#ifdef _DEBUG
  da->hashing = false;
  if (mhd_log)
  {
    fprintf(mhd_log, "EXIT digest_calc_hash: set da->hashing = false, da=%p\n", (void *)da);
    fclose(mhd_log);
  }
#else
  if (mhd_log)
  {
    /* If not a debug build, just close the log. */
    fclose(mhd_log);
  }
#endif /* _DEBUG */
}
