_MHD_static_inline void
digest_calc_hash (struct DigestAlgorithm *da, uint8_t *digest)
{
  FILE *log = fopen("/tmp/logs/673.txt", "a");
  if (log)
  {
    fprintf(log, "digest_calc_hash ENTER: da=%p digest=%p\n", (void *) da, (void *) digest);
    /* Log key, always-present flags/state used by this function. Do not access fields
       that might only exist under conditional compilation. */
    fprintf(log, "da state: uninitialised=%d algo_selected=%d ready_for_hashing=%d algo=%d\n",
            (int) da->uninitialised, (int) da->algo_selected, (int) da->ready_for_hashing, (int) da->algo);
  }

  mhd_assert (! da->uninitialised);
  mhd_assert (da->algo_selected);
  mhd_assert (da->ready_for_hashing);
#ifdef MHD_MD5_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_MD5 == da->algo)
  {
    if (log) fprintf(log, "Branch taken: MD5 (expected output length: 16 bytes)\n");
#ifdef MHD_MD5_HAS_FINISH
    MHD_MD5_finish (&da->ctx.md5_ctx, digest);
#ifdef _DEBUG
    da->ready_for_hashing = false;
#endif /* _DEBUG */
#else  /* ! MHD_MD5_HAS_FINISH */
    MHD_MD5_finish_reset (&da->ctx.md5_ctx, digest);
#ifdef _DEBUG
    da->ready_for_hashing = true;
#endif /* _DEBUG */
#endif /* ! MHD_MD5_HAS_FINISH */
  }
  else
#endif /* MHD_MD5_SUPPORT */
#ifdef MHD_SHA256_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_SHA256 == da->algo)
  {
    if (log) fprintf(log, "Branch taken: SHA256 (expected output length: 32 bytes)\n");
#ifdef MHD_SHA256_HAS_FINISH
    MHD_SHA256_finish (&da->ctx.sha256_ctx, digest);
#ifdef _DEBUG
    da->ready_for_hashing = false;
#endif /* _DEBUG */
#else  /* ! MHD_SHA256_HAS_FINISH */
    MHD_SHA256_finish_reset (&da->ctx.sha256_ctx, digest);
#ifdef _DEBUG
    da->ready_for_hashing = true;
#endif /* _DEBUG */
#endif /* ! MHD_SHA256_HAS_FINISH */
  }
  else
#endif /* MHD_SHA256_SUPPORT */
#ifdef MHD_SHA512_256_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_SHA512_256 == da->algo)
  {
    if (log) fprintf(log, "Branch taken: SHA512_256 (expected output length: 32 bytes)\n");
    /* Additional log to make it explicit for reproducing a potential out-of-bounds
       scenario: record the digest pointer and the expected number of bytes to be written. */
    if (log) fprintf(log, "Invoking SHA512_256_finish -> will write %d bytes to digest=%p\n", 32, (void *) digest);

    MHD_SHA512_256_finish (&da->ctx.sha512_256_ctx, digest);
#ifdef _DEBUG
    da->ready_for_hashing = false;
#endif /* _DEBUG */
  }
  else
#endif /* MHD_SHA512_256_SUPPORT */
  mhd_assert (0);   /* Should not happen */
#ifdef _DEBUG
  da->hashing = false;
#endif /* _DEBUG */

  if (log)
  {
    fprintf(log, "digest_calc_hash LEAVE: da=%p digest=%p final_ready_for_hashing=%d\n",
            (void *) da, (void *) digest, (int) da->ready_for_hashing);
    fclose(log);
  }
}
