_MHD_static_inline unsigned int
digest_get_size (struct DigestAlgorithm *da)
{
  FILE *logf = fopen("/tmp/logs/2025.txt", "a");
  if (logf)
    {
      fprintf(logf, "digest_get_size: entered -> da=%p\n", (void *) da);
      if (da)
        fprintf(logf, "  state: uninitialised=%d, algo_selected=%d, algo=%d\n",
                (int) da->uninitialised, (int) da->algo_selected, (int) da->algo);
      else
        fprintf(logf, "  state: da == NULL\n");
      fflush(logf);
    }

  mhd_assert (! da->uninitialised);
  mhd_assert (da->algo_selected);
#ifdef MHD_MD5_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_MD5 == da->algo)
    {
      if (logf)
        {
          fprintf(logf, "  chosen branch: MD5, size=%u\n", (unsigned) MD5_DIGEST_SIZE);
          fclose(logf);
        }
      return MD5_DIGEST_SIZE;
    }
#endif /* MHD_MD5_SUPPORT */
#ifdef MHD_SHA256_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_SHA256 == da->algo)
    {
      if (logf)
        {
          fprintf(logf, "  chosen branch: SHA256, size=%u\n", (unsigned) SHA256_DIGEST_SIZE);
          fclose(logf);
        }
      return SHA256_DIGEST_SIZE;
    }
#endif /* MHD_SHA256_SUPPORT */
#ifdef MHD_SHA512_256_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_SHA512_256 == da->algo)
    {
      if (logf)
        {
          fprintf(logf, "  chosen branch: SHA512_256, size=%u\n", (unsigned) SHA512_256_DIGEST_SIZE);
          fclose(logf);
        }
      return SHA512_256_DIGEST_SIZE;
    }
#endif /* MHD_SHA512_256_SUPPORT */
  if (logf)
    {
      fprintf(logf, "  unexpected branch: unknown algo=%d -> assert(0)\n", (int) da->algo);
      fclose(logf);
    }
  mhd_assert (0); /* May not happen */
  return 0;
}
